css:
@-moz-document url-prefix("http://www.falubaz.com/"), url-prefix("http://falubaz.com/") {
#all #header_top, #all #header_sh, #FBbox {
   position:fixed;
  top:-1000px; 
}
#all #header {
  height: 34px !important;
}
#all #two-col {
  margin-top: 34px !important; 
}
a[title="www.forum.falubaz.com"] img{
  position:fixed !important;
  top:-1px !important; 
  z-index:9999999;
  height: 34px !important;
  margin-left:100px;
}
#cookies {
  display:none;
}
}
@-moz-document url-prefix("http://www.forum.falubaz.com/"), url-prefix("http://forum.falubaz.com/") {
#chatbox ul#chat_messages {
  height: 100% !important;
  max-height: 100% !important;
}
}
@-moz-document url-prefix("http://www.forum.falubaz.com/index.php#chat"), url-prefix("http://forum.falubaz.com/index.php#chat") {
#bodybg {
  position:fixed;
  top:-999999999px;
}
div#chatbox,  div#chatbox div.inner {
  position:fixed;
  top:0px;
  left:0px;
  right:0px;
  bottom:30px;
}
}
