css:
@-moz-document domain("www.hs.fi"), domain("blogit.hs.fi"), domain("live.hs.fi") {

/* Paywall remover from https://userstyles.org/styles/89599/hs-fi-paywall-remover-ilmainen-hs */

body.paywall .scalable-article-right-block > div,
body .hesari-ad-element,
body.paywall .article-scalable .article-text-content>*,
body.paywall-premium .article-scalable .article-text-content>*
{
    display: inherit !important;
}
body.paywall .scalable-article-right-block .hesari-ad-element,
body .hesari-ad-element
{
    display: none !important;
}

body.paywall .for-paywall,
body.paywall-initial .for-paywall-initial,
#paywall-first-warning,
#paywall-second-warning,
.for-paywall-counter,
.for-not-logged-in,
.for-no-subscription,
.embedded-ad,
.ad-small,
#multi-ad-area,
#outside-ads,
#cover-ad-area
{
	display: none!important;
}



/* Theme specific styles start */

body {
  background: none!important;
  background: #f5f5f5!important;
}

#ad-container-middle-980,
#ad-container-bottom-980,
#outside-ads,
#area-metro-is,
#oikotie-ad,
#oikotie-asunnot-ad,
#oikotie-uutiset-asunnot-lazy,
#oikotie_tyopaikat,
#is-boksi-lazy,
#kampanja-banneri-980,
#banner_hs_blogit_jb1_980x120,
#banner_hs_blogit_box1_468x400,
.ad-container-nobg,
.ad-container-last-nobg,
.tsFrameContainer,
.ad-container,
.middleColumnAdCenter,
.bannerColumn,
.last-module,
#login-area,
#hs-campaign-element,
.for-not-logged-in,
.for-no-subscription,
.frontpage-headlines.article-scroller,
.share-links,
.most-popular-themes,
.departmentpage-video-element,
.blog-item,
.column-item,
#facebook-recommended,
#footer,
#mostPopularArea,
#themes,
#hs-info,
#popular-videos-list,
#departments-most-popular-articles,
#area-d,
#area-e,
#is-outbound-module,
#metro_fp_box_iframe,
.link-item-list,
.area.v-module.v-module-now,
.module.popular-department-articles,
.module.article-popular-tags,
.article-mid-section-header,
#paywall-info,
#video-main-registration-wall,
.dontsplit.unauthorized-content,
.notification.paywallFirstWarning,
.notification.paywallSecondWarning,
.article-social-media,
.ad-container,
#comics-area,
#personal-theme-content {
  display: none!important;
}

#popular-videos-element,
ul.theme-list {
  display: none!important;
}

.wrapper {
  width: 980px!important;
}


/*
#content #ad-container-bottom-980 {
 border:
}
 */


/**
 * - Hide comics area from the bottom of articles
 */
#comics-area {
  display: none!important;
}



/**
 * - Hide latest news area from the bottom of articles
 * - Hide health lifestyle related articles area from the bottom of articles
 * - Hide food related articles area from the bottom of articles
 * - Hide latest columns area from the bottom of articles
 */
#latest-news-element,
#life-health-element,
#food-element,
#career-money-element,
#columns-element {
  display: none!important;
}



/** Articles **/
#article-area {
  border-bottom: none!important;
}


div.slow-articles {
  padding: 0!important;
}

.article-tools-bottom {
  padding: 0!important;
  top: 0!important;
}

h2 {
  font-size: 32px!important;
  letter-spacing: 0!important;
}

#article-text #article-text-content p {
  font-family: "Georgia",serif!important;
  font-size: 17px!important;
  letter-spacing: 0!important;
}

.teaser .ingress {
  font-size: 16px!important;
  letter-spacing: 0!important;
}

.comment-btn-group-title {
  font-size: 11px!important;
}

.comment-text {
  font-size: 15px!important;
}

.comment-list .comment-text div.quote p,
.comment-list .comment-text div.quote div {
  font-size: 15px!important;
}

.author {
  font-size: 15px!important;
}

.related-article > h2 {
  font-size: 16px!important;
}

.caption {
  font-size: 14px!important;
}

.photobox {
  font-size: 14px!important;
}

body.paywall #article-main-alternative,
#news-aside,
body.paywall #video-main,
body.paywall-premium #video-main,
body.paywall #video-aside,
body.paywall-premium #video-aside {
  display: block!important;
}

.full-article-top {
  display: inline!important;
}



/* - Remove background color from article image */
.main-image-area .img-wrapper {
  background: none!important;
}


/* - Hide annoying comments area from articles. */
#commenting {
  display: none!important;
}






/* - Add more whitespace to the bottom of articles */
#main-content {
  margin-bottom: 300px!important;
}




/* - Remove fixed width from article content */
/* - Adjust right side margin */
.scalable-article-left-block {
  width: auto!important;
  padding-left: 20px!important;
  margin-right: 20px!important;
}


/* - Disable empty whitespace from the left side (left over by the social sharing buttons). */
/* - Change text width from fixed size to automatically scalable width. */
.article-text-content {
  margin-left: 0!important;
  width: auto!important;
}





/* - Hide "breaking news" header block element */
#breaking-news-block {
  display: none!important;
}


/**
 * Top navigation
 *
 * - Disable border-bottom
 */
#navigation-area {
  border-bottom: none!important;
}







/* Print stylesheet for removed unneeded elements from printed articles. */
@media print {

  #article-navigation-container,
  .hs45 #navigation-area #sticky-navigation-container.fixed,
  .scalable-article-right-block,
  .main-header,
  .hs6-ticker,
  .article-middle,
  .article-bottom {
    display: none!important;
  }

  .scalable-article-right-block,
  #breaking-news-block,
  #navigation-area,
  #narrow-recommended-articles {
    display: none!important;
  }

  /* - Change page width to automatic width. */
  .wrapper {
    width: auto !important;
  }

  /* - Change page maximum width to 100% width. */
  .header-wrapper,
  .content-wrapper,
  .main-footer {
    max-width: 100% !important;
  }

  /* - Increase article font size and line height to improve readability. */
  article p {
    font-size: 16px;
    line-height: 25px;
  }


  /* - Remove whitespace from the bottom of articles */
  #main-content {
    margin-bottom: 0!important;
  }


  /* - Improve readability by disabling bold font weight */
  .article-area .related-article .body-text p,
  #article-area .related-article .body-text p {
      font-weight: normal!important;
  }

}

}
