css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://userstyles.org/styles/) , url-prefix("https://userstyles.org/styles/") {

#left-sidebar, .ad, #discussions-area, #switch-browser-note, #show-code, .license-title, .install-note, #obsoletion-message, footer, iframe {
  display: none !important;
  }
#main-header, #main-header a, a {
  background: #fff !important;
  color: #206cb4 !important;
  }
}

@-moz-document url-prefix(http://userstyles.org/users/) , url-prefix("https://userstyles.org/users/") {

#left-sidebar, .ad, footer, iframe, thead, ul, dl, .numeric-value, .image-value, a[href*='/stats/'], a[href*='/delete/'] {
  display: none !important;
  }
#main-header, #main-header a, a:link, a:visited {
  background: #fff !important;
  color: #206cb4 !important;
  text-decoration: none !important;
  }
.obsolete {
  width: 240px !important;
  }
}
