css:
@-moz-document domain("headlines.yahoo.co.jp"), domain("zasshi.news.yahoo.co.jp") {
#contentsHeader, #msthdtp, #contents-header, .adCt, #sub, #contents-footer, #footer,
#subNav, .msthdtxt, .msthdlink, [id^="yads"], a[href*="rd.ane.yahoo.co.jp"],
#contentsFooter
{ display: none !important; }

body {
  background-color: #f4f4f4 !important;
}

#wrapper {
  width: 634px !important;
  min-width: 634px !important;
  background-color: #fff !important;
  margin: 2em auto !important;
  padding: 2em 3em !important;
  border-radius: 4px;
  box-shadow: 0 0 5px #e8e8e8;
}

.mainBox {
  border: 0;
}

h1 {
  margin-top: 1em;
}
}

@-moz-document domain("bylines.news.yahoo.co.jp") {
#msthdtp, .msthdtxt, #contents-header, #rics-sub, #fixedWrap
{ display: none !important; }

#rics-content {
  padding-top: 20px;
}

#rics-main-inner {
  margin: auto;
}

#header, #msthd, #rics-content-wrapper {
  width: 700px;
}

body {
  background: #f7f7f8;
}

#mainvisual {
  margin: 40px auto 0;
  width: 595px;
}

.articleInnr {
  padding: 20px 3em;
}

.articleInnr header h1 {
  font-size: 160%;
}

#article article .bd {
  line-height: 1.8em;
}
}

@-moz-document domain("news.yahoo.co.jp") {
#msthdtp, #contentsHeader, .msthdtxt, #sub,
#ranking, #contentsFooter, #footer,
#socialBtn
{ display: none !important; }

/* Layout */
#header, #contentsBody {
  width: 800px;
}
#main {
  width: auto;
}

/* Styling */
.articleBody .bd p {
  font-size: 16px;
}
.articleBody {
  padding: 0 2.5em;
}

}
