css:
@-moz-document domain("theoldreader.com") {

/*Remove favicon, adjust alignment, correct counters, use Google fonts etc.
Author: Marina Mazzoni*/

@font-face {
  font-family: 'Aldrich';
  font-style: normal;
  font-weight: 400;
  src: url(http://themes.googleusercontent.com/static/fonts/aldrich/v3/Y0zWA9-hxl3_xvcxN-VXfA.woff) format('woff');
  -webkit-font-smoothing: antialiased;
}

@font-face {
  font-family: 'Oxygen';
  font-style: normal;
  font-weight: 400;
  src: url(http://themes.googleusercontent.com/static/fonts/oxygen/v2/RzoNiRR1p2Mqyyz2RwqSMw.woff) format('woff');
}

/* more fonts */
@font-face {
  font-family: 'Maven Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Maven Pro Regular'), local('MavenProRegular'), url(http://themes.googleusercontent.com/static/fonts/mavenpro/v4/MG9KbUZFchDs94Tbv9U-pT8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

@font-face {
  font-family: 'Philosopher';
  font-style: normal;
  font-weight: 400;
  src: local('Philosopher'), url(http://themes.googleusercontent.com/static/fonts/philosopher/v4/OttjxgcoEsufOGSINYBGLYbN6UDyHWBl620a-IRfuBk.woff) format('woff');
}

@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

/* inizio */

.clearfix, .pull-right {
font-size: large !important;
line-height:1.1em !important;
font-family: Open Sans,sans-serif !important;
}

div[class="content clearfix"] p, div[class="content-body"] p {
font-size: large !important;
line-height:1.5em !important;
font-family: Maven Pro, sans-serif !important;
}

div[class="content clearfix"] * li, div[class="content-body"] * li {
margin-left:50px !important;
font-size: large !important;
line-height:1.5em !important;
font-family: Maven Pro, sans-serif !important;
}

div[class="content clearfix"] a, div[class="content-body"] a {
font-size: large !important;
line-height:1.5em !important;
font-family: Maven Pro, sans-serif !important;
}

div[class="content-body"] {
font-size: large !important;
line-height:1.5em !important;
font-family: Maven Pro, sans-serif !important;
}

div[class="list-post"] h3{
font-size: large !important;
line-height:1.5em !important;
font-family: Maven Pro, sans-serif !important;
}

span[class="label label-transparent"] {
font-size: small !important;
line-height:1.5em !important;
font-family: Open Sans,sans-serif !important;
}
/* */
i{
background-image:none !important;
}
i.icon-chevron-down{
background-image:none !important;
}
i.icon-chevron-right{
background-image:none !important;
}
div [class="content-body"] i{
background-image:none !important;
}
li{
margin-left: 5px;
}
span{
font-size: medium !important;
}
span.badge{
font-size: x-small !important;
color: #30a67c !important;
padding: 1px 5px !important;
background-color:#cccccc !important;
}
span.badge.badge-info{
font-size: x-small !important;
padding: 1px 5px !important;
color:#da3f2d !important;
}

/* da qui ver 1.1 */
div [class="well sidebar-nav"]{
font-family: Open Sans,sans-serif !important;
font-size: medium !important;
}
div [class="slide"]{
font-family: Open Sans,sans-serif !important;
font-size: medium !important;
}
div [class="clr"]{
font-family: Maven Pro, sans-serif !important;
font-size: small !important;
line-height:2.5em !important;
}
h1{
font-family: Maven Pro, sans-serif !important;
font-size: x-large !important;
}
div [class="posts"]{
font-family: Maven Pro, sans-serif !important;
}
div [class="dl-horizontal"]{
font-family: Maven Pro, sans-serif !important;
}
/* 2.1 */
div [class="clearfix listview-header"] :hover{
background-color:#DFFCD4 !important;
color: #30a67c !important;
}
/* 2.2 */
.subscribe .btn-primary {
font-family: Open Sans,sans-serif !important;
border-radius: 0px 0px 10px 10px !important;
border: 1px solid #ccc !important;
box-shadow: none !important;
}
div [class="well sidebar-nav"]{
font-family: Open Sans,sans-serif !important;
font-size: medium !important;
background-color: #ccc !important;
background: -moz-linear-gradient(center bottom , #ccc 0%, #fff 100%) repeat scroll 0% 0% transparent !important;
}
div.list-post h3 a{
position: relative;
float:right;
font-style: italic;
font-size: 15px !important;
}
#following_title span{
font-style: italic;
font-size: 11px !important;
}
#following{
font-size: 10px !important;
}
div [class="clearfix header"] :hover{
background-color:#DFFCD4 !important;
color: #333 !important;
}
div [class="list-post"] {
background-color: #ddd !important;
}
/***************thanks bluehorse*****************/
div.posts div.post {
    display: inline-block;
    vertical-align: top;
    width: 48% !important;  /* change width here*/
}
div.content{
    max-height: 230px !important;
    overflow: hidden;
}
div.content:hover{
    overflow: auto;
}
/*div.clearfix span.label,
div.note{
    display:none !important;
    }
*/
div.btns a{
    padding:0px 5px !important;
    margin-top:5px !important;
    }

div.clearfix a{
    max-width:100% !important;
    overflow: hidden;
    text-overflow: ellipsis;
}

div.content-body iframe,
div.content-body object embed
{
    width:100% !important;
    height:auto !important;
    }

}
