@-moz-document url-prefix("http://www.calcchat.com/book/") {


/*--------------------------------------
primary css
--------------------------------------*/

/* newcalcchat.css */

div.content {max-width: none; min-width: inherit;}
div#header {width: 100%; margin-top: 0;}
div#header-left {width: 320px; margin: 0 auto; display: block; float: none;}
div#header-right {display: none;}
div#header-edge {display: none;}
div#header > a > img, div#footer > a > img {display: block;}
div#footer {display: none;}

div#divider-top, div#divider-middle, div#divider-bottom {display: none;}

div#toolbar {display: block; margin: auto; height: auto; text-align: center;}
div#book-content {width: 100%;}
div#left-content-container {width: 100%; height: 100%;}
div#book-content-left {width: 100%; height: 100%; border: none; overflow: auto; margin: 30px 0;}
div#book-content-center {display: none;}
div#book-content-right {display: none;}
div#bottom-toolbar {display: none;}

/* undefined selectors */

img#solutionimg {display: block; margin: auto;}
div#header-left > a:first-of-type > img {margin: 50px auto 0;}
div#header-left > a:nth-of-type(2) > img {margin: 0 auto 50px;}
div#header-left > br {display: none;}


/*--------------------------------------
user settings
--------------------------------------*/

/*[[bootstrap]]*/


/*[[label]]*/


}

