css:
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist|store|membership).*") {
@font-face {
    font-family: 'FontAwesome';
    src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
    src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'),url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg'); }

* {line-height: 1.35em !important;}

body {
    color: rgba(var(--colorText)) !important;
    text-align: center;
    margin: 0 !important;
    font-size: 13px !important;
    font-family: Segoe UI !important;
    text-transform: lowercase !important;
    background-color: rgba(var(--colorLightGray)) !important;
    background-image: url(http://static.colourlovers.com/images/patterns/3518/3518772.png) !important;
    background-size: auto !important;
    background-repeat: repeat !important;
    animation: pattern var(--speed) linear infinite; }

@keyframes pattern {
    from{background-position: 0 0;}
    to{background-position: var(--size);}}

/* Colours */
:root {
    --colorBlue: 112,200,237;
    --colorPink: 244,101,146;
    --colorOrange: 255,196,108;
    --colorGreen: 112,208,182;
    --colorPurple: 140,125,208;
    --colorLightBlue: 240,248,252; /* #f0f8fc */
    --colorText: 65,65,65; /* #414141 */
    --colorTextHeader: 65,66,65; /* #414241 */
    --colorTextLink: 96,96,96; /* #606060 */
    --colorLightGray: 234,234,234; /* #eaeaea */
    --colorLightGrayText: 150,150,150; /* #969696 */
    --colorBackground: 250,250,250; /* #fafafa */
    --colorForeground: 246,246,246; /* #f6f6f6 */
    --colorButton: 244,244,244; /* #f4f4f4 */
    --colorButtonText: 174,174,174; /* #aeaeae */
    --colorButtonDarkText: 128,128,128; /* #808080 */
    --colorDark: 24,24,24; /* #181818 */
    --colorDarkText: 85,85,85; /* #555555 */
    --colorDarkTextHover: 104,104,104; /* #686868 */
    --colorLessDark: 50,50,50; /* #323232 */
    --colorLessDarkText: 96,97,96; /* #606160 */
    --colorLessDarkTextAlt: 111,111,111; /* #6f6f6f */
    --colorLightShadow: 0 0 0 1px #e7e7e7;
    --colorDarkShadow: 65,65,65;
    --colorHeaderShadow: 65,65,65; }

/* Temporary */
.initialize-tutorial, .notice_open_public {display: none !important;}

a img:hover {opacity: 0.8 !important;}
a img:active {opacity: 1 !important;}
img, .image {image-rendering:-webkit-optimize-contrast !important;} /* Undo blurry/smoothed images in Chrome */
a, a:visited {color: rgba(var(--colorTextLink)) !important;text-decoration: none !important;font-style: italic !important;}
a:hover, a > span[style^="color:"]:hover {color: rgba(var(--colorBlue)) !important;transition: 0.2s;}
.link1 {color: #818081;} /* ??? */
td {font-size: 13px !important;}
small {font-size: 10px !important;}
body[onload="onloadManga();"] {background: rgba(var(--colorBackground)) !important;}
.page-common .ff-avenir, .breadcrumb, .ff-Verdana, .floatRightHeader {font-family: Segoe UI !important;}
.breadcrumb {font-size: 13px !important;display: none !important;}
.page-common .fn-red1 {color: rgba(var(--colorPink)) !important;}

#horiznav_nav {
    margin: 10px !important;
    padding: 0 0 2px !important;
    border: none !important; }
#horiznav_nav + div {padding-top: 10px !important;}
#horiznav_nav + strong {display: inline-block !important;padding: 15px 8px !important;} /* Add entry (Mods) */
td[width="225"] + td #horiznav_nav {margin: 10px 0 24px -3px !important;width: 101.8% !important;} /* A/M/Char/People */
#content.style-setting #horiznav_nav {margin: 10px 10px 24px -6px !important;} /* Edit style page */
td[width="225"] + td .js-scrollfix-bottom-rel #horiznav_nav {margin: 10px 0 20px -3px !important;} /* Manga */
td[width="225"] + td .js-scrollfix-bottom-rel #horiznav_nav + div + h2 {margin-top: 24px !important;} /* Manga */

#header, #headerSmall {height: 65px !important;background: transparent !important;}
#headerSmall > a {height: 100% !important;position: relative;z-index: 499 !important;}

#contentWrapper {position: relative;min-height: 0 !important;padding-bottom: 0 !important;border-radius: 0 0 18px 18px;}

#content, #contentHome {
    border: 1px solid white !important;
    border-radius: 0 0 18px 18px;
    min-height: 0 !important;
    padding-top: 10px !important; }

/* Upper navigation */
#menu {
    background: rgba(var(--colorDark)) !important;
    border-radius: 8px 8px 0 0;
    height: 46px !important;
    position: relative; }

#menu_left, #menu_right {background: none !important;}
#menu_left {position: relative;left: 85px;z-index: 490 !important;}
#menu_right {right: 75px;}

#nav li {
    background: none !important;
    text-indent: 0 !important;
    width: auto !important;
    height: 46px !important;
    z-index: 401 !important; }

#nav li a {
    padding: 5px 20px !important;
    font-style: normal !important;
    font-size: 14px !important;
    height: 36px !important;
    line-height: 36px !important;
    color: rgba(var(--colorDarkText)) !important;
    background: rgba(var(--colorDark)) !important; }

#nav li a:hover {color: rgba(var(--colorDarkTextHover)) !important;transition: 0.5s;}

#nav ul {
    z-index: -1;
    padding-bottom: 7px !important;
    background: rgba(var(--colorLessDark)) !important;
    border-radius: 0 0 5px 5px !important;
    box-shadow: 0 0 10px rgba(var(--colorHeaderShadow),0.5); } /* Top bar dropdown, main */

#nav ul a {
    font-size: 13px !important;
    background: rgba(var(--colorLessDark)) !important; }
#nav ul a:hover {background: none !important;color: rgba(var(--colorLessDarkTextAlt)) !important;}

#nav ul li {width: 150px !important;height: 36px !important;} /* Top bar dropdown, spacing */
#nav ul li a {
    color: rgba(var(--colorLessDarkTextAlt)) !important;
    padding: 0 20px !important;
    background: none !important; } /* Top bar dropdown, links */
#nav ul li:hover {background: none !important;}

#nav li.tiny {width: 50px;}
#nav li.smaller {width: auto !important;}
#nav li.small {width: auto !important;}
#nav li.large {width: 97px;}
#nav .wide li {width: 110px;}
#nav .wider li {width: 130px;}
#nav li.small:first-of-type {background: rgba(var(--colorDark)) !important;}

/* 'Blog' */
.header-profile-dropdown ul li {overflow: hidden;}
.header-profile-dropdown ul li a[href*="/blog/"]:before {content: "Blog";margin-right: 125px;}

/* 'Seasonal' */
#nav li:nth-child(1) ul li {overflow: hidden;}
#nav li:nth-child(1) ul li a[href*="/season"]:before {content: "Seasonal";margin-right: 125px;}

/* 'Articles' */
#nav li:nth-child(4) ul li {overflow: hidden;}
#nav li:nth-child(4) ul li a[href*="/featured"]:before {content: "Articles";margin-right: 125px;}

/* 'Streams' */
#nav li:nth-child(5) ul li {overflow: hidden;}
#nav li:nth-child(5) ul li a[href*="/watch/episode"]:before {content: "Streams";margin-right: 125px;}

/* 'Promos' */
#nav li:nth-child(5) ul li {overflow: hidden;}
#nav li:nth-child(5) ul li a[href*="/watch/promotion"]:before {content: "Promos";margin-right: 125px;}

/* 'Special' */
#nav li:nth-child(5) ul li {overflow: hidden;}
#nav li:nth-child(5) ul li a[href*="/watch/special"]:before {content: "Special";margin-right: 125px;}

/* Hide 'Help' */
#nav > li:nth-child(6) {display: none !important;}

/* 'Profile' */
#header-menu {display: block !important;right: inherit !important;}
#header-menu.pulldown {top: 0 !important;height: 0 !important;width: 0 !important;}
#header-menu .header-menu-unit {display: block !important;position: absolute !important;padding-left: 0 !important;}
.header-list, .header-message, .header-notification, .header-profile {height: 0 !important;width: 0 !important;}
.header-menu-unit.header-profile, #header-menu .border {visibility: hidden;}
.header-menu-unit.header-profile {top: 39px !important;}
.header-profile .header-profile-dropdown {
    display: block !important; visibility: visible !important;
    right: inherit !important; padding: 5px 0 0 !important;
    border: none !important; box-shadow: none !important;
    height: 41px !important; width: 160px !important;
    background: transparent !important; overflow: hidden; }
.header-profile .header-profile-dropdown:hover {height: auto !important;}
.header-profile .header-profile-dropdown ul {border-radius: 0 0 5px 5px;margin: -5px 10px 10px 0 !important;}
.header-profile .header-profile-dropdown ul:hover {box-shadow: 0 0 10px rgba(var(--colorHeaderShadow),0.5);}

.header-profile .header-profile-dropdown ul li:first-child a {
    font-weight: bold !important;
    font-size: 14px !important;
    color: rgba(var(--colorDarkText)) !important;
    padding: 14px 20px !important;
    background: transparent !important; }
.header-profile .header-profile-dropdown ul li:first-child a:hover {
    background: transparent !important;
    color: rgba(var(--colorDarkTextHover)) !important;
    transition: 0.5s; }
.header-profile .header-profile-dropdown ul li:last-child a {padding-bottom: 17px !important;border-radius: 0 0 5px 5px;}
.header-profile-dropdown .fa-book, .header-profile-dropdown .fa-cog, .header-profile-dropdown .fa-sign-out {display: none !important;}
.header-profile .header-profile-dropdown ul li a {
    border: none !important;
    font-style: normal !important;
    padding: 11px 20px !important; }
.header-profile .header-profile-dropdown ul li a,
.header-profile .header-profile-dropdown ul li a:hover {background: rgba(var(--colorLessDark)) !important;color: rgba(var(--colorLessDarkTextAlt)) !important;}

/* Invite/Request # */
.header-profile-dropdown ul li a[class$="link-count"] {
    font-size: 10px !important; font-weight: bold;
    height: 14px !important; line-height: 12px !important;
    padding: 4px !important; margin: 7px 12px 0 !important;
    border: 2px solid rgba(var(--colorLessDarkTextAlt)) !important; border-radius: 50%;
    color: rgba(var(--colorLessDarkTextAlt)) !important; }

/* Green 'New' Circle */
.header-profile-dropdown ul li a[class$="link-count"]:before {
    background: rgba(var(--colorGreen)) !important;
    content: ""; border-radius: 50%;
    position: absolute; top: 20px; left: 72px;
    height: 7px !important; min-width: 7px !important; }

/* Notifications & Messages (Buttons) */
.header-notification {top: 89px !important;left: 1023px;}
.header-message {top: 89px !important;left: 993px !important;}
.header-notification-button .fa, .header-message-button .fa {
    font-size: 10px !important;
    color: rgba(var(--colorLessDarkText)) !important;
    line-height: 20px !important; }
.header-notification-button .fa {margin-top: 1px;}
.header-notification-button, .header-message-button {
    background: rgba(var(--colorLessDark)) !important;
    height: 22px !important;
    width: 22px !important;
    transition: 0s !important;
    border-radius: 3px !important; }
.header-notification,
.header-notification-button.new,
.header-notification-button {z-index: 501 !important;}
.header-message-button {z-index: 500 !important;}
.header-notification-button::after, .header-message-button::after {display: none !important;}
.page-common #header-menu>.border, .page-common #header-menu>.border:before {display: none !important;}

/* New & Unread */
.header-notification-button.new, .header-message-button.has-unread {background: rgba(var(--colorPink)) !important;}
.header-notification-button:hover .fa, .header-message-button:hover .fa,
.header-notification-button.has-unread .fa {color: rgba(var(--colorBlue)) !important;}
.header-notification-button.new .fa, .header-message-button.has-unread .fa {color: white !important;}

/* Lists (Dropdown) */
.header-list {top: 77px !important;left: 546px !important;}
.header-list-button {display: none !important;}
.header-list .header-list-dropdown {
    background: none !important; display: block !important;
    border: none !important; box-shadow: none !important;
    top: 0 !important; left: 0 !important;
    height: 46px !important; width: 198px !important; }
.header-list .header-list-dropdown ul {height: 46px !important;width: 50px !important;overflow: hidden;}
.header-list .header-list-dropdown ul:hover {width: 198px !important;}
.header-list .header-list-dropdown ul:before {
    content: "List"; position: absolute; opacity: 1;
    color: rgba(var(--colorDarkText)); border: 14px solid transparent;
    font-size: 15px; font-weight: bold; }
.header-list .header-list-dropdown ul:hover:before {content: "List ▹";}
.header-list .header-list-dropdown ul li {position: relative;height: 22px;width: 30px;}
.header-list .header-list-dropdown ul li a {
    background: rgba(var(--colorLessDark)) !important; color: rgba(var(--colorLessDarkText)) !important;
    border: none !important; padding: 2px 7px 4px !important;
    font-size: 14px !important; font-style: normal !important;
    font-family: FontAwesome !important;
    overflow: hidden; opacity: 0;
    height: 16px; width: 16px; }
.header-list .header-list-dropdown ul:hover li a {opacity: 1;}
.header-list .header-list-dropdown ul li a:hover {transition: 0.25s;}
.header-list .header-list-dropdown ul li a:before {margin-right: 25px;}
.header-list .header-list-dropdown ul li:nth-child(1) a {border-radius: 3px 0 0 3px;}
.header-list .header-list-dropdown ul li:nth-child(4) a {border-radius: 0 3px 3px 0;}
.header-list .header-list-dropdown ul li:nth-child(1) {left: 65px;margin-top: 12px;}
.header-list .header-list-dropdown ul li:nth-child(2) {left: 95px;top: -22px;}
.header-list .header-list-dropdown ul li:nth-child(3) {left: 125px;top: -44px;}
.header-list .header-list-dropdown ul li:nth-child(4) {left: 155px;top: -66px;}

/* Icons */
.header-list .header-list-dropdown ul li:nth-child(1) a:before {content: "";margin-left: 2px;}
.header-list .header-list-dropdown ul li:nth-child(2) a:before {content: "";margin-left: 3px;}
.header-list .header-list-dropdown ul li:nth-child(3) a:before {content: "";margin-left: 4px;}
.header-list .header-list-dropdown ul li:nth-child(4) a:before {content: "";margin-left: 2px;}

/* Icons (Hover) */
.header-list .header-list-dropdown ul li:nth-child(1) a:hover,
.header-list .header-list-dropdown ul li:nth-child(2) a:hover {color: rgba(var(--colorGreen)) !important;}
.header-list .header-list-dropdown ul li:nth-child(3) a:hover {color: rgba(var(--colorOrange)) !important;}
.header-list .header-list-dropdown ul li:nth-child(4) a:hover {color: rgba(var(--colorPurple)) !important;}

/* Notifications (Inner) */
.header-notification-dropdown {
    top: 11px !important;
    right: -30px !important;
    z-index: 500 !important;
    box-shadow: none !important; }
.header-notification-dropdown .arrow_box {
    background: rgba(var(--colorLightBlue)) !important;
    padding: 8px !important;
    border: none !important; border-radius: 8px;
    box-shadow: 0 0 10px rgba(var(--colorBlue),0.75) !important; }
.arrow_box::before, .arrow_box::after {display: none !important;}
.header-notification-item-list {max-height: 395px !important;}
.header-notification-item .time {font-size: 10px !important;letter-spacing: 1px;line-height: 17px !important;}

.header-notification-dropdown-inner {width: 345px !important;}
.header-notification-dropdown-inner h3 {
    box-sizing: content-box !important;
    font-size: 0 !important;
    padding: 0 0 7px !important;
    border-color: rgba(var(--colorBlue),0.35) !important; }
.header-notification-dropdown-inner h3 .mark-all {color: rgba(var(--colorTextHeader)) !important;float: none !important;font-size: 15px !important;}
.header-notification-dropdown-inner h3 .mark-all:hover {text-decoration: none !important;}
.header-notification-dropdown-inner h3 .mark-all.disabled {color: transparent !important;}
.header-notification-dropdown-inner h3 .settings a {font-size: 0 !important;color: transparent !important;}
.header-notification-dropdown-inner h3 .settings .fa-cog {
    float: right !important; border-radius: 3px;
    position: relative; top: -23px;
    margin-right: 30px !important;
    padding: 2px 3px 2px 5px;
    font-size: 14px !important;
    box-shadow: 0 0 5px rgba(var(--colorBlue),0.75) !important;
    background: rgba(var(--colorBlue)) !important; color: white !important; }
.header-notification-dropdown-inner h3 .settings .fa-cog:before {content: "" !important;}

.header-notification-item {border: none !important;margin-bottom: 2px;}
.header-notification-item:hover {background: none !important;}
.header-notification-item.new {background: rgba(var(--colorBlue)) !important;color: white !important;border-radius: 4px;}
.header-notification-item.new:hover {opacity: 0.9;}

.header-notification-item a, .header-notification-item .time, .header-notification-item .message,
.header-notification-item .category, .header-notification-item-content {color: rgba(var(--colorText)) !important;}

.header-notification-item-content {min-height: 20px !important;}

.header-notification-item.new a, .header-notification-item.new .time,
.header-notification-item.new .message, .header-notification-item.new .category,
.header-notification-item.new .header-notification-item-content {color: white !important;}

.is-read {color: rgba(var(--colorBlue)) !important;top: -3px !important;}
.is-read .fa-check-circle {color: rgba(var(--colorBlue)) !important;}
.is-read .fa-check:hover {color: inherit !important;}

.header-notification-item.new .header-notification-item-header .is-read {color: white !important;top: -3px !important;}
.header-notification-item.new .header-notification-item-header .is-read .fa-check-circle {color: white !important;}

/* 'View All' */
.header-notification-view-all {margin-top: 5px;border-radius: 4px;border-top: 2px solid rgba(var(--colorBlue),0.35);}
.header-notification-view-all a {
    background: none !important;
    color: rgba(var(--colorText)) !important;
    border: none !important;
    padding: 5px 0 0;
    letter-spacing: 2px;
    font-size: 15px !important;
    font-style: normal !important; }

.header-notification-item-content.friend_request img {border-radius: 4px;margin-right: 4px;}
.friend_request .actions .result {background: white !important;color: rgba(var(--colorText)) !important;}
.action-button.accept {background: rgba(var(--colorGreen)) !important;}
.action-button.deny {background: rgba(var(--colorPink)) !important;}
.action-button.accept, .action-button.deny,
.friend_request .actions .result {
    letter-spacing: 1px;
    border: none !important;
    line-height: 2em !important; }

/* Username */
.arrow_box .inner a[href*="/profile/"] {border-bottom: 2px dashed rgba(var(--colorBlue));}
.arrow_box .new .inner a[href*="/profile/"] {border-bottom: 2px dashed white;}
.arrow_box .inner a[href*="/profile/"].fl-l.mr4 {border-bottom: none;} /* IMG */

/* Search bar */
#searchBar {
    background: none !important;
    height: 22px !important;
    width: 125px !important;
    position: relative !important;
    padding: 12px 0 !important; }

#searchBar .form-select-outer {
    height: 18px !important; width: 96px !important;
    position: absolute; top: 12px; left: -78px;
    border-radius: 3px !important;
    border: 2px solid rgba(var(--colorLessDark));
    opacity: 0; z-index: 1; transition: 0.5s; transition-delay: 5s;}
#searchBar .form-select-outer:hover {opacity: 1;transition: 0.2s;}

/* Search arrow */
#searchBar:before {
    font-family: FontAwesome; font-size: 10px;
    content: ""; position: absolute;
    height: 19px; width: 22px;
    text-align: center;
    background: rgba(var(--colorLessDark)); color: rgba(var(--colorLessDarkText));
    padding: 3px 1px 0 0; border-radius: 3px;
    top: 12px !important; right: 103px !important; }

/* Hovered arrow */
#searchBar .form-select-outer:after {
    background: rgba(var(--colorLessDark)); color: rgba(var(--colorLessDarkText));
    content: ""; z-index: 2;
    padding: 1px 6px 0 4px; 
    position: absolute; top: 0; right: 0;
    font-family: FontAwesome; font-size: 10px; }

#searchBar #topSearchValue {
    background: rgba(var(--colorLessDark)) !important; color: rgba(var(--colorLessDarkText)) !important;
    display: block !important; padding: 0 10px !important;
    margin: -3px 0 0 -2px !important; border: none !important;
    height: 23px !important; width: 100px !important;
    font-size: 13px !important; font-weight: bold;
    text-transform: lowercase; box-shadow: none !important; }
#searchBar option {background: rgba(var(--colorLessDark)) !important;text-align: center !important;}

#topSearchText {
    background: rgba(var(--colorLessDark)) !important; color: rgba(var(--colorLessDarkText)) !important;
    float: right !important; padding: 3px 5px 5px !important;
    height: 14px !important; width: 85px !important;
    font-size: 11px !important; text-indent: 0 !important;
    border-style: none !important; border-radius: 3px !important; }
#topSearchText:focus {box-shadow: none !important;}

#topSearchButon, #topSearchButton {display: none !important;}

::-webkit-input-placeholder {color: transparent;padding-top: 1px;}
::-moz-placeholder {color: transparent;opacity: 1;}

/* Dropdown Search */
.incrementalSearchResultList {
    z-index: 400 !important;
    border: none !important;
    box-shadow: 0 0 10px rgba(var(--colorDarkShadow),0.25) !important; }
#topSearchResultList {
    width: 277px !important;
    top: 56px !important;
    left: -87px !important;
    border-radius: 4px 4px 8px 8px !important;
    box-shadow: 0 0 10px rgba(var(--colorHeaderShadow),0.5) !important; }
#advancedSearchResultList {top: 32px !important;border-radius: 8px !important;}
.incrementalSearchResultList .list.separator {text-transform: lowercase !important;}

.incrementalSearchResultList .list.list-bottom {
    background: white !important;
    color: rgba(var(--colorText)) !important;
    text-align: center;
    border-radius: 0 0 8px 8px !important; }
.page-common .incrementalSearchResultList .list.list-bottom.focus a span {text-decoration: none !important;}
.incrementalSearchResultList .list.list-bottom a {white-space: nowrap;overflow: hidden;text-overflow: ellipsis;}
.incrementalSearchResultList .list.focus .extra-info {white-space: normal;} /* People/Char */

.incrementalSearchResultList .list {
    background: none !important;
    border: none !important;
    padding: 5px !important; }
.incrementalSearchResultList .off .info {padding-left: 47px !important;}
.incrementalSearchResultList .info {font-style: normal !important;padding-left: 67px !important;}
.incrementalSearchResultList .media-type, .incrementalSearchResultList .extra-info {letter-spacing: 1px;}
.incrementalSearchResultList .list.focus .media-type {display: none !important;}
.incrementalSearchResultList .name {color: rgba(var(--colorTextLink)) !important;font-weight: bold !important;font-size: 13px !important;}
.incrementalSearchResultList .list.focus .name {color: rgba(var(--colorBlue)) !important;}

.incrementalSearchResultList .list:nth-last-of-type(-n+2):not(.list-bottom):not(.focus) {padding-bottom: 9px !important;}
