@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gw2crafts.net") {
html{ padding: 0!important; margin: 0!important; }
body { 
margin:0;
background-color: hsl(34, 53%, 82%);
background-image: repeating-linear-gradient(45deg, transparent 5px, hsla(197, 62%, 11%, 0.5) 5px, hsla(197, 62%, 11%, 0.5) 10px,                  
  hsla(5, 53%, 63%, 0) 10px, hsla(5, 53%, 63%, 0) 35px, hsla(5, 53%, 63%, 0.5) 35px, hsla(5, 53%, 63%, 0.5) 40px,
  hsla(197, 62%, 11%, 0.5) 40px, hsla(197, 62%, 11%, 0.5) 50px, hsla(197, 62%, 11%, 0) 50px, hsla(197, 62%, 11%, 0) 60px,                
  hsla(5, 53%, 63%, 0.5) 60px, hsla(5, 53%, 63%, 0.5) 70px, hsla(35, 91%, 65%, 0.5) 70px, hsla(35, 91%, 65%, 0.5) 80px,
  hsla(35, 91%, 65%, 0) 80px, hsla(35, 91%, 65%, 0) 90px, hsla(5, 53%, 63%, 0.5) 90px, hsla(5, 53%, 63%, 0.5) 110px,
  hsla(5, 53%, 63%, 0) 110px, hsla(5, 53%, 63%, 0) 120px, hsla(197, 62%, 11%, 0.5) 120px, hsla(197, 62%, 11%, 0.5) 140px       
  ),
repeating-linear-gradient(135deg, transparent 5px, hsla(197, 62%, 11%, 0.5) 5px, hsla(197, 62%, 11%, 0.5) 10px, 
  hsla(5, 53%, 63%, 0) 10px, hsla(5, 53%, 63%, 0) 35px, hsla(5, 53%, 63%, 0.5) 35px, hsla(5, 53%, 63%, 0.5) 40px,
  hsla(197, 62%, 11%, 0.5) 40px, hsla(197, 62%, 11%, 0.5) 50px, hsla(197, 62%, 11%, 0) 50px, hsla(197, 62%, 11%, 0) 60px,                
  hsla(5, 53%, 63%, 0.5) 60px, hsla(5, 53%, 63%, 0.5) 70px, hsla(35, 91%, 65%, 0.5) 70px, hsla(35, 91%, 65%, 0.5) 80px,
  hsla(35, 91%, 65%, 0) 80px, hsla(35, 91%, 65%, 0) 90px, hsla(5, 53%, 63%, 0.5) 90px, hsla(5, 53%, 63%, 0.5) 110px,
  hsla(5, 53%, 63%, 0) 110px, hsla(5, 53%, 63%, 0) 140px, hsla(197, 62%, 11%, 0.5) 140px, hsla(197, 62%, 11%, 0.5) 160px       
)!important;
    font-size: 14px!important; 
    line-height: 20px!important;
    color: #333!important;
}

    hr { margin-bottom: 25px!important; 
        border-top: 1px solid #aeaeae;
        border-bottom: 1px solid #fff;
        border-left: 0;
        border-right: 0;
        background: none!important;
        height: 1px!important;
    }
    
    h1, h2 {
        background: #4D5263!important;
        padding: 10px;
        color: #eaeaea;
        border-radius: 2px;
        font-weight: 100!important;
        text-shadow: 0 1px rgba(0,0,0,0.8);
    }
    
    h3{
        display: inline-block;
        padding: 10px 35px;
        background: #9b6d69;
        color: #fff;
        text-shadow: 0 1px rgba(0,0,0,0.8);
    }

nav ul{
    background-color: #333!important;
    background: rgb(76,76,76);
    background: -moz-linear-gradient(top, rgb(76,76,76) 0%, rgb(0,0,0) 100%)!important;
    height: 40px;
    font-family: Arial;
    font-variant: inherit!important;
    font-size: 15px!important;
    text-transform: capitalize;
    width: 100%;
    text-align: center;
    border: 0 none!important;
    box-shadow: 0 0 2px 1px rgba(0,0,0,0.5)!important;
}

nav ul li {
    float: none!important;
    display: inline-block;
    margin: 0!important;
    padding: 0!important;
}

nav ul li a{
    color: #fff!important;
    padding: 0 10px!important;
    margin: 0!important;
    line-height: 40px;
    -moz-transition: all 0.25s;
}

    nav ul li a:hover{
        background: rgba(0,100,155,0.8)!important;
        box-shadow: inset 0 0 3px 2px rgba(0,0,0,0.2);
    }
    
nav ul ul {
    background: #333!important;
    top: 44px!important;
    width: 200px!important;
}
    nav ul ul ul { top: 0px!important; }

nav ul ul li{
    display: block!important;
    background: #333!important;
    position: relative;
}

section {
    width: 920px!important;
    background: rgb(242,242,242);
    background: -moz-linear-gradient(top, rgb(242,242,242) 0%, rgb(255,255,255) 2%, rgb(255,255,255) 100%);
    background-color: #ffffff!important;
    border: 0 none!important;
    border-radius: 3px;
    padding: 10px 20px!important;
    margin-bottom: 35px!important;
    box-shadow: 0 0 5px 0 rgba(0,0,0,1)!important;
}

.s0 { background: #eaeaea!important; }
.s1 { background: #fafafa!important; }
    .quantity, .common { line-height: 24px;}
.goldIcon, .silverIcon, .copperIcon {
    margin-left: 2px!important;
    margin-right: 10px!important;
}
button.arrow { font-size: 14px!important; }
button.info { color: #fff!important; }
.quantity { width: auto!important;}
.itemIcon { margin: 0 10px!important; }

footer {
background: rgba(255,255,255,0.5)!important;
    width: auto!important;
    font-size: 11px!important; 
    margin: 0!important;
    border: 0 none!important;
    text-align: center;
}
}
