  /*===============================================[ Font Sizes ]===*/
  .my_th-greetingtbl .my_th-myebaytitle { font-size:18px !important; }
  .c-yl .r3_lt, td a:visited, td a:active, td a:link,
  .accrdn-msh a.accrdn-ms-ttl, .accrdn-msh-ct { font-size:10px !important; }
  .g-asm, .g-asm33, div.myeb_cont a, div.myeb_cont a:active,
  .g-asmR1, .dtbl-colCnt, .g-asmG, .pgn-0, .accrdn-m ul,
  div.local li a, .MyeBayToDosHeader-reminders,
  #glbfooter a, .addllinks a:link, .addllinks a:visited, .addllinks a:active,
  .bstab-tabListCnt a:link, .bstab-tabListCnt a:active, .bstab-tabListCnt a:visited,
  .my_th-greetingtbl td + td span a:link, .my_th-greetingtbl td + td span a:active,
  .my_th-greetingtbl td + td span a:visited  { font-size:11px !important; }
  .g-vxsB, .itmsPrPg-itemsPerPageSpanStyle { font-size:8px !important; }
  div.g-vxs + div, div.g-vxs + div +div { display:none !important; }
  button, input[type="reset"], input[type="button"], input[type="submit"] { font-size:10px !important; }
 .i-in { border:double 10px !important; color:#ff0000 !important; font-weight:bold !important; }


.bstab-tabListCnt .general-Section { width:12px !important; height:12px !important; }
.ln-e, .ln-c, .ln-ch, .ln-eh, .ln-nc { font-size: 10px !important;}
span.mbg-nw, .mbg-nw { font-size: 25px !important; color:#ff0000 !important; font-weight:bold !important; border:dotted 2px !important;}
.mbg { font-size: 25px !important; color:#ff0000 !important; font-weight:bold !important;}
}

