css:
@-moz-document domain(pinboard.in), url-prefix(http://pinboard.in)
{

/* GENERAL STUFF */

body
{
background: #edf6ec !important;
font: 76%/120% "Verdana", sans-serif !important;
color: #024705 !important;
margin: 0 0 0 20px !important;
}
a, a:link
{
color: #026d04 !important;
}
a:hover
{
color: #5300a0 !important;
text-decoration: none !important;
}
h1, h2, h3, h4, em
{
background: #edf6ec !important;
color: #024705 !important;
}
blockquote, code, pre
{
color:  #5300a0 !important;
}
blockquote
{
margin: 3px 60px 3px 10px !important;
}
textarea
{
font-size: 12px !important;
}
input[type="text"]
{
max-width: 100% !important;
}
.help, .bookmarklet
{
background: #e0dae8 !important;
}
.howto li tt, #right_bar ul li tt, #right_bar p tt, .answer tt
{
color:  #5300a0 !important;
background: #e0dae8 !important;
}
.alert
{
color:  #5300a0 !important;
background: #e0dae8 !important;
border: 0 !important;
padding-left: 20px !important;
padding-right: 0 !important;
}
#popup_header, .disabled, .count, #login
{
color: #6b986d !important;
}
.flash
{
color: #5300a0 !important;
font-weight: bold;
background: #e0dae8 !important;
}

/* HEADER AND FOOTER */

#banner
{
top: 0px;
margin-top: 0px !important;
background: #024705 !important;
color: #024705 !important;
width: auto !important;
padding: 10px;
z-index: 100;
border-bottom: 1px solid #6b986d !important;
border-bottom-right-radius: 10px;
border-bottom-left-radius: 10px;
}
#logo .pin_logo
{
display: none;
}
#logo #pinboard_name
{
background: url(data:image/png;base64,) center left no-repeat;
height: 14px;
line-height: 14px;
padding-left: 16px;
}
#timer
{
color: #d0e8d1 !important;
font-family: "Verdana", san-serif !important;
font-size: 0.7em !important;
}
#banner a, #banner a.banner_username
{
color: #edf6ec !important;
}
#banner a:hover, #banner a.banner_username:hover
{
color: #5300a0 !important;
}
#footer
{
color: #6b986d !important;
}
#footer hr
{
width: auto !important;
border-width: thin 0 0 0  !important;
border-style: solid !important;
border-color: #6b986d !important;
}

/* MAIN CONTENT */

#content
{
margin-top: 0px;
clear: left;
display: inline-block;
height: 100%;
padding: 0px 15px;
}
#content:after
{
content: ".";
display: block;
clear: both;
visibility: hidden;
line-height: 0;
height: 0;
}
html[xmlns] #content
{
display: block;
}
* html #content
{
height: 1%;
} 
#main_column
{
width: 730px !important;
}
#main_column div p, #main_column p, #content div p
{
color: #6b986d !important;
}
#main_column div table tr
{
background: #edf6ec !important;
}
#main_column a.bundle
{
font-weight: bold !important;
}
.user_navbar, #nextprev, #nextprev table tr td
{
color: #edf6ec !important;
}
.user_navbar a.selected
{
color: #5300a0 !important;
font-weight: bold !important;
}
.user_navbar div, #main_column p b
{
padding-left: 5px !important
}
.bookmark_count
{
color: #6b986d !important;
}
#nextprev
{
margin-bottom: -13px !important;
padding-left: 5px !important;
}
#per_page_picker
{
color: #6b986d !important;
padding-top: 7px !important;
}
a.per_page_selected, a.sort_order_selected
{
color: #5300a0 !important;
background: #edf6ec !important;
font-weight: bold !important;
}
.sort_arrow
{
margin-left: 10px;
color: #6b986d !important;
}
#bottom_next_prev
{
padding-top: 7px !important;
}
.paginator
{
padding-top: 10px !important;
margin-bottom: -10px !important;
float: left !important;
}

/* EDIT AND ORGANIZE */

#organize_main
{
color: #6b986d !important;
width: auto !important;
}
#organize_main .when, #organize_table tbody tr td, #organize_table tr td p span
{
color: #6b986d !important;
}
#organize_instructions
{
color: #6b986d !important;
width: 200px !important;
font-size: 0.9em !important;
opacity: 1 !important;
}
#bulk_edit, .selected
{
background: transparent !important;
}
#bulk_links #bulk_edit_options
{
margin-top: 17px !important;
}
#bulk_edit_box
{
color: #024705 !important;
background: #edf6ec !important;
border: 1px solid #6b986d !important;
border-radius: 10px !important;
}
#bulk_top_bar
{
background: #d0e8d1 !important;
border: 0 !important;
border-top-right-radius: 10px;
border-top-left-radius: 10px;
}
#confirm
{
background: #024705 !important;
color:  #024705 !important;
}
#confirm a
{
color: #edf6ec !important;
}
#confirm a:hover
{
color: #5300a0 !important;
}
#confirm_message
{
color: #edf6ec !important;
}
#selected_count
{
color: #5300a0 !important;
font-weight: bold !important;
}

/* BOOKMARKS */

.bookmark
{
width: 700px !important;
border-bottom: 1px dashed #6b986d !important;
padding: 3px !important;
padding-top: 8px !important;
margin-bottom: 0 !important;
}
.display
{
width: 700px !important;
}
a.url_display
{
color: #6b986d !important;
}
a.url_display:hover
{
color: #5300a0 !important;
}
a.bookmark_title
{
color: #026d04 !important;
font-size: 1.1em !important;
font-weight: bold !important;
text-decoration: none !important;
}
a.bookmark_title:visited
{
color: #479c41 !important;
}
a.bookmark_title:hover
{
color: #5300a0 !important;
}
.unread, a.unread
{
background: #e0dae8 !important;
}
a.has_bmark, a.has_bmark:visited
{
color: #6b986d !important;
}
a.has_bmark:hover
{
color: #5300a0 !important;
}
.description
{
color: #024705 !important;
line-height: 123% !important;
margin-bottom: 4px !important;
margin-top: 1px !important;
}
.description a
{
color: #026d04 !important;
}
.description a:visited
{
color: #479c41 !important;
}
.description a:hover
{
color: #5300a0 !important;
}
.description br
{
margin: 0 !important;
}
.bookmark a.tag, .source
{
font-size: 0.9em !important;
border: 1px solid #d0e8d1 !important;
padding: 1px 4px !important;
background: #d0e8d1 !important;
border-radius: 10px !important;
}
.bookmark a.tag:hover, .source:hover
{
background: #edf6ec !important;
border-color: #6b986d !important;
}
.private
{
background: #d0e8d1 !important;
border-top: #d0e8d1 !important;
border-left: #d0e8d1 !important;
border-right: #d0e8d1 !important;
border-bottom: 1px dashed #6b986d !important;
}
.private a.tag
{
border-color: #6b986d !important;
}
a.when
{
margin-left: 120px !important;
font-size: 0.8em !important;
color: #6b986d !important;
}
.star
{
color: #d0e8d1 !important;
font-size: 1.09em !important;
padding: 1px !important;
}
.selected_star
{
color: #5300a0 !important;
}
a.edit, a.delete, a.mark_read
{
font-size: 0.95em !important;
padding: 1px 4px !important;
border: 0 !important;
}
a.url_link
{
font-size: 0.95em !important;
float: right;
color: #026d04 !important;
background-color: transparent !important;
font-weight: bold !important;
}
a.url_link:hover
{
color: #5300a0 !important;
}
#edit_bookmark_form
{
color: #024705 !important;
border: 1px solid #6b986d !important;
background: #edf6ec !important;
border-radius: 10px !important;
}
#edit_bookmark_form p
{
color: #6b986d !important;
font-size: 0.8em !important;
padding-bottom: 2px !important;
}
.edit_links
{
margin: 0 10px 0 0 !important;
opacity: 1;
float: left;
text-align: left;
line-height: 135% !important;
}
.edit_links a
{
color: #edf6ec;
}
.confirm
{
color: #024705 !important;
}

/* RIGHT BAR & TAG CLOUD/TABLE */

#right_bar
{
width: 285px !important;
background: #edf6ec !important;
color: #6b986d !important;
border: 1px solid #6b986d !important;
float: right !important;
padding: 10px !important;
border-radius: 10px !important;
}
#right_bar a
{
border: 0 !important;
padding: 0 !important;
}
#right_bar a, #right_bar a.tag:visited, #tag_cloud #tag_cloud_header a.tag, #tag_cloud_header, #right_bar a.edit, #right_bar a.edit:visited
{
color: #026d04 !important;
}
#tag_cloud_header p a:hover, #tag_cloud #tag_cloud_header a.tag_heading_selected, #tag_cloud #tag_cloud_header a.tag:hover, #tag_cloud #tag_cloud_header a:hover, #right_bar a:hover, #right_bar a.tag:hover, #right_bar a.edit:hover, #right_bar #bundle_names a, #right_bar #bundle_names p a.bundle:hover
{
color: #5300a0 !important;
}
#right_bar a:visited
{
color: #479c41 !important;
}
#right_bar #bundle_names a.bundle, #right_bar #bundle_names a.bundle:visited, #tag_cloud_header a:link, #tag_cloud_header a:visited, #tag_cloud p, #right_bar .tag_count, #bundle_names p span
{
color: #6b986d !important;
}
#tag_cloud a.selected
{
background: #e0dae8 !important;
border-radius: 10px !important;
}
#right_bar #searchbox
{
margin-bottom: 10px !important;
}
#tag_cloud
{
margin-top: 10px !important;
width: 270px !important;
}
#tag_cloud div table tr td
{
font-size: 80% !important;
width: 250px !important;
}
#tag_cloud #tag_cloud_header a.tag, #right_bar #bundle_names a.bundle, #right_bar #bundle_names a.bundle:visited
{
font-size: 100% !important;
}
#right_bar #bundle_names a.bundle
{
line-height: 150% !important;
}
#right_bar a.tag, #tag_cloud_header a
{
font-weight: normal !important;
}
#tag_cloud a.tag_heading_selected, #right_bar #bundle_names a, #right_bar #bundle_names a.bundle, #bundle_names p span
{
font-weight: bold !important;
}

/* TAG BUNDLING PAGES */

#main_column table td
{
padding: 0 0 2px 0 !important;
}
#main_column table td, #main_column table td a
{
color: #6b986d !important;
font-size: 100% !important;
}
#main_column table td a:hover
{
color: #5300a0 !important;
}
#main_column table a.bundle
{
color: #026d04 !important;
font-size: 110% !important;
padding-right: 15px !important;
}
#main_column table a.edit
{
color: #026d04 !important;
padding-left: 10px !important;
font-size: 110% !important;
}
#bundle_header
{
padding-top: 20px !important;
}
#tagPickerNav 
{ 
border-bottom: 1px solid #6b986d !important;
}
#tagPickerSelectLinks span, #bundle_column span
{
color: #6b986d !important;
}
#show_all_link
{
padding: 1px 4px !important;
background: #e0dae8 !important;
border: 1px solid #e0dae8 !important;
border-radius: 10px !important;
}
#show_all_link[style*="rgb(255, 255, 170)"], #show_unbundled_link[style*="rgb(255, 255, 170)"]{
padding: 1px 4px !important;
background: #e0dae8 !important;
border: 1px solid #e0dae8 !important;
border-radius: 10px !important;
}
#show_all_link[style*="rgb(255, 255, 255)"], #show_unbundled_link[style*="rgb(255, 255, 255)"]{
padding: 1px 4px !important;
background: #edf6ec !important;
border: 1px solid #edf6ec !important;
border-radius: 10px !important;
}
#save_changes_marker
{
background: #e0dae8 !important;
border: 1px solid #5300a0 !important;
border-radius: 10px !important;
}
#save_changes_marker:hover
{
background: #5300a0 !important;
color: #edf6ec !important;
}
#tagpicker
{
line-height: 1.75em !important;
}
.tagNode
{
padding: 1px 4px !important;
background: #e0dae8 !important;
border: 1px solid #e0dae8 !important;
border-radius: 10px !important;
}
#bundle .tagNode
{
color: #024705 !important;
}
.tagNode:hover
{
border: 1px solid #5300a0 !important;
background: #e0dae8 !important;
}
.unbundled
{
border: 1px solid transparent !important;
background: transparent !important;
}
.unbundled:hover
{
border: 1px solid #5300a0 !important;
background: transparent !important;
}
.hover
{
border: 1px solid #5300a0 !important;
}
.tagSelected
{ 
background: #6b986d !important;
border: 1px solid #6b986d !important;
border-radius: 10px !important;
}
.tagSelected:hover
{
border: 1px solid #5300a0 !important;
background: #6b986d !important;
}
#typer_box, #bundle_column #bundle
{
border: 1px solid #024705 !important;
}
.help_box
{
border: 1px solid #6b986d !important;
background: #edf6ec !important;
border-radius: 10px !important;
}

/* TAG EDITOR */

.tagNode.once
{
color: #6b986d !important;
}
.tagNode
{
color: #479c41 !important;
}
.tagNode.common, #status_display
{
color: #026d04 !important;
}
.tagNode.tagSelected
{
color: #5300a0 !important;
}

/* SETTING PAGES */

.settings_heading, td.settings_heading
{
background: #edf6ec !important;
color: #024705 !important;
}
#tab_panes, #import_history_table
{
color: #024705 !important;
border: 1px solid #6b986d !important;
background: #edf6ec !important;
border-radius: 10px !important;
}
#tab_panes p, #tab_panes p b, #tab_panes ol ol li p, .service_box p, #import_history_table caption, #tab_panes div
{
color: #024705 !important;
}
#tab_panes td
{
background: #edf6ec !important;
}
.tabs
{
background: #edf6ec !important;
margin-left: 10px !important;
margin-top: 10px !important;
}
.tab
{
background: #edf6ec !important;
margin-right: 10px;
padding-top: 3px;
border: 0 !important;
}
.tab_selected
{
background: #edf6ec !important;
border: 1px solid #6b986d !important;
border-bottom: 0 !important;
border-top-right-radius: 10px;
border-top-left-radius: 10px;
}
.tab_spacer
{
display: none !important;
}
#import_history_table
{
margin-top: 15px !important;
padding: 10px !important;
color: #6b986d !important;
}
#import_history_table caption
{
font-weight: bold !important;
font-size: 100% !important;
}
#import_history_table tr
{
text-align: left !important;
}
#import_history_table th
{
padding-top: 7px !important;
}
#import_history_table td
{
font-size: 95% !important;
}

/* PROFILE PAGES */

#public_profile_left_column
{
width: 410px !important;
margin-right: 30px !important;
line-height: 130% !important;
}
#public_profile_tag_cloud
{
width: 420px !important;
margin: 28px 10px 20px 0 !important;
}
#public_profile_subscribe_div, .pprofile_heading
{
color: #6b986d !important;
}
#profile_main_column
{
width: auto !important;
}

/* MISC OTHER PAGES */

.homepage_subheading
{
color:  #5300a0 !important;
}
.tbullet
{
font-weight: bold !important;
}
.blog_entry table tbody tr th
{
background: #e0dae8 !important;
color: #026d04 !important;
border-bottom: 1px solid #6b986d !important;
}
#right_column
{
width: 240px !important;
background: #edf6ec !important;
color: #6b986d !important;
border: 1px solid #6b986d !important;
float: right !important;
padding: 10px !important;
border-radius: 10px !important;
}
#right_column p
{
color: #6b986d !important;
}
.logo
{
float: none !important;
display: block !important;
padding: 0 0 10px 0 !important;
}

}
