css:
body {
	background-attachment: fixed !important;
	background-color: #000000 !important;
	background-image: url("http://lvlt.bioware.cdn.ea.com/bioware/u/f/eagames/bioware/masseffect3/resources/assets/media/wallpapers/wallpaper-009-shepard-1600x1200.jpg") !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
}


#ts_wrap.vk {left: 144px !important;  width: 170px !important; overflow: hidden !important;}
#ts_cont_wrap.vk { left: 147px  !important; right: auto  !important; }
#ts_wrap.vk div.ts_input_wrap { width: 166px !important; }
#page_header.p_head1 .left{width: 134px !important;}
#ts_settings{display: none !important;}


#top_logo_down{
    background: transparent !important;
}

#page_header { 
    height: 41px !important;
    margin-bottom: -2px !important;
    position: relative !important;
    z-index: 100 !important;
    	border-radius: 0 0 20px 20px;
}

#side_bar {
    width:134px !important;
}

#fmenu, #left_blocks {
	border-radius: 15px !important;
}

#side_bar, #side_bar ol li { 
    background-color: transparent !important;

}

#side_bar ol li a {
    background-color: transparent !important;
}

#side_bar li ul {
    background-color: rgba(255, 255, 255, 0.9) !important;
    border-radius: 0px 0 0 10px !important;
}

#side_bar ol {
   background: -moz-linear-gradient(bottom, rgba(0,0,0,0.2), rgba(134,242,234,0.2)); /* Firefox 3.6+ */
    /* Chrome 1-9, Safari 4-5 */
    background: -webkit-gradient(linear, left top, left bottom, 
                color-stop(0%,rgba(0,0,0,0.2)), color-stop(100%, rgba(134,242,234,0.2)));
    /* Chrome 10+, Safari 5.1+ */
    background: -webkit-linear-gradient(bottom, rgba(0,0,0,0.2), rgba(134,242,234,0.2));
    background: -o-linear-gradient(bottom, rgba(0,0,0,0.2), rgba(134,242,234,0.2)); /* Opera 11.10+ */
    background: -ms-linear-gradient(bottom, rgba(0,0,0,0.2), rgba(134,242,234,0.2)); /* IE10 */
    background: linear-gradient(bottom, rgba(0,0,0,0.2), rgba(134,242,234,0.2)); /* CSS3 */ 
    border-radius: 0px 0 0 10px !important;
    box-shadow: -5px 5px 10px rgba(0,0,0,0.4);
}

#side_bar ol li a:hover {
    background-color: #3d5dff !important;
  color: white;
}

#header_wrap1{
    border-left-color:  transparent !important;
    border-right-color:  transparent !important;
}

#side_bar ol, #side_bar ul, #sideBar ol, #sideBar ul{
   border: 0px; border-left: 1px solid rgba(0, 0, 0, 0.4); 
  border-bottom: 1px solid rgba(0, 0, 0, 0.4);
}
.more_div {display: none; width: 0px; height:0px;}

div#left_blocks div#left_block3_0 div.left_box, div#left_blocks div#left_block3_0 div#left_hide3_0.left_hide{
    border-radius: 10px !important;
    background-color: rgba(255, 255, 255, 0.9) !important;
}

#fmenu{
	padding: 0 !important;
	opacity: 0,7 !important;
  	border-radius: 0 0 20px 20px;
}

#content{
    background-color: rgb(255, 255, 255) !important;
    opacity: 0.7;
  	border-radius: 0 0 20px 20px;

}
#wrap1, #wrap2 {  	border-radius: 0 0 20px 20px;
     box-shadow: 0 0 10px rgba(0,0,0,0.5);
}

#side_bar ol li .left_row:hover{
  	margin-left:-15px;
  	height:30px;
  	padding-top:4px;
    border-radius: 10px 0 0 10px !important;
    text-decoration: none !important;
  	background: black;
  	background: #6dc2ff;
    background: -moz-linear-gradient(top, #6dc2ff, #0074c7);
    background: -webkit-gradient(linear, left top, left bottom, 
                color-stop(0%,#6dc2ff), color-stop(100%,#0074c7));
    background: -webkit-linear-gradient(top, #6dc2ff, #0074c7);
    background: -o-linear-gradient(top, #6dc2ff, #0074c7); 
    background: -ms-linear-gradient(top, #6dc2ff, #0074c7);
    background: linear-gradient(top, #6dc2ff, #0074c7); 
  	font-size: 14px;
  	border:0px;
  	border-left:1px solid #000;
  	border-bottom:1px solid #000;
  	border-top:1px solid #000;
  	font-weight: bold;
text-shadow: 0px 1px 2px #000000;  
}
div.post_table img {
	  border-radius: 10px;
	  border:2px solid black;
}
div.post_table div.replies img.reply_image {
  width:28px !important;
  height:28px !important;
}
div.post_table img:hover 
{
-moz-transition: all 0.8s ease-in-out;
-webkit-transition: all 0.8s ease-in-out;
-o-transition: all 0.8s ease-in-out;
-ms-transition: all 0.8s ease-in-out;
transition: all 0.8s ease-in-out;
-moz-transform: rotate(-360deg);
-webkit-transform: rotate(-360deg);
-o-transform: rotate(-360deg);
-ms-transform: rotate(-360deg);
transform: rotate(-360deg);
}
#footer_wrap {
  background: white;
  width: 500px !important;
  float:left;
  margin-left:200px;
  margin-top:-1px;
  border: 0px;
  border-left:1px solid rgba(0, 0, 0, 0.4);
  border-right:1px solid rgba(0, 0, 0, 0.4);
  opacity: 0.7 !important; 
  box-shadow: 0px 10px 10px rgba(0,0,0,0.7);
}
#page_body {
    border-radius: 0 0 20px 20px;
}
#owner_photo_wrap, #page_avatar img {
     opacity: 1.0 !important; 
}
/****Меню****/
#page_header .back{background:#4E729A!important;position:fixed!important;left:0;z-index:110}
    #page_header .left{background:url(https://m.vk.com/images/vkn_logo.png) no-repeat scroll 16px 9px transparent!important;position:fixed!important;z-index:110}
    #page_header .right{background:#4E729A!important;position:fixed!important}
    #page_header .content{left:0;position:fixed!important; border-bottom:1px solid black; box-shadow: 0 0 5px rgba(0,0,0,0.7);}
    #top_logo_down{background:url(https://m.vk.com/images/vkn_logo.png) no-repeat scroll 16px 9px transparent!important;margin-left:0!important}
    .top_home_link{position:fixed!important;left:0}
    #side_bar,#pad_wrap{position:fixed!important;margin-top:-4px !important;}
    #stl_side,#pad_arrow,#gp{display:none!important}
    #pad_wrap{z-index:800!important;top:7px!important}
    #pad_wrap[style*="top: 46px"]{right:6px!important;left:auto!important;top:46px!important}
    #stl_text{margin-top:52px!important}
    #pg_fixed{top:45px!important}
/******Белые блоки*****/
.fc_clist_inner, .fc_tab.fc_tab_online, .fc_tab
 {
   border: 1px solid #d3dae0 !important;
   background: #e1e7ed !important;
   position: relative !important;
   box-shadow: 0 0 10px rgba(0,0,0,0.5);
 }
div.fc_scrollbar_inner
 {
   background: rgba( 69, 104, 128, 0) !important;
 }
div.fc_tab_title.noselect
 {
   color: #45688e !important;
   text-shadow: 0px 1px 0px #ffffff !important;
 }
.fc_tab_min_wrap.fl_r,.fc_tab_close_wrap.fl_r,.fc_tab_max_wrap.fl_r
 {
   background-color: #45688e !important;   
 }
#gp_wrap
{
text-shadow: 0px 1px 0px #000000 !important;
}
