css:
@-moz-document url-prefix("http://tieba.baidu.com/f?kw="), url-prefix("http://tieba.baidu.com/f?ie=gbk&kw="), url-prefix("http://tieba.baidu.com/f?ie=utf-8&kw="), url-prefix("http://tieba.baidu.com/f/vote?kw="), url-prefix("http://tieba.baidu.com/f/good?kw="), url-prefix("http://tieba.baidu.com/f?tp") {
#frs_list_pager {
padding:2px 0px 3px 2px!important;
border:none !Important;
background:rgba(0,0,0,.65) !Important;
position:fixed;
bottom:0px;left:20%;
z-index:999999;
transition:bottom 0.2s linear;
-webkit-transition:bottom 5s linear;}

#frs_list_pager:hover {
bottom:0px;}

#frs_list_pager .cur,#frs_list_pager a {
border:none !Important;
background:rgba(62,109,181,.1) !Important;
color:#FFF !Important;}

#frs_list_pager a {
background:rgba(62,109,181,99) !Important;}
}

@-moz-document url-prefix("http://tieba.baidu.com/p"), url-prefix("http://tieba.baidu.com/f?ct="), url-prefix("http://tieba.baidu.com/f?z="), url-prefix("http://tieba.baidu.com/f?kz=") {
.l_posts_num {
padding:5px 5px 3px 2px!important;
background:rgba(0,0,0,.65);
position:fixed;
bottom:0px;left:13%;
z-index:999999;
transition:bottom 0.2s linear;
-webkit-transition:bottom 5s linear;}

.l_posts_num:hover {
bottom:0px;}

#thread_theme_3 .l_reply_num {
color:rgba(255,255,255,80) !important;
margin-left:10px !important;}

#pager_go2 {
-moz-appearance:none !important;
background:rgba(62,109,181,99) !important;
border:none !Important;
height:18px !Important;}

#jumpPage2 {
-moz-appearance:none !important;
border:none !Important;
background:rgba(200,200,200,.40) !important;}


.l_pager.pager_theme_2 .tP,.l_pager.pager_theme_2 a {
color:#FFF !important;
padding:2px 8px !important;
}

.l_pager.pager_theme_2 a {
background:rgba(62,109,181,65) !Important;}

.j_pager.l_pager.pager_theme_2 .tP {
color:rgba(62,109,181,65) !important;}

#thread_theme_4 {DISPLAY:NONE !iMPORTANT}
}

@-moz-document url-prefix("http://tieba.baidu.com/disk/index?kw=") {
.pagination {
padding:7px 4px 4px!important;
border:none !Important;
background:rgba(0,0,0,.65);
position:fixed;
bottom:-40px;left:20%;
z-index:999999;
transition:bottom 0.2s linear;
-webkit-transition:bottom 5s linear;}

.pagination:hover {
bottom:-15px;}

.pagination .current,.pagination a {
border:none !Important;
color:#FFF !Important;}

.pagination a {
border-radius:0px !Important;
background:rgba(62,109,181,99) !Important;}

.current {background:none !Important;}
}

@-moz-document url-prefix("http://tieba.baidu.com/f/search") {
.pager.pager-search {
padding:4px 5px 3px 2px!important;
border:none !Important;
background:rgba(0,0,0,.65);
position:fixed;
bottom:0px;left:1%;
z-index:999999;
transition:bottom 0.2s linear;
-webkit-transition:bottom 5s linear;}

.pager.pager-search:hover {
bottom:0px;}

.pager.pager-search .cur,.pager.pager-search a {
border:none !Important;
color:#FFF !Important;}

.pager.pager-search a {
background:rgba(62,109,181,99) !Important;}

.pager span.hasPage {
margin-left:10px !Important;
line-height: 20px !Important;
color:#FFF !important;}
}
