toolbar:not(#TabsToolbar):not(#toolbar-menubar) {
        margin-top: 0px !important;
        
        }
#main-window {-moz-appearance: none !important; 
        margin-top: -2px !important;
        margin-left: -2px !important;
        margin-right: -2px !important;
       /* background: transparent !important;*/
        }

#nav-bar,
#nav-bar:-moz-lwtheme-brighttext,

#navigator-toolbox[tabsontop=false] > #nav-bar,
#navigator-toolbox[tabsontop=false] > #nav-bar[tabsontop=false],
#navigator-toolbox[tabsontop=false] > #nav-bar[tabsontop=false]:not(:-moz-lwtheme),

#navigator-toolbox[tabsontop="true"] > #nav-bar,
#navigator-toolbox[tabsontop="true"] > #nav-bar[tabsontop="true"],
#navigator-toolbox[tabsontop="true"] > #nav-bar:not(:-moz-lwtheme),

#navigator-toolbox[iconsize=small][mode=icons] > #nav-bar,
#navigator-toolbox[iconsize=large][mode=icons] > #nav-bar 
{box-shadow: none !important;
-moz-appearance: none !important; 
background-color: transparent;
margin-top : 2px !important;   
margin-left : 30px !important; 
margin-right : 30px !important; 
border-width: 0px 40px 5px 40px !important; 
border-style: solid !important;
border-image: url(http://s25.postimg.org/9nplrcggv/Neogravity.png) 
10 100 3 100 fill stretch !important; 
height: 55px!important;
}
