css:
/**
 * Theme Name: NewsBlur - Kemwer Black
 * Description: A dark theme for NewsBlur
 * Version: 0.1.4
 * Author: Kemwer
 */

@-moz-document

  domain('newsblur.com') {
  

body {
  background-color: #191b1c !important;
}

.NB-static-alert {
  color: #ccc !important;
}

::-webkit-scrollbar {
    width: 12px !important;
}
::-webkit-scrollbar-track {
    background-color: rgba(245,250,255,.15) !important;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.6) !important; 
    border-radius: 4px !important;
}
::-webkit-scrollbar-thumb {
    background-color: rgba(0,5,10,0.5) !important;
    border-radius: 4px !important;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5) !important; 
}

a.NB-splash-link:hover,
.NB-module-features .NB-module-feature .NB-module-feature-description a:hover {
  color: #a85b40 !important;
}

a.NB-splash-link,
.NB-module-features .NB-module-feature .NB-module-feature-description a {
  color: #83b4e0 !important;
}

.NB-feeds-header-user,
.NB-taskbar-container,
#story_titles .NB-feedbar,
.NB-splash-info.NB-splash-bottom {
  color: #ddd !important;
  text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
  background: none !important;
  background-color: #191b1c !important;
  border-color: #303739 !important;
  border-bottom: 1px solid #000 !important;
}
#story_titles .NB-feedbar .feed .feed_title {
  color: #ddd !important;
  text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

.NB-splash-info .NB-splash-links {
  text-shadow: none !important;
}
.NB-splash-info .NB-splash-links a {
  color: #83b4e0 !important;
}
#story_titles .NB-feedbar .NB-feedbar-options,
#story_titles .NB-feedbar .NB-story-title-indicator {
color: rgba(255,255,255,.5) !important;
border: 1px solid rgba(0,0,0,.4) !important;
background-color: #242627 !important;
}
.NB-modal h5, 
.NB-module h5, 
.NB-module-header,
.NB-module-recommended-date,
#story_taskbar .NB-river-progress .NB-river-progress-text,
#story_taskbar .NB-feed-error .NB-feed-error-text,
ul.segmented-control, 
ul.segmented-control li {
  background: none !important;
  background-color: #303739 !important;
  color: #ccc !important;
  text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

.NB-callout {
  color: #333 !important;
  text-shadow: none !important;
}


.segmented-control li.NB-active, .segmented-control li.NB-active:active, .segmented-control-active li {
  background: none !important;
  background-color: #252D30 !important;
  color: #ccc !important;
}
.NB-module-stats-counts {
  background-color: #303739 !important;
}
.NB-graph-value .NB-graph-bar {
  background-color: #83b4e0 !important;
}
.NB-module-stats-count-number {
  color: #ccc !important;
  text-shadow: 1px 1px 1px rgba(0,0,0,0.5) !important;
}

.left-pane,
.NB-feedlist {
  background-color: #303739 !important;
}

.NB-feedlist .feed {
  border-top: 1px solid #303739 !important;
  border-bottom: 1px solid #303739 !important;
  padding-right: 2px !important;
  background-color: #303739 !important;
}

.NB-feedlist .feed.selected, .NB-feedlist .feed.NB-selected, .NB-feeds-header.NB-selected, .NB-feedlist .folder.NB-selected>.folder_title {
  background: none !important;
  background-color: #40492F !important;
  border-top: 1px solid #515A3F !important;
  border-bottom: 1px solid #282E1C !important;
}

.NB-feedlists .NB-socialfeeds {
  border-bottom: 0 !important;
}

.NB-feedlists .NB-socialfeeds .feed {
  background-color: #303739 !important;
  border-top-color: #303739 !important;
  border-bottom-color: #303739 !important;
}

.NB-feeds-header .NB-feeds-header-title,
.NB-feedlists .NB-socialfeeds .feed .feed_title,
.NB-feedlist .feed_title {
  color: #bbb !important;
  text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

.NB-feedbar .feed .feed_title, 
.NB-feedbar .folder_title_text,
.NB-feedlist .folder_title,
.NB-feedlist .folder.NB-selected>.folder_title {
  color: #777 !important;
  text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}

.unread_view_neutral .unread_neutral {
  font-weight: normal !important;
}
            
.NB-feeds-header {
  background: none !important;
  background-color: #49666f !important;
  border-color: #303739 !important;
}
            
.NB-end-line,
.NB-feed-story-endbar {
  background: none !important;
  background-color: #1B2325 !important;
  border-top-color: #303739 !important;
  border-bottom: 1px solid #111111 !important;
}
            
.NB-feeds-header-river-container .NB-feeds-header {
  border-bottom: 1px solid #222d31 !important;
}

.NB-feeds-header-starred.NB-feeds-header {
  border-top: 1px solid #222d31 !important;
}

.NB-feeds-header:hover:not(.NB-selected) {
  background: none !important;
  background-color: #5d8392 !important;
}

.unread_count_neutral {
  background-color: #49666f !important;
  text-shadow: none !important;
}

.NB-module-recommended .NB-recommended-description,
.NB-module-features .NB-module-feature .NB-module-feature-description {
  color: #ccc !important;
}

.NB-module.NB-module-features .NB-module-content-header,
.NB-module-recommended .NB-recommended-description,
.NB-module-recommended .NB-recommended-title {
  border-bottom: 1px solid #777 !important;
}

.NB-recommended-title {
  color: #eee !important;
}

.NB-feed-stories {
  background-color: #191b1c !important;
}
  
.NB-story-content-container *[style],
.NB-story-content-container {
  color: #ccc !important;
}
    
.NB-feed-story blockquote {
  color: #777 !important;
}

.NB-feed-story a {
  color: #83b4e0 !important;
}

#story_titles .NB-story-title {
  color: #ccc !important;
  background-color: #191b1c !important;
  border-top: 1px solid #232526 !important;
  border-bottom: 1px solid #101111 !important;
}

#story_titles .NB-story-title:hover:not(.NB-selected) {
  background-color: #232526 !important;
  border-top: 1px solid transparent !important;
}
        
#story_titles .NB-story-title.NB-selected a.story_title, 
#story_titles .NB-story-title a.story_title {
  color: #ddd !important;
}
        
#story_titles .NB-story-title.NB-selected, .NB-interaction:hover:not(.NB-disabled) {
  color: #eee !important;
  border-top: 1px solid #101111 !important;
  border-bottom: 1px solid #101111 !important;
  background: none !important;
  background-color: #303739 !important;
}

.NB-feed-story a.NB-feed-story-title:hover, .NB-feed-story a.NB-feed-story-title:hover .NB-score-1, .NB-feed-story a.NB-feed-story-title:hover .NB-score--1 {
  color: #fff !important;
}

.NB-feed-story .NB-feed-story-header-info,
.NB-feed-story.read .NB-feed-story-header-info {
  background-color: #303739 !important;
}

.NB-feed-story a.NB-feed-story-title,
.NB-feed-story.read a.NB-feed-story-title {
  color: #ddd !important;
}

.NB-sideoption {
  text-shadow: 0 !important;
  background-color: #232526 !important;
}

.NB-sideoption .NB-sideoption-title {
  color: #777 !important;
}
              
.NB-sideoption:hover, .NB-sideoption.NB-active, .NB-story-starred .NB-sideoption.NB-feed-story-save, .NB-story-shared .NB-sideoption.NB-feed-story-share {
  background-color: #303739 !important;
}
.NB-story-comments-shares-teaser,
.NB-feed-story-comments {
  background-color: #222425 !important;
  border-color: #18191A !important;
  text-shadow: 0 1px 0 rgba(0,0,0,.8) !important;
}
.NB-story-comment,
.NB-story-comment-reply {
  border-color: #191b1c !important;
}
.NB-story-content-wrapper .NB-story-content-expander {
  background: #191b1c !important;
}
.NB-story-comment-content, 
.NB-story-comment-reply-content {
  color: #ccc !important;
}
.NB-story-comment .NB-story-comment-username {
  color: #83b4e0 !important;
  text-shadow: 0 -1px 0 rgba(0,0,0,.5) !important;
}
.NB-story-content-wrapper .NB-story-content-expander .NB-story-cutoff {
  background: transparent url(data:image/png;base64,) repeat-x left bottom !important;
}
.NB-splash-link-logo img {
  background-image: url(data:image/png;base64,) !important;
  width:183px !important;
  display:inline-block !important;
  padding:32px 0 0 0 !important;
  height: 0px !important;
}

}
