css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://tweakers.net) {
div#utracker {
background-color: white !important;
background-image: url(http://tweakimg.net/g/if/v2/tracker/top_wide.png) !important; 
background-position: left bottom !important;
background-repeat: no-repeat !important;
padding-bottom: 3px !important;
top: 90px !important;
}
div#utracker * {
background-color: white !important;
}
div#utracker div {
margin-left: 13px !important;
}

#channelNav {
background-image: none !important;
height: 1px !important;
overflow: hidden !important;
}
#utracker .trakeritem h4 a {
color: black !important;
}
#utracker ul li {
border-bottom:1px dotted #C8C8C8 !important;
}
#header {
margin-left: 13px !important;
margin-right: 13px !important;
}
#header div.hr {
display: none !important;
}
#contentWrapper {
padding-right: 14px !important;
padding-left: 25px !important;
}

.widearticle .sidebar {
width: 300px !important;
}
#utracker ul li a {
color:#444444 !important;
}

#utracker .trackeritem h4 a {
color:#646464 !important;
}
/* 'Onnodige' witruimte bovenin weg*/
.columnwrapper {
padding-top:0px !important;
}
body {
background: none !important;
}
#utracker .trackeritem ul {
border-top:1px dotted #555555!important;
}
#utracker ul li a.new {
color:#111111!important;
}
#utracker ul li a.new span.time {
color:#B22447!important;
font-weight: normal !important;
}

.frontpage .content {
width:437px!important;
}
div#contentBottom a {
margin: 0px !important;
}
div#contentBottom a img {
margin-top: 5px !important;
margin-left: 35px !important;
position: relative !important;
left: 20px !important;
top: 0 !important;
}
div#contentBottom a:first-child img {
margin-top: 18px !important;
margin-left: -20px !important;
left: 0 !important;
}
div#contentBottom ul,
div#contentBottom p,
div#contentBottom b {
position: relative !important;
left: 20px !important;
top: 0 !important;
}
#utracker.foldout ul li a:hover, 
#utracker.foldout ul li a:hover span.time, 
#utracker.foldout .trackeritem h4 a:hover {
color:#b22447 !important;
}
.res1024 div.content {
width:394px!important;
}
.res1024 div#contentBottom a img {
left: 00px !important;
}
.res1024 div#contentBottom a:first-child img {
margin-left: 0px !important;
}
}
