css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){

/* i really want this to be global */

#popover_blogs .tab_notice:after{display:none; }

.l-header .iconic>a{
		background-image:url('https://31.media.tumblr.com/0e298c08e13b5e0f3ed77d428c938c49/tumblr_inline_mxx1hmv3Gg1qkv2pp.png') !important;}
    #xkit-control.tab.iconic > a,

#xkit-control.tab.iconic.selected > a {

  background-image: url("http://media.tumblr.com/9e8c86f536f099f42858fd695003fad6/tumblr_inline_ngc7cbF06M1qkv2pp.png") !important;
background-size: 28px 28px !important;}
  
  #account_button {
    padding-right: -20px !important;
}
#account_button a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 38px !important;
    padding-top: 38px !important;
    background: url('http://media.tumblr.com/7cf184d5527d453edad203b3125a1747/tumblr_inline_ngc7b8i20Y1qkv2pp.png') !important;}


  .tab_bar .iconic a.tab_anchor {
  background-size: 175px 36px;
}
    #inbox_button a, #account_button a, #discover_button a, #home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 35px !important;
    padding-top: 36px !important;
}
.tab_bar .tab_inbox .tab_anchor {background-position: -70px 0}
  .tab_bar .tab_discover .tab_anchor {background-position: -35px 0}
.tab_bar .tab_home .tab_anchor {background-position: 0 0}
.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {
  opacity: .7;
}
.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}



.tab.iconic > .tab_anchor::before {content: none !important;}



.tab.iconic > .tab_anchor i::before {content: none !important;}
}
