css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {
/* Tracker wit & ronde onderkant */
#utracker {
	background-color: #FFFFFF !important;
	background-image: url(http://tweakers.net/ext/f/aBuGvOlAocgEyYRLRtAbFCSH/full.png) !important;
	background-position: left bottom !important;
	background-repeat: no-repeat !important;
	padding-bottom: 3px !important;
	margin-top: -14px !important;
}

/* Removing channel choices */
#channelNav {
	padding: 0 0 15px !important;
}
#channelNav fieldset,
#channelNav.hidden fieldset {
	height: 7px !important;
	padding: 0 !important;
}
#channelNav ul {
	display: none !important;
}

#utracker.foldout {
	background-color: #FFFFFF !important;
	z-index: 0 !important;
}
#utracker .trackeritem {
	background-color: #FFFFFF !important;
	padding-left: 12px !important;
}
#utracker.foldout .trackeritem {
	background-color: #FFFFFF !important;
}
#utracker .dummy {
	background-color: white !important;
	width: 227px !important;
}

#utracker .trackerbottom {
	display: none !important;
}

/* Trackeritems gestippelde lijnen */
#utracker ul li {
	border-bottom: 1px dotted #C8C8C8 !important;
}
#utracker .trackeritem ul {
	border-top: 1px dotted #555555 !important;
}

/* Trackeritems tekstkleuren */
#utracker ul li a,
#utracker.hct ul li a,
#utracker.foldout ul li a {
	color: #000000 !important;
}
#utracker ul li a {
	outline-color: #444444 !important;
}
#utracker ul li a.new,
#utracker.hct ul li a.new,
#utracker.foldout ul li a.new {
	color: #111111 !important;
}
#utracker ul li a:visited,
#utracker.hct ul li a:visited,
#utracker.foldout ul li a:visited {
	color: #444444 !important;
}
#utracker ul li a:hover,
#utracker ul li a.new:hover {
	color: #ED053E !important;
}

/* Trackeritems tussenkoppen tekstkleur */
#utracker .trackeritem h4 a,
#utracker.hct .trackeritem h4 a,
#utracker.foldout .trackeritem h4 a {
	color: #646464 !important;
}
#utracker .trackeritem h4 a:hover,
#utracker.hct .trackeritem h4 a:hover,
#utracker.foldout .trackeritem h4 a:hover {
	color: #ED053E !important;
}

/* Trackeritems tekstkleur tijd & datum */
#utracker ul li a span.time, #utracker.hct ul li a span.time, #utracker.foldout ul li a span.time,
#utracker ul li a:hover span.time, #utracker.hct ul li a:hover span.time, #utracker.foldout ul li a:hover span.time {
	color: #777777 !important;
}
#utracker ul li a.new span.time, #utracker.hct ul li a.new span.time, #utracker.foldout ul li a.new span.time,
#utracker ul li a.new:hover span.time, #utracker.hct ul li a.new:hover span.time, #utracker.foldout ul li a.new:hover span.time {
	color: #ED053E !important;
	font-weight: normal !important;
}
#utracker ul li a:hover span.caption,
#utracker.hct ul li a:hover span.caption,
#utracker.foldout ul li a:hover span.caption {
	color: #ED053E !important;
}

/* Tracker eigenschappen afbeelding */
#utracker .tracker_button {
 background-position: -16px 0 !important;
}
#utracker .tracker_button:hover {
 background-position: -16px -16px !important;
}

/* Tracker eigenschappen afbeelding met tracker rechts */
.trackerright #utracker .tracker_button {
 background-position: -48px 0 !important;
}
.trackerright #utracker .tracker_button:hover {
 background-position: -48px -16px !important;
}

/* Tracker instellingen afbeeldingen + margin-fix */
#trackerSettings span {
 background-image: url(http://tweakers.net/ext/f/98tPfZEN9MTTdfENtBaUKsUL/full.png) !important;
 margin: 4px 2px 8px !important;
}
#trackerSettings .choices {
 background-position: -32px 0 !important;
 margin-left: 12px !important;
}
#trackerSettings .choices:hover {
 background-position: -32px -16px !important;
}
#trackerSettings .contrast {
 background-position: -16px 0 !important;
}
#trackerSettings .contrast:hover {
 background-position: -16px -16px !important;
}
#trackerSettings .position {
 background-position: 0 0 !important;
}
#trackerSettings .position:hover {
 background-position: 0 -16px !important;
}
#trackerSettings .fixate, #trackerSettings .fixate:hover, .trackerfixed #trackerSettings .fixate, .trackerfixed #trackerSettings .fixate:hover {
 background-position: -48px 0 !important;
}


/* Tekst naar links bij geen items in wenslijst */
#utracker ul li a[title^="Geen producten"] span.time,
#utracker ul li a[title^="Geen favoriete lijst gekozen"] span.time {
	width: 0 !important;
}

/* Tracker refreshafbeelding uitschakelen */
#utracker img[src$="_ani.gif"] {
	display: none !important;
}

/* Tracker op lagere resoluties */
.res1100 #utracker,
.res1100 #utracker.foldout {
	background-image: url(http://tweakers.net/ext/f/OwSG4be5yn0Y1XpFfU7QXhka/full.png) !important;
	background-position: left bottom !important;
	background-color: #FFFFFF !important;
}
.res1100 #utracker,
.res1100 #utracker.foldout,
.res1100 #utracker .trackeritem {
	width: 190px !important;
}
.res1100 #utracker .dummy {
	width: 188px !important;
}
.res1024 #utracker,
.res1024 #utracker.foldout {
	background-image: url(http://tweakers.net/ext/f/6hflNWk7KaKelIZcW4uOLXH0/full.png) !important;
	background-position: left bottom !important;
	background-color: #FFFFFF !important;
}
.res1024 #utracker,
.res1024 #utracker.foldout,
.res1024 #utracker .trackeritem {
	width: 140px !important;
}
.res1024 #utracker .dummy {
	width: 138px !important;
}

/* Tracker op lage resolutie - h4 koppen naar binnen */
#page.res1024 #utracker .trackeritem h4 {
	padding-left: 15px !important;
}
#page.res1024 #utracker .trackeritem h4 span.time {
	display: none !important;
}

/* Tracker op lage resolutie - trackerkeuze pijltje naar binnen */
.res1100 #utracker .trackeritem h4 .tracker_button,
.res1024 #utracker .trackeritem h4 .tracker_button {
	right: 12px !important;
}

/* Tracker naar rechts verplaatst of uitgeschakeld */
.trackerright #dm, .trackerright #med, .trackerright #dbdown,
.notracker #dm, .notracker #med, .notracker #dbdown {
	margin-left: 0 !important;
}
.trackerright #utracker {
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	padding-bottom: 0 !important;
	margin-top: -21px !important;
	background-image: none !important;
}
.trackerright #utracker .trackeritem {
	padding-left: 0 !important;
}

.trackerright #trackerSettings span, .trackerright #trackerSettings .choices {
 margin: 4px 2px 1px !important;
}
.trackerright #trackerSettings .position {
 background-position: 0 -32px !important;
}
.trackerright #trackerSettings .position:hover {
 background-position: 0 -48px !important;
}


/* Tracker met hoog contrast */
#utracker.hct,
#utracker.hct.foldout {
	background-image: none !important;
	background-color: transparent !important;
	background-position: left top !important;
	margin-top: -2px !important;
}

#utracker.hct .dummy {
	border: 1px dashed #E4E4E4 !important;
	margin-left: -1px !important;
	margin-right: -1px !important;
}
#utracker.hct .dummy {
	background-color: transparent !important;
}
.res1100 #utracker.hct, .res1100 #utracker.hct .trackeritem {
	width: 186px !important;
}
.res1024 #utracker.hct, .res1024 #utracker.hct .trackeritem {
	width: 136px !important;
}
#utracker.hct .trackeritem {
	padding-left: 0 !important;
}
#utracker.hct .trackeritem,
#utracker.foldout.hct .trackeritem {
 background-color: #EDEDED !important;;
}
.res1100 #utracker.hct .trackeritem h4 .tracker_button,
.res1024 #utracker.hct .trackeritem h4 .tracker_button {
 right: 5px !important;;
}
.hct #trackerSettings span {
 margin: -3px 2px 6px !important;
}
.hct #trackerSettings .choices {
 margin-left: 2px !important;
}
.hct #trackerSettings .contrast:hover {
 background-position: -16px -32px !important;;
}
.hct #trackerSettings .fixate {
 background: url(http://tweakimg.net/g/if/v2/tracker/sprite.png) -112px 0 no-repeat !important;;
}
.hct #trackerSettings .fixate:hover {
 background-position: -112px -16px !important;;
}


/* Tracker met hoog contrast op lage resolutie */
.res1100 #utracker.hct,
.res1024 #utracker.hct {
	margin-top: 4px !important;
}

/* Tracker met hoog contrast naar rechts verplaatst */
.trackerright #utracker.hct {
	background: none !important;
	margin-top: -30px !important;
}
.trackerright .hct #trackerSettings span {
 margin: -3px 2px 6px !important;
}


/* Fullscreen pagina's zonder tracker */



/* Extra tweaks */

/* No useless space around page */
#layout {
	margin: 2px auto !important;
	padding: 0 !important;
}

/* Header fix removing channel color */
#header div.hr {
	display: none !important;
}

/* Remove useless whitespace above content */
.columnwrapper {
	padding-top: 0 !important;
}

/* More compact breadCrumb */
p.breadCrumb {
	padding: 4px 0 !important;
	margin: 0 1px 5px !important;
}
.fullScreen p.breadCrumb {
padding-top: 15px !important;
}

/* True sponsorlogo uit breadcrumb */
#contentWrapper .sponsorlogo {
	display: none !important;
}

/* Frontepage items closer together */
.frontpageItem {
	margin-bottom: 8px !important;
}

/* Hide review buttons on Frontpage */
.frontpageItem #reviewTabList {
	display: none !important;
}

/* Always same colored logopointer */
#logopointer {
	cursor: default !important;
	background-position: 0 -40px !important;
}

/* Show spoiler content on hover */
div.spoiler:hover {
	color: white !important;
}

/* Remove simple banners */
.textadContainer, .frontpageAdItem, #as_topbanner, #b_ad, #b_ad_tmp {
	display: none !important;
}
/* Remove larger banners */
div.topBanner, div.leaderBoard, #headerReplacement {
	display: none !important;
}
}
