css:
@-moz-document url-prefix('http://www.shufoo.net/shxweb/site/shopDetail/'),
url-prefix('https://www.shufoo.net/shxweb/site/shopDetail/') {

/*=============================================================
  shufoo.net.css - urlprefix(http://www.shufoo.net/shxweb/site/shopDetail/)
  =============================================================*/

/*--- header ---*/
div#header {padding: 0 !important; width: 100% !important;}
div#header.simple div.header-inner
 {height: auto !important; width: 100% !important;}
div#header.simple div.header-inner ul.nav-top {left: 16% !important;}
div#header.simple div.header-inner div#nav-newmenber {left: 64% !important;}

/*--- thumnail row ---*/
body.column-2D div#contents  {padding: 0 !important; width: 100% !important;}
#shopbar {margin: 0 !important; width:100% !important;}
#shopbarBase {background-size: cover; width: 100% !important;}
#shopbarThumbListViewArea
 {left: 0% !important; width: 100% !important; height: 80px !important;}
#shopbarThumbListViewArea ul {height: auto !important;}

/*--- chirashi left (main) ---*/
body.column-2D div#main-content
 {width: 100% !important; margin: 0 -195px 0 0 !important;}
body.column-2D div#main-content>div
 {width: auto !important; margin: 0 195px 0 0 !important;}
object#externalbook {width: 100% !important;}
div.section-shop, div.section-shop div.shop-head
 {background: none !important; border: solid 1px gray; border-radius: 10px;}
div.section-shop div.shop-head {width: 100% !important;}
div.section-shop .shop-left-area
 {width: 100% !important; margin: 0 -203px 0 0 !important;}
div.section-shop .shop-left-area dl {
 width: auto !important;
 margin: 0 203px 0 0 !important;
 border: solid 1px gray; border-radius: 10px;
 }
div.section-shop .shop-left-area dl dt {background: none !important;}
.column-2D #sectionAreaSearch,
.column-2D #sectionShopSearch,
.column-2D #sectionFavoriteShop,
.column-2D #sectionCategorySearch .category_unit_01,
.column-2D #sectionCategorySearch .category_unit_02,
.column-2D #sectionCategorySearch .category_unit_03
 {width: 33% !important;}

/*--- chirashi right (sub) ---*/
div.section-shop .shop-right-area
 {width: 194px; border: solid 1px gray; border-radius: 10px;}

/*--- bottom banner ---*/
#bnrSuperbannerBottom {display: none !important;}

/*--- topic path ---*/
div.topic-path, div.topic-path>p {width: 100% !important; padding: 0 !important;}

/*--- footer ---*/
div#footer {width: 100% !important; padding: 0 !important;}
}
