css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {background: url('http://i36.tinypic.com/2cdtpx5.png')top left #f7f2eb no-repeat fixed !important;font-size:11px!important;}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:transparent!important;}

img#content_top, img#content_bottom {display: none!important;}

*{text-shadow:none !important; color:#7a7a7a !important;}
a:hover{color:#afafaf!important;}

a, a:visited{text-decoration:none!important}




ol#posts li.notification{background-color: #dbdfa3 !important;border:none!important;}
#left_column #posts .post .permalink{background-color:#7b9369;}


#dashboard_switch_blog_menu_current,


#dashboard_switch_blog_arrow_and_divider,


#dashboard_switch_blog_arrow_current


{background-image:none !important}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 


	background: none !important;


	width: 0px !important;


	height: 0px !important;


	margin-top: 0px !important;


	border-bottom: solid 11px white !important;


	border-right: 11px solid transparent !important;


	border-left: 11px solid transparent !important; }



.selection_nipple:not([class="selection_nipple white"]) {



	border-bottom: solid 11px #f9f9f9 !important;}



.notification_type_icon{display:none !important}
.notification.alt_type_icon{display:none !important}


.selected .following,



.selected .followers,



.selected .settings,



.selected .posts,



.selected .messages,



.selected .drafts,



.selected .queue, 



.selected .likes {



	color: #c0c5ca !important; 



	background: #f2f0db !important;}



a.explore_more_tags,



a.add_and_remove,



a.customize,



a.mass_editor



	{background-color:#f2f0db !important;  



	background-image:none !important;



	border:0px !important}



div.gradient,



div.dashboard_nav_item{
display:none !important}



a.permalink



	{background-color:#aac161 !important;



	opacity:.9; 



	background-image:none !important;



	border:0px !important}



.content.is_media, 



.mask.top, 



.mask.bottom {



	background: none !important; }



.post.photo_post .photo, 



.post.photo_post .video_thumbnail {



	border-radius: 16px !important;}



.post.photo_post .photo, 



.post.photo_post .video_thumbnail 



	{-moz-box-shadow: 1px 1px 3px #000000 !important;}



#posts .notification .nipple{border:none!important;}

#auto_pagination_loader



	{background: #dfe2c5 !important;}

#pagination a{
background: rgba(186,110,32,0.1) !important;
border-bottom:0px!important;}


div.return_to_top_icon{	background: none !important;


        background-image: url('http://lastsecret.net/gifs/mug_greentea.gif') !important;


	width: 67px !important;



	height: 48px !important;



	margin-top: 0px !important;



	border-bottom: solid 30px white !important;



	border-right: 0px solid transparent !important;



	border-left: 0px solid transparent !important;}



#left_column #posts .notification {
background-color:#ffffff!important;}
#left_column #posts .notification.alt {
background-color:rgba(255,255,255,0.4)!important;}

#new_post_notice_container{



color:#fff !important}



#new_post_notice_container a{



color:#fff !important}


/*tumblr logo*/

#logo {
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  padding-left: 300px !important;
  padding-top: 50px !important;
  background-image: url("http://i38.tinypic.com/2rhsdx5.png") !important;
  background-repeat: no-repeat !important;}


.permalink {background: none !important;



width: 16px !important;



height: 16px !important;



  background: #f9f9f9) !important;



  border-bottom-left-radius: 10px !important;



  border-top-right-radius: 10px !important;



  margin-top: 3px !important;



  margin-right: 4px !important;}
}

@-moz-document url-prefix("http://www.tumblr.com/new/"), url-prefix("http://www.tumblr.com/reblog/"), url-prefix("http://www.tumblr.com/edit") {
li.post,
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent !important;
box-shadow:none !important;
}
}
