css:
@-moz-document domain("fixfeel.com") {
body {
        background: #f8f8f8 url("http://bulkin.me/files/Ubuntu12.10_wallpaper.jpg") no-repeat;
  		background-size: cover;
  		background-attachment: fixed;
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif
}
.sharefeel {
width: 43%;}
.mainpage #header {
        position: relative;
        z-index: 1000;
        width: 940px;
        padding: 0 10px 0 10px;
        height: 40px;
        margin-bottom: 20px;
        text-shadow: 0 1px #000;
        background-color: #1d1d1d;
        background: #1d1d1d;
        -moz-box-shadow: 0px 2px 6px rgba(0,0,0,0.5);
        -webkit-box-shadow: 2px 0px 6px rgba(0,0,0,0.5);
        box-shadow: 0px 2px 6px rgba(0,0,0,0.5);
        -webkit-border-radius: 10px 10px 0 0;
        -moz-border-radius: 10px 10px 0 0;
        border-radius: 10px 10px 0 0
}
#header {
        position: fixed;
        z-index: 1000;
        width: 100%;
        height: 40px;
        text-shadow: 0 1px #000;
        background-color: #1d1d1d;
        background: #1d1d1d;
        -moz-box-shadow: 0px 1px 6px rgba(0,0,0,0.3);
        -webkit-box-shadow: 1px 0px 6px rgba(0,0,0,0.3);
        box-shadow: 0px 1px 6px rgba(0,0,0,0.3)
}
.tagshadleft {
        position: absolute;
        z-index: 100;
        left: 0;
        background: none;
        height: 34px;
        width: 34px;
}
.tagshadright {
        position: absolute;
        z-index: 100;
        left: 0;
        background: none;
        height: 34px;
        width: 34px;
}
.sortingmini a {
		color: rgb(255, 255, 255);
      	text-shadow: none;
		padding: 7px 9px;
		cursor: pointer;
		-webkit-border-radius: 5px;
		-moz-border-radius: 5px;
		border-radius: 5px;
}
.mpcommentarea {
        position: relative;
        background-color: #e9e9e9;
        background-image: none;
        background-repeat: no-repeat;
        width: 100%;
        -webkit-border-radius: 0 0 3px 3px;
        -moz-border-radius: 0 0 3px 3px;
        border-radius: 0 0 3px 3px;
}
.mpowner {
        display: block;
        width: 230px;
        margin-left: auto;
        margin-right: auto;
        background: #f8f8f8;
        border: 1px solid #d2d2d2;
        border-bottom: 1px solid #e0e0e0;
        -webkit-border-radius: 5px 5px 0 0;
        -moz-border-radius: 5px 5px 0 0;
        border-radius: 5px 5px 0 0;
        padding: 5px 10px 4px 10px;
        box-shadow: 0 0 6px #e9e9e9;
}
.mpinside {
        position: relative;
        width: 270px;
        background: #fff;
        border: 1px solid #d0d0d0;
        -webkit-border-radius: 0 0 4px 4px;
        -moz-border-radius: 0 0 4px 4px;
        border-radius: 0 0 4px 4px;
        -moz-box-shadow: 0 0 5px rgba(50,50,50,0.1);
        -webkit-box-shadow: 0 0 5px rgba(50,50,50,0.1);
        box-shadow: 0 0 5px rgba(50,50,50,0.1);
    	-moz-box-shadow: 0 0 6px #e9e9e9;
        -webkit-box-shadow: 0 0 6px #e9e9e9;
        box-shadow: 0 0 6px #e9e9e9;
}
.sortingmini a:hover, a.cursorting {
		color: #000;
		background-color: #dbdbdb;
		text-shadow: none;
    	-moz-box-shadow: 0 0 6px #e9e9e9;
        -webkit-box-shadow: 0 0 6px #e9e9e9;
        box-shadow: 0 0 6px #e9e9e9;
}
.tag_name {
        display: block;
        background-color: #ddd;
        text-shadow: none;
        -webkit-border-radius: 10px;
        -moz-border-radius: 10px;
        border-radius: 10px;
        height: 17px;
        padding: 5px 8px 0 8px;
        position: relative;
        z-index: 10;
}
.feelgroup {
        float: left;
        position: static;
        width: 678px;
        margin-bottom: 20px;
        background: #fff;
        border: none;
  		border-left: 1px solid #c9c9c9;
        -webkit-border-radius: 5px;
        -moz-border-radius: 5px;
        border-radius: 5px;
  		-moz-box-shadow: 0 0 6px #e9e9e9;
        -webkit-box-shadow: 0 0 6px #e9e9e9;
        box-shadow: 0 0 6px #e9e9e9;
        z-index: 100
}
.fourzerofour .closedexplain {
margin-top: 20px;
line-height: 140%;
color: rgb(255, 255, 255);
font-weight: bold;
font-size: 21px;
text-shadow: 2px 2px 2px rgba(39, 37, 37, 0.9);
}
.inside-feel {
border-radius: 4px;
}
}
