css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("month.gismeteo.ru") {
table { width: 100% !important; }
}
@-moz-document domain("gismeteo.ru") {
.soft-promo, #footer, #logo i, .rfc,  .rftl, .rftr, .rfbl, .rfbr, .counters, .ad.ad_topper_cont, table[width="984"], td[width="220"], td[width="216"], td[width="12"], td[height="8"], iframe[height="390"], td[width="230"], #table2, #table1 iframe, #table1 td[align="right"], td[height="28"], iframe[height="60"], td[height="7"][bgcolor="670085"], td[height="3"][bgcolor="E6D0EC"], table[height="67"] td[align="right"], img[width="282"][heigth="60"], td[style="width:473px;border:0px"], .cov > a, .cross_links_grad, #float_adv img {
	display: none !important;
}
body {
	background-image: url("/*[[back]]*/") !important;
	background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
}
/*** WIDE code ***/
#header, #cities-more .more, #astronomy .section, .m {
	margin: 0 !important;
}
	/* news section */
.article {
	width: 90% !important;
}
.pic-img {
	margin: auto !important;
	display: block !important;
}
.ugc .pic P, .ugc .pic .pic-img-descr, .article__title.bottom {
	text-align: center !important;
}
	/* else */
#canvas {
	width: 98% !important;
	padding: 0 !important;
}
#content, #cities1 .cities, #cities2 .cities, #cities3 .cities, .wsection table, .wbfull > table, .gmlgroup, table[width="987"], table[width="990"], table[width="735"] {
	width: 100% !important;
}
.cleft {
	float: none !important;
	width: auto !important;
	margin-right: 260px !important;
}
#weather-cities, #weather-maps, #informer, #feedback_form, #mobile_form, #static_page, #weather-news, #weather-daily, #wmaps-list, #weather-hourly, #weather-weekly, #geomagnetic, .page {
	float: none !important;
	margin-left: 270px !important;
	width: auto !important;
}
div.var_cont, #citysearch, .gmlgroup {
	float: none !important;
}
.cur_news_block, #map-view .player, #map-view .mult, .article__title.bottom {
	margin: auto !important;
}
#cities1 .cities ul, #cities2 .cities ul, #cities3 .cities ul {
	width: 25% !important;
}
#cities-teaser .cities {
	width: 33% !important;
}
#weather-news .fcontent, #interesting .fcontent, #weather-maps .fcontent, .Map_Catalog #wmaps-list .fcontent, #intown .fcontent, #geomagnetic .fcontent, #graph .fcontent {
	height: auto !important;
}
#weather-daily {
	position: inherit !important;
}
#astronomy ul.sun {
	padding-left: 45px !important;
}
#astronomy ul.moon {
	padding-right: 40px !important;
}
#astronomy strong {
	padding-right: 5px !important;
}
.wdata tbody .clicon, .wdata tbody .cltext {
	width: 12% !important;
}
.wtab {
	width: 31% !important;
}
.workday, .wbshort .weekend { /* прогноз на 2 недели, спойлера: равная ширина колонок */
	width: 15% !important;
}
.wbshort .wbnight { /* прогноз на 2 недели: ночь */
    left: 20% !important;
}
.wbshort .wbday { /* прогноз на 2 недели: день */
    left: 62% !important;
}
.full_date, .value.m_temp.c{ /* прогноз на 2 недели: побольше дату */
	font-size: 20px !important;
	text-shadow: 1px 1px black !important;
}
.wdata thead th { /* заголовки таблиц побольше */
	font: 11px Arial,sans-serif !important;
}
.wdata tbody .cltext {
	font-size: 14px !important;
}
.dayname {
	font-size: 16px !important;
}
.wdata-compact tbody th {
	width: 150px !important;
}
/* погода по-старому */
#weather-old, #weather-busy, #map-view, #citysearch, td[width="530"], td[width="500"], .txnn {
	width: auto !important;
}
/* геомагнитная таблица */
#geomagnetic table {
	float: left !important;
}
.gmlegend {
	margin: 34px 0 22px !important;
	padding-left: 30px !important;
}
.gmsource {
	clear: both;
}
/* левая колонка */
#weather, #projects, #intown, #graph, #water, #astronomy {
	position: absolute !important;
}
#weather		{ top: 133px !important; }
#projects, #intown	{ top: 360px !important; }
#water			{ top: 570px !important; }
#astronomy		{ top: 685px !important; }
#graph			{ top: 840px !important; }
#weather-hourly .bottom, #weather-daily .bottom {
	position: relative !important;
}
#water .temp {
	padding-left: 30px !important;
}
/* лого на странице с месячной погодой */
td[width="760"] {
	height: 90px;
}
/* новости, верхний "комикс" */
.mainmenu3 .nav {
	margin-bottom: 6px !important;
}
/*** darkness ***/
#ad-top, #logo, .rframe, #menu, h2, .wmaps li .map, .rframe h2, #weather .fcontent .header {
	border: none !important;
}
.rframe, #header, #weather .section, .mycity, #weather-news .gradient, #interesting .gradient, .input, #jSuggestContainer ul li, .rate_content_bg, h2 span.title.sel, .wmtop, .swmptab, #menu li ul.mainsubmenu, .body-clouds, .extrbcua .extra-title span, #rbcua .extra-title span, #tabs_closer, #tabs_opener, .wdata-compact thead .wtlogo, #weather-busy thead td, #weather-busy thead td.weekend, .wdata-compact tbody th, .wdata-compact td, .wcol0, .wcol1, .wcol2, .wcol3, .wcol4, .wdata-compact thead th, .mainmenu3 .balloon TABLE TD.cur A SPAN, .mainmenu3 .balloon TABLE TD STRONG SPAN, #content .main-informer-block .zbg-cnt, .mvtabs li, .mvtabs .ttl, .mvtabs .ttr, .multiplayer, .jhc, td[width="240"], .enclosures, #table1, #table1 td[height="90"], .mm, td[bgcolor="ffffff"], td[bgcolor="#ffffff"], td[bgcolor="#F0F0F0"], td[bgcolor="#E2EEFE"], .pageblock h2, td[align="center"][width="100%"], #content #weather-daily .more, #content #weather-hourly .more {
	background: none !important;
}
#ad-top {
	height: 1px !important; /* sorry, display:none crashes background */
}
/* text */
body, #weather .temp span.meas, #weather .cloudness dd table, #weather .cloudness dd td, #weather .date, #weather .unit, h2 > a, #menu li a, .input, #jSuggestContainer ul li, h6, #water .temp, .wdata tbody th, .wdata tbody td, .wdata thead th, h3, .rate_comment_input, h2 span.title.sel, .wtab, .links-black A, .links-black.links-em A EM, .links-black A.link-em EM, .items .item a.item__a, .extrbcua .rbcua.right_adv .links-black A span, #rbcua .rbcua.right_adv .links-black A span, h1, #weather .temp, .wdata-compact table, .wdata-compact tbody th, .ui-autocomplete.ui-menu, .soc2 H4, .monthname, hr, textarea, a.meny, .tx font, .phones, .form_labels{
	color: white !important;
}
#weather .temp {
	 text-shadow: -2px -2px 2px black, -2px 2px 2px black, 2px 2px 2px black, 2px -2px 2px black !important;
}
#menu li.news .cov a, .gmlegend dt, #geomagnetic tbody div b {
	color: black !important;
}
.rate_button.rate_button_like {
	color: #222 !important;
}
#weather .barp, #weather .wind, #weather .hum {
	color: #ddd !important;
}
#weather .wind dt {
	color: #0fa400 !important;
}
/* links */
a {
	color: #0bf !important;
}
a:hover, #menu li ul.mainsubmenu li.active b, #weather-busy thead td.weekend, .wbshort .weekend, .dayname {
	color: #0fb !important;
}
.links-black A:hover span {
	text-decoration: none !important;
	background: none !important;
}

#weather-cities, #weather-maps, #informer, #feedback_form, #mobile_form, #static_page, #weather, #projects, #weather-news, #information, #jSuggestContainer, .rframe.rate_popup.rate_popup_opened, #intown, #water, #astronomy, #weather-daily .fcontent, #maps-search, #wmaps-list, .mainmenu3 .nav, .layout .extra, .cat_cont, .box__h__i, #citysearch, .contents, #weather-hourly, #weather-weekly, #graph, #geomagnetic, #weather-old, #weather-busy, #map-view, .ui-autocomplete.ui-menu, .article, .jhcontent, table[width="240"], td[width="530"], td[width="500"], td[width="100%"][valign="top"][bgcolor="ffffff"], #feedBody, .ifilters__i, .txnn > table, td[width="510"][bgcolor="ffffff"], #exat, .pageblock, #error_404, td[align="center"][width="100%"][style="padding-top:20px"], #link_block.rframe {
	background: none repeat scroll 0 0 rgba(0, 0, 0, 0.8) !important;
	padding: 10px !important;
	border-radius: 10px !important;
	-webkit-border-radius: 10px !important;
	-moz-border-radius: 10px !important;
	-khtml-border-radius: 10px !important;
}
#cities-more, .rframe .stab, .wtab {
	background: rgba(0, 0, 0, 0.4) !important;
}
#menu li ul.mainsubmenu li {
	background: rgba(0, 0, 0, 0.7) !important;
}
#weather .temp span.meas {
	left: 79px !important;
	text-shadow: none !important;
	top: 0 !important;
}
.rframe .tab, #cities-more {
	 border: 1px solid black !important;
}
h2 span.title.sel {
	border-width: 1px !important;
}
.wtabs .swtab, .wtabs .hover, .daybg, textarea, .more.item_mid {
	background-color: #222 !important;
}
.daybgh {
	background-color: #333 !important;
}
#menu li ul.mainsubmenu li, .item__title.links-black {
	text-shadow: 1px 1px 3px black !important;
}
/* bottom */
.subregions_column:first-child {
    margin-left: 9% !important;
}
.subregions_column {
    width: 30% !important;
}
/* headers */
#weather-cities > h2, .fcontent > h2, .rfc, .rframe h2, .rframe .tside, .extra-title.links-black a, .wbshort, .violet-lu, .violet-l, .violet-long, #bg-top-line, .nightbg, .nightbgh, .mcapbg, tr[bgcolor="#f7f7ff"], .pl, .pageblock h2 span, td[style="width:106px;border:0px"], #menu li, #menu li.wide, .header.rate_header, .nokian #weather .fcontent .header {
	background: -moz-linear-gradient(top, #444444 0%, #222222 52%, #222222 99%) !important; /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#444444), color-stop(52%,#222222), color-stop(99%,#222222))  !important; /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top, #444444 0%,#222222 52%,#222222 99%)  !important; /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top, #444444 0%,#222222 52%,#222222 99%)  !important; /* Opera 11.10+ */
	border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-khtml-border-radius: 5px !important;
	padding-left: 10px !important;
}
/* IMAGES */
#logo, td[width="760"], table[height="67"] td[height="57"][width="282"] {
	background: url("http://raw.github.com/Pmmlabs/GismeteoWideBlack/master/images/logo.png") no-repeat scroll center 0 transparent !important;
	width: 700px !important;
	margin: auto !important;
	position: relative !important;
	top: 4px !important;
}
.wtabs .prev, .wtabs .next {
	background-image: url('http://raw.github.com/Pmmlabs/GismeteoWideBlack/master/images/wtabs.png') !important;
}
#weather .wicon {
	background-image: url('data:image/png;base64,') !important;
}
.rfc, .rframe .tside { /* frame.png */
	background-image: url('data:image/png;base64,') !important;
}
#astronomy ul {
	background-image: url('http://raw.github.com/Pmmlabs/GismeteoWideBlack/master/images/astro.png') !important;
}
.wmtprev, .wmtnext, .wmtnext .control, .wmtprev .control {
	background-image: url('http://raw.github.com/Pmmlabs/GismeteoWideBlack/master/images/wmtabs2.png') !important;
	background-color: transparent !important;
}
.buttonConst span.left, .buttonConst div.cont, .buttonConst span.right {
	background-image: url('http://raw.github.com/Pmmlabs/GismeteoWideBlack/master/images/button-solid.png') !important;
	background-color: transparent !important;
}
#projects .section .sprite li .icon, #projects .section .sprite .flakes a {
	background-image: url('http://raw.github.com/Pmmlabs/GismeteoWideBlack/master/images/projects.png') !important;
}
}
