css:
@-moz-document regexp("(.*)//lofigames.com/phpBB3(.*)"), regexp("(.*)//www.lofigames.com/phpBB3(.*)") {
/** FORUM **/

* {
  font-family: Georgia, "Times New Roman", serif !important;
}

body {
  padding-top: 0 !important;
  color: #ccc !important;
}

a:link, h3, .content, .copyright {
  color: #ccc !important;
}

a:hover {
  color: #c0a87b !important;
}

#logo {
  display: block;
  background: url("http://groenroos.fi/kenshi/logo.png") no-repeat top center;
  height: 300px;
  width: 850px;
  margin-top: -60px;
}

#logo img,
#site-description {
  display: none;
}

/*
.bg1, .bg2 {
  border-style: solid !important;
  border-width: 12px 11px 9px !important;
  -moz-border-image: url("http://www.groenroos.fi/kenshi/panel.png") 12 11 9 repeat;
  -webkit-border-image: url("http://www.groenroos.fi/kenshi/panel.png") 12 11 9 repeat;
  -o-border-image: url("http://www.groenroos.fi/kenshi/panel.png") 12 11 9 repeat;
  border-image: url("http://www.groenroos.fi/kenshi/panel.png") 12 11 9 fill repeat;
  background-color: transparent !important;
}
*/
}

@-moz-document regexp("(.*)//lofigames.com(.*)"), regexp("(.*)//www.lofigames.com(.*)") {
/** SITE **/

* {
  font-family: Georgia, "Times New Roman", serif !important;
}

html {
  background: url("http://groenroos.fi/kenshi/bg-loop.jpg") #7e5c41 !important;
}

body {
  background: url("http://groenroos.fi/kenshi/bg.jpg") no-repeat !important;
  background-size: contain;
}

hr {
  border: 0;
  height: 1px;
  background: #777;
  margin: 25px 0;
}

h2 {
  background: none !important;
  border: none !important;
}

td.createdate, .small, .modifydate {
  color: #aaa !important;
}

.small {
  padding-left: 20px;
}

#header,
#fordropmenu span.mainlevel,
#content_top, #content_bottom {
  display: none;
}

#fordropmenu {
  background: url("http://groenroos.fi/kenshi/logo.png") no-repeat top center !important;
  height: 275px !important;
  padding-top: 25px;
}

#fordropmenu a {
  color: #444;
  font-size: 12px;
}

#fordropmenu a:hover,
#fordropmenu a#active_menu {
  color: #967252;
}

#fordropmenu a.mainlevel {
  padding: 7px;
}

td.contentheading {
  background: url("http://groenroos.fi/kenshi/headline.png") no-repeat center center !important;
  width: 550px;
  height: 35px;
  border: 0 !important;
  padding: 0 12px;
  text-transform: none !important;
  line-height: 36px !important;
  letter-spacing: 1px !important;
  text-shadow: 0px 1px 3px #000;
  color: #fff;
}

td.contentheading a {
  color: #fff;
}

.contentpaneopen img,
.contentpaneopen p,
.contentpaneopen div {
  margin: 10px 20px;
  max-width: 520px;
  font-size: 12px;
}

.contentpaneopen ol,
.contentpaneopen ul {
  margin: 15px 20px;
  max-width: 480px;
  text-align: justify;
  font-size: 12px;
}

.contentpaneopen p img,
.contentpaneopen div img {
  margin: 0 !important;
}

td.createdate {
  padding-left: 20px;
  font-size: 11px;
  font-style: italic;
}

#content,
#leftcolumn {
  border-style: solid !important;
  border-width: 12px 11px 9px !important;
  -moz-border-image: url("http://www.groenroos.fi/kenshi/border.png") 14 11 9 repeat;
  -webkit-border-image: url("http://www.groenroos.fi/kenshi/border.png") 14 11 9 repeat;
  -o-border-image: url("http://www.groenroos.fi/kenshi/border.png") 14 11 9 repeat;
  border-image: url("http://www.groenroos.fi/kenshi/border.png") 14 11 9 fill repeat;
  -webkit-box-shadow: 0px 3px 6px 0px rgba(0, 0, 0, .7);
  box-shadow: 0px 3px 6px 0px rgba(0, 0, 0, .7);
  background: url("http://groenroos.fi/kenshi/stone.png") #443a27 !important;
}

#leftcolumn {
  margin-top: 10px !important;
}

#leftcolumn h3 {
  border: 0 !important;
  background: url("http://groenroos.fi/kenshi/sidebar-title.png") no-repeat !important;
  height: 42px;
  width: 192px !important;
  margin-left: -20px;
  padding: 0 !important;
  line-height: 38px;
}

#leftcolumn div.module:first-child h3 {
  margin-top: -13px !important;
}

#sgf {
  color: #222 !important;
  padding-bottom: 80px !important;
}

#maincolumn {
  background: none !important;
  border: 0 !important;
}

#header + br + table {
  width: 850px;
}

#header + br + table div > a {
  float: right !important;
}

#leftcolumn a[href^="http://www.indiedb.com/games/kenshi/downloads/"] {
  display: block;
  width: 140px;
  height: 72px;
  margin: 0 auto;
  background: url("http://groenroos.fi/kenshi/download.png") no-repeat;
}

#leftcolumn a[href^="http://www.indiedb.com/games/kenshi/downloads/"]:hover {
  background-position: 0 -72px;
}

#leftcolumn a[href^="http://www.indiedb.com/games/kenshi/downloads/"]:active {
  background-position: 0 -144px;
}

#leftcolumn a[href^="http://www.indiedb.com/games/kenshi/downloads/"] img {
  display: none;
}

#leftcolumn a[href="/index.php?option=com_content&view=article&id=259&Itemid=67"] {
  display: block;
  width: 140px;
  height: 72px;
  margin: 0 auto;
  background: url("http://groenroos.fi/kenshi/buy.png") no-repeat;
}

#leftcolumn a[href="/index.php?option=com_content&view=article&id=259&Itemid=67"]:hover {
  background-position: 0 -72px;
}

#leftcolumn a[href="/index.php?option=com_content&view=article&id=259&Itemid=67"]:active {
  background-position: 0 -144px;
}

#leftcolumn a[href="/index.php?option=com_content&view=article&id=259&Itemid=67"] img {
  display: none;
}

#content a[href*="bmtmicro.com/servlets/Orders.ShoppingCart"] {
  display: block;
  width: 140px;
  height: 72px;
  background: url("http://groenroos.fi/kenshi/buy-plain.png") no-repeat;
}

#content a[href*="bmtmicro.com/servlets/Orders.ShoppingCart"]:hover {
  background-position: 0 -72px;
}

#content a[href*="bmtmicro.com/servlets/Orders.ShoppingCart"]:active {
  background-position: 0 -144px;
}

#content a[href*="bmtmicro.com/servlets/Orders.ShoppingCart"] img {
  display: none;
}

table.contentpaneopen h4 {
  color: #efefef !important;
  font-size: 16pt;
  margin: 5px 20px !important;
}
    
.latestnews {
  margin: 0;
  padding: 0;
  list-style: none;
}

li.latestnews {
  margin: 0 0 7px;
  padding: 0 0 7px;
  border-bottom: 1px solid #777;
}

li.latestnews:last-child {
  border: 0;
}
}
