css:
.navbar.navbar-fixed-top .navbar-inner{
background-color: #1b1b1b;
background-image: -moz-linear-gradient(top, #222222, #111111);
background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#222222), to(#111111));
background-image: -webkit-linear-gradient(top, #222222, #111111);
background-image: -o-linear-gradient(top, #222222, #111111);
background-image: linear-gradient(to bottom, #222222, #111111);
background-repeat: repeat-x;
border-color: #252525;
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ff222222', endColorstr='#ff111111', GradientType=0);
}

#bodyContainer .span12{ 
width: 89%;
margin-left: 160px;
}

#feedsDropDownList {
position: absolute;
display: block;
left: -30px;
top: 40px;
width: 200px;
height: 680px;
overflow-x: hidden;
overflow-y: scroll;
border-radius: 0px;
}

.multiplxLogo {
	visibility:hidden
}

.feedEntry {
	padding-left: 0px!important;
	padding-right: 0px!important;
}

.feedEntry p {
	padding: 9px; 
}

.feedEntry h3 {
	margin: 0px
}

.pod-footer {
	margin: 0px 0px -20px -5px;
}
.navbar .nav > li > a {
	color: #F3F3F3;
	text-shadow: 0 -1px 0 #000;
}
.navbar .nav > li > a:hover {
	color:#FFF;
}
.well {
	padding-top: 0px; 
}

.modal-body img {
	min-width: 100%;
	max-width: 100%;
	width: 100%;
}

#entryListContainer h3 a {
	padding: 9px;
	display:block;
	color: #FFFFFF;
	text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
	background-color: #C9C9C9;
	*background-color: #C9C9C9;
	background-image: -moz-linear-gradient(top, #D1D1D1, #C9C9C9);
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#D1D1D1), to(#C9C9C9));
	background-image: -webkit-linear-gradient(top, #D1D1D1, #C9C9C9);
	background-image: -o-linear-gradient(top, #D1D1D1, #C9C9C9);
	background-image: linear-gradient(to bottom, #D1D1D1, #C9C9C9);
	background-repeat: repeat-x;
	border-color: #C9C9C9;
	border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
	filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#D1D1D1', endColorstr='#C9C9C9', GradientType=0);
	filter: progid:DXImageTransform.Microsoft.gradient(enabled=false);
}
#entryListContainer h3 a:hover,
#entryListContainer h3 a:active,
#entryListContainer h3 a.active,
#entryListContainer h3 a.disabled,
#entryListContainer h3 a[disabled] {
	color: #FFFFFF;
	background-color: #C9C9C9;
	*background-color: #003bb3;
	text-decoration:none;
}
