css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mcft.net") {
#Head {
    background: none repeat scroll 0% 0% #505050 !important;
    padding: 0px;
}
body {
    color: rgb(0, 0, 0);
    background: none repeat scroll 0% 0% rgb(50, 50, 50) !important;
        background-color: rgb(50, 50, 50) !important;
}
.Box {
    background: none repeat scroll 0% 0% #6d6d6d !important;
    color: #cacaca !important;
    padding: 6px 10px;
    margin: 0px 0px 10px;
    box-shadow: 0px 0px 1px #fff !important;
}
.Box h4 {
    font-size: 110%;
    color: #cacaca !important;
    font-weight: bold;
    margin: 0px;
    padding: 0px 0px 1px;
}
h1, h2, h3, h4, h5, h6 {
    color: #cacaca !important;
    font-family: "Helvetica Neue",Helvetica,arial,sans-serif;
    font-weight: normal;
    margin: 0px;
}
.PanelInfo li {
    background: none repeat scroll 0% 0% #5d5d5d !important;
    border-bottom: 1px solid #555 !important;
}
.PanelInfo li:hover {
    background: none repeat scroll 0% 0% #606060 !important;
}
.PanelInfo {
    border-top: 1px solid #555 !important;
    border-bottom: 0;
}
.DismissMessage.Box {
    border: 1px solid #555 !important;
}
a {
    color: #cacaca !important;
}
a:hover {
    color: #babafa !important;
}
.DataList .MetaBox {
    background-color: #5d5d5d !important;
    border-bottom: 4px solid #555 !important;
    color: #cacaca !important;
}
.DataList .Meta {
    color: #ddd !important;
}
.DataList .Item {
    background-color: #6d6d6d !important;
}

.TabLink, .TabLink:link {
    border: 2px solid #555 !important;
    background: none repeat scroll 0% 0% #5d5d5d !important;
}
.TabLink:hover {
    background: none repeat scroll 0% 0% #666 !important;
}
.TabLink:active {
    background: none repeat scroll 0% 0% #665 !important;
}
.DataList .Meta a.Category {
    border: 1px solid #555 !important;
    background: none repeat scroll 0% 0% #5d5d5d !important;
    padding: 2px 4px;
    border-radius: 2px 2px 2px 2px;
}
a.BigButton:focus {
    background: none repeat scroll 0% 0% rgb(238, 238, 238);
    box-shadow: 0px 0px 2px rgb(153, 153, 153);
}
a.BigButton {
    font-weight: bold;
    background: -moz-radial-gradient(center top , #aaa, #5d5d5d) repeat scroll transparent !important;
    color: #555 !important;
    text-shadow: 0px 1px 0px #666 !important;
    border: 1px solid #555 !important;
    box-shadow: 0px 0px 2px rgb(153, 153, 153);
}
.DataList .Meta strong {
    border-radius: 2px 2px 2px 2px;
    background: -moz-linear-gradient(center top , #efef5d, #dddd5d) repeat scroll 0% 0% transparent !important;
    color: rgb(0, 0, 0) !important;
    border: 1px solid #fdfd5d
}
.DataList .Meta .Closed {
    background: none repeat scroll 0% 0% #655 !important;
    color: inherit !important;
    border-radius: 3px 3px 3px 3px !important;
    border: 1px solid #544 !important;
}
a.Bookmark, a.Bookmarked, a.Bookmarking {
    display: inline-block;
    background: none no-repeat scroll center top transparent !important;
    height: 22px;
    width: 22px;
}
a.Bookmark:after {
    content: "★";
    font-size: 22px;
    color: #efefef;
}
a.Bookmarked:after {
    content: "★";
    font-size: 22px;
    color: #efef5d;
}
a.Bookmarking:after {
    content: "★";
    font-size: 22px;
    color: #d0d0ff;
}
.Comment .Meta .no-stile {
    background: linear-gradient(to right, rgba(109, 109, 109, 0.87) 0%, rgba(109, 109, 109, 0.87) 55%, rgba(109, 109, 109, 0) 100%) repeat scroll 0% 0% transparent !important;
    height: 40px;
}
.MessageForm, .Tabs, .Headings {
    display: block;
    border-bottom: 4px solid #555 !important;
    padding: 5px 8px;
    margin: 0px;
    background: none repeat scroll 0% 0% #5d5d5d !important;
    position: relative;
}
.Kudos a{
    color: #b0b0b0 !important;
    text-shadow: 1px 1px 1px black;
}
.MessageList .Meta .Author a {
    color: #b0b0b0 !important;
    text-shadow: 1px 1px 1px black;
}
a.Permalink {
    color: #b0b0b0 !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 1px black;
}
.CommentQuote a {
    color: #b0b0b0 !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 1px black;
}
a.FlagContent.Popup {
    color: #b0b0b0 !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 1px black;
}
.Kudos a:hover{
    color: #ccc !important;
    text-shadow: 1px 1px 1px black;
}
.MessageList .Meta .Author a:hover {
    color: #ccc !important;
    text-shadow: 1px 1px 1px black;
}
a.Permalink:hover {
    color: #ccc !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 1px black;
}
.CommentQuote a:hover {
    color: #ccc !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 1px black;
}
a.FlagContent.Popup:hover {
    color: #ccc !important;
    font-weight: bold !important;
    text-shadow: 1px 1px 1px black;
}
.Kudos .give_kudos:hover{
    color: #FFD2CB !important;
    -webkit-animation-name: heart_pulse;
    -webkit-animation-duration: 2s;
    -webkit-animation-iteration-count: infinite;
}
.Kudos .loved{
    color: #E86786;
    font-weight: bold;
    padding: 0 3px 0 0 !important;
    text-shadow: 1px 1px 1px black;
}
.Kudos .kudos_heart{
    color: #F4A1B5;
    font-weight: bold;
    padding: 0 !important;
    text-shadow: 1px 1px 1px black;
}
.Kudos .kudos_more{
    color: #FF3E61;
    text-shadow: 1px 1px 1px black;
}
.Kudos .kudos_more sup{
    font-size:xx-small; 
    vertical-align:super;
    text-shadow: 1px 1px 1px black;
}
div.Popup h1 {
    background: none repeat scroll 0% 0% #5d5d5d !important;
    border-bottom: 4px solid #555 !important;
}
#DiscussionForm form {
    border-radius: 2px 2px 2px 2px;
    background: none repeat scroll 0px 0px #5d5d5d !important;
    border: 1px solid #555 !important;
    margin: 0px;
    padding: 20px;
    color: #5d5d5d !important;
}
#DiscussionForm label {
    font-size: 14px;
    font-weight: bold;
    color: #cacaca !important;
}
body.Profile .PanelInfo {
    border-bottom: 1px solid #555 !important;
}
div.Popup .Body .Content {
    background: none repeat scroll 0px 0px #838383 !important;
}
.Popup .Preview {
    background: none repeat scroll 0px 0px #838383 !important;
}
#Menu a span, #Menu li.Active a span, #Menu li.Highlight a:hover span {
    background: none repeat scroll 0% 0% #f0f0f0 !important;
    color: #505050 !important;
}
.Tabs li a span {
    background: none repeat scroll 0% 0% #000 !important;
    color: #f0f0f0 !important;
}
}
