css:
@-moz-document domain("soundcloud.com") {
div.streamSidebar.sc-clearfix.sc-browsers-enable-gpu {
    display: none !important;
}

div.l-sidebar-right {
    display: none !important;
}

div.l-main.sc-border-light-right {
    margin-right: auto !important;
}

time.relativeTime {
    color: rgba(255,102,0,1) !important;
    font-family: "Lucida Grande", Verdana, sans-serif !important;
    font-weight: bold !important;
    font-size: 10px !important;
}

a.profileMenu__premium, a.header__link {
    display: none !important;
}

a.profileMenu__friends {
    display: none !important;
}

a.profileMenu__groups  {
    display: none !important;
}

a.header__mainMenu-explore  {
    display: none !important;
}

a.header__goPro{
    display: none !important;
}

div.sound__trackPreview {
    background-color: rgba(255,102,0,1) !important;
    box-shadow: inset 0 0 10px rgba(0,0,0,0.1), 0 0 5px rgba(0,0,0,0.4)!important;
    border-radius: 3px! important;
    margin-top: 6px !important;
    height: 20px !important;
}

a.trackPreviewItem__titleLink.sc-truncate {
    color: #FFF !important;
    font-size: 11px !important;
}

div.trackPreview__pre.sc-type-light {
    color: #FFF !important;
    margin-left: 4px !important;
}

a.trackPreviewItem__userLink.sc-link-dark {
    color: #000 !important;
    font-size: 11px !important;
    font-weight: bold;
}

h3.spotlight__header.sc-type-light.sc-border-dark-bottom {
    font-family: "Lucida Grande", Verdana, sans-serif !important;
    font-weight: bold !important;
    color: #000000 !important;
}
    
h3.spotlight__header.sc-type-light.sc-border-light-bottom {
    border-color: rgba(255,102,0,1) !important;
    font-weight: bold !important;
    color: #000000 !important;
}
    
div.spotlightTitle.sc-text-light.sc-font-light.sc-border-light-bottom {
    border-color: rgba(255,102,0,1) !important;
    font-weight: bold !important;
    color: #000000 !important;
}
    
div.spotlightTitle.sc-text-light.sc-font-light.sc-border-light-bottom h3 {
    border-color: rgba(255,102,0,1) !important;
    font-weight: bold !important;
    color: #000000 !important;
}
    
    div.l-container.l-content {
    width: auto !important;
}

li.spotlight__item.soundList__item.g-all-transitions-300 {
       background-color: #ffffff !important;
}

div.sound__soundActions, html, .stream, .l-three-column, .sc-border-light-right, .l-sidebar-left, .userInfo__scrollable.g-scrollable.g-scrollable-v,.l-container.l-content {
       background-color: #ffffff !important;
}
    
