css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("huaban.com") {
body{background:#000 !important;}
#ctx_bar:after,#BoardTitle h1{border-bottom:none !important;}
#ctx_bar{border-bottom:none !important;}
.pin, .Board{background-color:#222 !important;}
.attribution{background-color:#111 !important;}
.pin .description{background-color:#111 !important;}
.attribution{background-color:#111 !important;}
#header{background:#000 !important;border-bottom:1px solid #333 !important;box-shadow:none !important;}
#Profile .profile-stats{background:#000 !important;border-top:1px solid #000 !important;border-bottom:1px solid #000 !important;}
#Profile .profile-stats a.middle{border-left:1px solid #000 !important;border-right:1px solid #000 !important;}
.btn.Indicator span{background:#000 !important;color:#555 !important;}
a#NewIndicator strong,#ctx_bar .bar-links a, #ctx_bar .bar-links span,#ProfileSidebar h1,#ProfileSidebar,#ProfileSidebar .stats a,.activity li a,#BoardBoards a, .PinRepinStory a.stats{color:#ccc !important; text-shadow: none !important;}
.attribution{border-top:1px solid #000 !important;}
#main_menu .active{background: #111 !important;}
#Profile .profile-acts{background:#000 !important;border:0 none !important;}
.profile-acts.convo a.btn.wbtn,#huaban,#pin_stats .share,.pin-caption{display:none !important;}
#elevator .btn.Indicator span{background:#555 !important;}
#Profile .profile-basic{background:#111 !important;}
#Profile .profile-basic a.userlink{color:#888 !important;}
.pin-board-switcher li a{background:#333 !important;color:#999 !important;text-shadow:none !important;}
.pin-board-switcher li{border-right:none !important;}
#main_menu .active{border-color:#555 !important;}
#main_menu a span{color:#666 !important;}
#main_menu .border,#PinPinner,#PinAddComment{display:none !important;}
#search input,#search a, #search input[type="submit"]{border-color:#444 !important;box-shadow:none !important;}
#shadow_nav{background:#333 !important;box-shadow:none !important;}
#shadow_nav .tent{background:#333 !important;}
#search input{background:#333 !important;color:#888 !important;}
.huaban-shadow-nav .border,#BoardBoards a .stats{border:none  !important;}
.huaban-shadow-nav .unit,.attribution,.pin .commodity{border:none !important;}
.huaban-shadow-nav .line{border-top:1px solid #333 !important;}
.huaban-shadow-nav .large-btn{color:#999 !important;background:#333  !important;border:1px solid #212121 !important;}
.huaban-shadow-nav .active{color:#fff !important;}
#ctx_bar .p{color:#eee !important;text-shadow:none !important;}
#Profile .profile-stats a:hover{background:#333 !important;color:#eee !important;}
#Profile .profile-stats a:hover strong{color:#eee !important;}
#Profile .profile-basic a.settings{background-color:#333 !important;}
#Profile .profile-stats a strong{color:#666 !important;}
.pin-board-switcher li.selected a.pin-link,#main_menu .active span{color:#fff !important;}
.pin-board-switcher li a:hover{color:#eee !important;}
.huaban-shadow-nav .unit ul li a,#main_menu a span:hover{color:#666 !important;}
.huaban-shadow-nav .unit ul li a:hover,.huaban-shadow-nav .large-btn:hover{color:#fff !important;}
#shadow_nav .tent{box-shadow:none !important;}
.huaban-shadow-nav .unit ul li a.onthis{background:#555 !important;;color:#fff !important;}
.search-item input{background:#555 !important;color:#eee !important;box-shadow:none !important;border:1px solid #555 !important;}
#elevator span,#pin_view_arrows .next{background:#444 !important;}
#elevator strong{color:#999 !important;text-shadow:none !important;}
.wt{background:#222 !important;}
#pin_sources,#related_recommend .recommend-pin .description,#related_recommend .recommend-board{background:#222 !important;border:none !important;}
#PinPinner #PinnerName a,#pin_source a,#pin_source,#pin_sources a{color:#888 !important;}
.less,#pin_stats .stats .count:last-child{color:#555 !important;}
#pin_sources .source{border-bottom:1px solid #000 !important;}
.CloseupLeft{right:0 !important;}
.CloseupRight{margin:15px 320px 0 0 !important;}
#pin_view_footer,.activity{border:none !important;}
#pin_sources .closed:hover{background:#333 !important;}
#pin_view_arrows .prev,#pin_view_arrows .next,#ctx_bar .goodies-tip{display:none !important;}
.stats span,#pin_stats .stats .count:last-child{color:#999 !important;}
a.img,#ProfileSidebar{background:#222 !important;}
#category_query .subqueries a,#BoardMeta .board-category,.wbtn span, button.wbtn, input.wbtn[type="button"], input.wbtn[type="submit"], input.wbtn[type="reset"],#BoardBoards a, .PinRepinStory a.stats{background:#333 !important;color:#888 !important;box-shadow:none !important;border:1px solid #444 !important;}
.top-left-prom img,.pin .description,.attribution p{display:none !important;}
.top-left-prom{margin: 0 !important;}
.Board .FollowBoard .btn,.btn strong,h1{box-shadow:none !important;color:#888 !important;text-shadow:none !important;}
.CloseupLeft{right:0 !important;left:auto !important;}
.PinActivity,#pin_comments{border-top:1px solid #333 !important;}
.btn{box-shadow:none !important;}
}
