style_setting_options.value:
/*MAKE ALL LATEST THUMBNAILS SMALL*/
.img-border{
width:300px!important;
display: block!important;
margin: 0 auto!important;
}

/*MAKE STORY IMAGE SMALLER*/
.cursor-crosshair{
width:640px!important;
display: block!important;
margin: 0 auto!important;
}

.img-border:after{
border:none!important
}

.avatar {
width:132px!important
}

.avatar-mini{
display: block!important;
width:20px!important;
height:20px!important}

/*UN-GREY UNAPPROVED COMMENTS*/
.replybranch-ng .reply.unapproved{
background:#FFFFFF!important;

}
.reply.unapproved{
background:none!important;
}
.first-text,.replybranch-ng .reply-content p,p{
color:#000000!important;
}
.reply-author {
 color:#d44237!important
 }
.grayscale, .unapproved-container .avatar-mini{
filter:none!important
}
.replybranch.expanded .reply.unapproved {
  margin-top: 0px!important;
  padding-top: 0px!important;
  border-color: #EEEEEE!important;
  margin-left: -18px!important;
  margin-right: -18px!important;
  padding-left: 18px!important;
  padding-right: 18px!important;
}
