width: 26px !important;
-moz-opacity: 0.9 !important;
}

.tabbrowser-tabs #tabs-newbutton-start:hover, 
.tabbrowser-tabs #tabs-newbutton-end:hover, 
.tabbrowser-tabs #tabs-newbutton-left:hover {
-moz-opacity: 1 !important;
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-topright: 4px !important;
}

/* Transform the toolbar separator */

toolbarseparator {
-moz-appearance: none !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
width: 1px !important;
border-left: dotted 1px #aaaaaa !important;
background: transparent !important;
margin: 0px 0px -2px 2px !important;
}

/* Transform the UrlBar & SearchBar Textboxes */

.searchbar-engine-button:hover, 
.autocomplete-history-dropmarker:hover {
border-color: rgba(64,140,200,1) !important;
}

.searchbar-textbox, 
#urlbar {
-moz-appearance: none !important;
-moz-border-radius: 4px !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
