/*------------------------------------------------------------------ 
 MBlue Simple-Gray Theme Ext-Support (Ver. 0.3.5)
--------------------------------------------------------------------
Author: M.BLUE -- Tested on: Firefox 3.0 -- Last Update: 27.07.2008
--------------------------------------------------------------------
Description:
 Give Extensions support for the Simple-Gray Theme.
--------------------------------------------------------------------
What it does:
 - makes "GlaZoom" buttons looks better
 - makes "Download-Statusbar" looks better
 - skins the "Hyperwords" and "ABP" Textbox
 - makes the "YASS" icon rounded and transparent
 - makes the default loading-bars and "Fission" bar blue
--------------------------------------------------------------------
Supported Extensions:
 GlaZoom, Download-Statusbar, ADBlockPlus, HyperWords, Fission,
 YetAnotherSmoothScrolling (YASS).
--------------------------------------------------------------------
Notes:
 If you want to skin your firefox you also need the Simple-Gray
 Theme.
--------------------------------------------------------------------
Integrates with:
 MBlue Simple-Gray Theme
>MBlue Simple-Gray Theme Ext-Support
 MBlue Simple-Gray Sidebar
 MBlue Simple-Gray Sidebar Ext-Support
 MBlue Simple-Gray Slim-Download
 MBlue Simple-Gray Slim-Extension
 MBlue Simple-Gray Buttons
 MBlue Simple-Gray Top-Toolbar
 MBlue Simple-Gray Popup
 MBlue Simple-Gray Statusbar Icon
 MBlue Combine Nav-Buttons Vista
--------------------------------------------------------------------
Updates:
 27.07.2008 Ver. 0.3.5 = new version
 22.07.2008 Ver. 0.3.2 = some changes in Download-Statusbar
 18.07.2008 Ver. 0.3.1 = working on new version
 14.07.2008 Ver. 0.3.0 = GlaZoom redesigned,
                         GlaZoom scroll bug fixed
 13.07.2008 Ver. 0.2.9 = some changes for GlaZoom
 10.07.2008 Ver. 0.2.8 = few fixes
  7.07.2008 Ver. 0.2.5 = had to split the code into 3 parts!
------------------------------------------------------------------*/

/* Change the Download-Statusbar style */

#downbar {
-moz-appearance: none !important;
background: #dddddd 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAMAAAAMEmfoAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAD9QTFRF3Nzb5+fn2trazc3N19fX8fHx1NTU0tLS1tXWz8/P4uHh6enp0tHR5ubm4+Pj8PDw4ODg3d3d7+/v7OzszMzMDEXxSAAAAC5JREFUeNoMxEECQCAAALBRISpR/38rO8xUHDgthldy/w+Xqukeq2izy4L5CTAAGhMBDLV8+TkAAAAASUVORK5CYII=") 
repeat-x top !important;
background-position: 0px 1px !important;
}

#downbar .db_filenameLabel {
font-size: 10px !important;
font-weight: normal !important;
margin-left: 3px !important;
}

#downbar .db_progressIndicator {
font-size: 8px !important;
font-weight: normal !important;
margin-bottom: -1px !important;
margin-top: -1px !important;
color: #333333 !important;
}

#downbar stack {
min-height: 10px !important;
max-height: 40px !important;
height: 20px !important;
}

#downbar stack:hover {
opacity: 1 !important;
border-color: rgba(64,140,200,1) !important;
}

#downbar .db_finishedHbox {
-moz-border-radius: 4px !important;
border: solid 1px #999999 !important;
opacity: 1 !important;
}

#downbar .db_finishedIcon {
-moz-border-radius: 0px !important;
background: transparent !important;
border: none !important;
margin: 1px 0px 1px 0px !important;
opacity: 0.8 !important;
}

#downbar .db_pauseStack {
-moz-border-radius: 4px !important;
background: white !important;
border: solid 1px #ffaa22 !important;
opacity: 0.9 !important;
}

#downbar .db_notdoneHbox  {
-moz-border-radius: 4px !important;
background: white !important;
border: solid 1px #999999 !important;
opacity: 0.9 !important;
}

#downbar .db_progressStack {
-moz-border-radius: 4px !important;
background: white !important;
border: solid 1px #999999 !important;
opacity: 0.9 !important;
}

#downbar .db_progress-remainder {
-moz-border-radius-topleft: 0px !important;
-moz-border-radius-bottomleft: 0px !important;
-moz-border-radius-topright: 4px !important;
-moz-border-radius-bottomright: 4px !important;
border-left: solid 1px #eeeeee !important;
opacity: 1 !important;
}

#downbar .db_progress-bar {
-moz-border-radius-topleft: 4px !important;
-moz-border-radius-bottomleft: 4px !important;
-moz-border-radius-topright: 0px !important;
-moz-border-radius-bottomright: 0px !important;
border-right: solid 1px #cccccc !important;
opacity: 1 !important;
}

#downbarMainMenuButton {
border: none !important;
border-right: solid 1px #999999 !important;
margin: 0px 0px 0px 0px !important;
-moz-border-radius: 0px !important;
}

#downbarMainMenuText,
#downbarClearButtonText {
margin: 2px 4px 2px 2px !important;
font-size: 10px !important;
}

#downbarClearButton {
border: none !important;
border-left: solid 1px #999999 !important;
margin: 0px 0px 0px 0px !important;
-moz-border-radius: 0px !important;
}

#downbarMainMenuButton:hover,
#downbarClearButton:hover {
border-top: solid 1px #777777 !important;
border-bottom: solid 1px #777777 !important;
border-color: #777777 !important;
}

/* BG1 */

#downbar .db_progress-remainder, 
#downbarMainMenuButton, 
#downbarClearButton,
#diGlazoomEnlarge, 
#diGlazoomReduce {
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF6Ons5ufr6ent6+vu9vb36uvs7Ozv4+Tn7e3u5Obp6uvu4ePm5+jr6+zt2tzh3uDj7u7u5OXpZ2yYZQAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQJTdigvB/j2Gqfo0Sfda1hFgAAwIALtNlQlcAAAAAElFTkSuQmCC") repeat-x top !important;
}

/* BG2 */

#downbar .db_finishedHbox, 
#downbar .db_progress-bar,
#downbarMainMenuButton:hover, 
#downbarClearButton:hover,
#diGlazoomEnlarge:hover, 
#diGlazoomReduce:hover {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACdQTFRFzeb12+340uj23O34yuT01uv38Pf83u745fL64/H54PD51+v34fD5hLUNLQAAAClJREFUeNoMickNACAMgPBs1e4/r4QPATaHkkvQeLKYQNeGtfxJfgEGAAw5AJhVXkavAAAAAElFTkSuQmCC") 
repeat-x top !important;
opacity: 1 !important;
}

/* BG3 */

#downbar .db_notdoneHbox, 
#diGlazoomEnlarge:active:hover,
#diGlazoomReduce:active:hover {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF1NXa0dPYwMPJ0NHV6enq2dnaz9DV09PZ1dXa1djZxcfLzM/Ty8zQ1NXY2NjcycvPzM3T2tra83RnDwAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQDS8eATZ+jWXYqpu3SfZ9hFgAAzoALv0oeGDAAAAAElFTkSuQmCC") 
repeat-x top !important;
opacity: 1 !important;
}

/* GlaZoom */

#menulist-zoompanel {
-moz-appearance: none !important;
-moz-border-top-colors: none !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-bottom-colors: none !important;
background: #FFFFFF !important;
height: 22px !important;
width: 44px !important;
margin-top: 1px !important;
margin-bottom: 1px !important;
font-size: 90% !important;
padding-left: 0px !important;
border-left: none !important;
border-right: solid 1px #888888 !important;
border-top: solid 1px #888888 !important;
border-bottom: solid 1px #888888 !important;
opacity: 0.8 !important;
}

#menulist-zoompanel:hover {
opacity: 1 !important;
}

#menulist-zoompanel > .menulist-editable-box.textbox-input-box {
font-size: 10px !important;
margin: 2px 0px 0px 3px !important;
padding: 0px 0px 0px 0px !important;
}

#menulist-zoompanel > dropmarker {
margin-top: -2px !important;
margin-right: -1px !important;
}

#diGlazoomEnlarge,
#diGlazoomReduce {
-moz-appearance: none !important;
border-left: solid 1px #888888 !important;
border-right: solid 1px #888888 !important;
border-top: solid 1px #888888 !important;
border-bottom: solid 1px #888888 !important;
margin-top: 1px !important;
margin-bottom: 1px !important;
padding-left: 4px !important;
padding-right: 0px !important;
opacity: 0.8 !important;
}

#diGlazoomEnlarge {
list-style-image:
url("data:image/png;base64,") !important;
margin-left: -5px !important;
margin-right: 8px !important;
padding: 0px 0px 1px 4px !important;
-moz-border-radius-topright: 12px !important;
-moz-border-radius-bottomright: 12px !important;
}

#diGlazoomReduce {
list-style-image:
url("data:image/png;base64,") !important;
margin-left: 8px !important;
margin-right: -4px !important;
padding: 0px 0px 1px 4px !important;
-moz-border-radius-topleft: 12px !important;
-moz-border-radius-bottomleft: 12px !important;
}

#diGlazoomEnlarge:hover {
list-style-image:
url("data:image/png;base64,") !important;
border-color: #777777 !important;
}

#diGlazoomReduce:hover {
list-style-image:
url("data:image/png;base64,") !important;
border-color: #777777 !important;
}

/* HyperWords / ABP Textbox like others */

#HW2-TB-Textbar, #searchField {
-moz-appearance: none !important;
-moz-border-radius: 4px !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
background: transparent !important;
border: solid 1px #999999 !important;
margin-top: 2px !important;
margin-left: 2px !important;
margin-right: 2px !important;
margin-bottom: 2px !important;
min-height: 22px !important;
height: 22px !important;
width: 300px !important;
background-color: white !important;
}

#HW2-TB-Textbar:hover, #searchField:hover {
border-color: rgba(64,140,200,1) !important;
}

#abp-sidebar #list {
margin-bottom: -1px !important;
}

/* Loading-bar/Fission Blue */

#urlbar[fission="fusion"] > #statusbar-icon .progress-bar, .progress-bar {
-moz-appearance: none !important;
background:
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADxQTFRFr7Xejp7X3eDxAHDjk978AInuGrT1AF7YAFDQdYjSt73hu8HjADDAmqrZgZPUprHctLrgJ8D4CKLyAEPHSv8bDQAAACpJREFUeNoEwQkCgBAAALApVESO//+1zaEqBj5N0D1e2+1ySpZsir8AAwAOwAC/vOEMrAAAAABJRU5ErkJggg==") 
repeat-x center !important;
}

#urlbar[fission="fusion"] > #statusbar-icon .progress-bar {
opacity: 0.5 !important;
}

.progress-bar {
opacity: 1 !important;
}

.progress-remainder {
-moz-appearance: none !important;
background:
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF6Ons5ufr6ent6+vu9vb36uvs7Ozv4+Tn7e3u5Obp6uvu4ePm5+jr6+zt2tzh3uDj7u7u5OXpZ2yYZQAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQJTdigvB/j2Gqfo0Sfda1hFgAAwIALtNlQlcAAAAAElFTkSuQmCC") 
repeat-x center !important;
opacity: 1 !important;
}

/* "YASS" Statusbar Icon */

#yass_status{
margin-right: 2px !important;
margin-left: 2px !important;
}

#yass_status_color_indicator{
-moz-appearance: none !important;
-moz-border-radius: 3px !important;
border: solid 1px #000000 !important;
opacity: 0.6 !important;
}

#yass_status_color_indicator:hover{
border: solid 1px #000000 !important;
background: rgba(64,140,200,0.3) !important;
border-color: rgba(64,140,200,1) !important;
}
