css:
/* 

BTC-e.com Floating Orders List v1.0
author: goahead from BTC-e.com 

*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("btc-e.com") {


#orders-self-list {
    position: fixed;
    left: 0 !important;
    top: 0;
}

#orders-self-list table {
    background-color: white;
    border-collapse: collapse;
}

#orders-self-list table, #orders-self-list td {
    border: 1px solid gray;
}

#orders-self-list table tbody .table_h td {
    font-weight: bold;
    padding: 0 4px 0 0 !important;
}

#orders-self-list table tbody tr td {
    padding: 0 4px 0 2px !important;
}


}
