css:
/* Big Dark DeskSMS by tonytamps */
/* Re themed bigger version of wavedashdoc's Dark Desksms web sms site */
/* 2013 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("desksms.appspot.com"), url-prefix("desksms.appspot.com/#")

{

body {
  margin:0;
  font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
  font-size: 13px;
  font-weight: normal;
  line-height: 18px;
  color:#d4d4d4 !important;
  text-rendering: optimizeLegibility;
}

.container {
  width: 1000px;
}


.span10.columns.offset1 {
  width: 820px;
  margin-left: 210px;
}

.row .span7 {
  width: 780px;
}
    
#facebook-like {
  display: none;      
}

html,body {
  background-color: #1e1e1e !important;
}

form .uneditable-input {
  background-color: #222222 !important;
  display: block;
  border-color: #ccc;
  -webkit-box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.075);
  -moz-box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.075);
  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.075);
}


h1,h2,h3,h4,h5,h6 {
  font-size: 14px !important;
  font-weight: bold;
  color: #0393D8 !important;
}

h6 {
  font-size: 12px !important;
  color: #696969 !important;
  text-transform: uppercase;
}

h1 {
  margin-bottom: 18px;
  font-size: 30px;
  line-height: 36px;
}

h1 small,h2 small,h3 small,h4 small,h5 small,h6 small {
  color: #000000 !important;
}

.well {
  background-color: #313131 !important;
}
}
