css:
@-moz-document domain("theoldreader.com") {

all div a{
   line-height: 10px;
}
.subscribe .btn-primary{
   padding: 0px; 20px; 0px; 20px;
}
.subscribe .icon{
   top: 3px;
}
.sort-item{
   line-height:1;
}
.feed-list{
   padding-top: 0px;
   padding-bottom: 0px;
}
.nav-list{
   overflow: visible;
}
.nav-list li.nav-header{
   margin-top: 0px;
   padding-top: 0px;
}
.nav-list li.nav-header{
   margin-top: 0px;
   padding-top: 0px;
}
.nav-list li.nav-header a{
   margin-top: 0px;
   margin-bottom: 0px;
   padding-top: 0px;
   padding-bottom: 0px;
}
ul.nav.nav-list li.social{
   display: none;
}
.nav-list li.nav-header.active a:before{
   top: 0px;
   height: 17px;
}
div.slide > div.well.sidebar-nav{
   background: transparent;
   border: 0px;
   padding: 25px 0px 10px 0px
}
.nav-list li.nav-header li.nav-header.dropdown > a > span{
   line-height: 10px;
}
div.sort-item:hover{
   background: #F7F7F7;
}
.nav-list li.sort-item:hover{
   background: #E7E7E7;
}
.page-header{
   border: 0px;
   padding: 0px;
   margin: 0px;
}
.static > .floating{
   border: 0px;
   padding: 0px;
   margin: 0px;
}
.navbar .brand{
   margin-top: 0px;
   margin-bottom: 0px;
}
.navbar .form-search{
   margin-top: 0px;
   margin-bottom: 0px;
}
.navbar ul.nav.pull-right li a{
   margin-top: 0px;
   margin-bottom: 0px;
   padding-top: 0px;
   padding-bottom: 0px;
}
.navbar .nav li.divider-vertical{
   height: 15px;
}
.navbar .navbar-inner .container{
   height: 40px;
}
.navbar-inner{
   min-height: 0px;
   border: 0px;
}
.reader .container-fluid{
   top: 43px;
}
div.posts:first-child{
   margin:0px;
}
div.post{
   margin-bottom:3px;
   border-radius: 0;
}
.btn{
   line-height: 12px;
}
.post .content{
   margin:0px;
   padding:0px;
}
.content-body p{
   margin:0px;
   line-height: 14px;
}
.content-body a{
   height: 12px;
}
.content-body a img{
   height: 12px;
}
.post .label{
   margin-bottom: 0px;
   background-color: transparent;
}
.post .label{
   margin-bottom: 0px;
   background-color: transparent;
   color: black;
}
.post .label-transparent{
   margin-bottom: 0px;
}
.badge{
   background-color: transparent;
   color: black;
}
.nav-list .badge{
   background-color: transparent;
   color: black;
}
.row-fluid .span3{
   width:200px;
}
.reader .body-fixed-top{
   left: 20%;
   width: 80%;
   margin: 0px;
}
}
