css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jappy.de"), domain("jappy.at"), domain("jappy.tv") {


.mailConversation .profileImage img {position: relative; left: 25px !important;}

.mailConversation .profileImage img:hover {
    border-radius:5px!important;
    -moz-transform: scale(3.5) !important;z-index:99999 !important;
    -moz-transition-duration:0.3s;
    -webkit-transform: scale(3.5) !important;
    -webkit-transition-duration:0.3s;
    box-shadow:rgba(0,0,0,0.5) 1px 1px 1px !important;
    -webkit-box-shadow:rgba(0,0,0,0.5) 1px 1px 1px !important;
    -webkit-border-radius:5px !important; 
}
.listOfChatters .chatter .profileImage img{
border-radius: 5px !important;}

.listOfChatters .chatter .profileImage img:hover {
    -moz-transform: scale(3.0) !important;z-index:99999 !important;
    -moz-transition-duration:0.3s;
    -webkit-transform: scale(3.0) !important;
    -webkit-transition-duration:0.3s;
    box-shadow:rgba(0,0,0,0.5) 1px 1px 1px !important;
    -webkit-box-shadow:rgba(0,0,0,0.5) 1px 1px 1px !important;
    border-radius:3px !important;
    -webkit-border-radius:3px !important;
}

.listOfChatters .chatter {
position:relative;left:22px !important;
width:85% !important;
}

.mailConversation .entry .text{
text-align: right !important;
margin-left: 0 !important;
padding: 3px 5px 3px 5px!important;
background: #f3c5bd; /* Old browsers */
background: -moz-linear-gradient(top,  rgba(222,240,247,1) 3%, rgba(41,184,229,1) 10%, rgba(188,224,238,1) 93%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,rgba(222,240,247,1)), color-stop(10%,rgba(41,184,229,1)), color-stop(93%,rgba(188,224,238,1))); /* Chrome,Safari4+ */
border-radius: 7px!important;
-webkit-box-shadow: 0px 0px 1px 1px #888!important;
box-shadow: 0px 1px 1px 0px #888!important;
float: right!important;
margin-top: 7px!important;
margin-bottom: 5px!important;
clear: both!important;
font-weight:bold;
text-shadow: #fff 1px 1px 0px;
line-height: 1.8;
color:#000!important;
overflow:hidden!important;}




.mailConversation .entry.foreign .text {
text-align: left !important;
background: rgb(231,231,231)!important;
background: -moz-linear-gradient(top,  rgb(231,231,231) 0%, rgb(254,254,254) 2%, rgb(230,230,230) 27%, rgb(222,222,222) 27%, rgb(221,221,221) 29%, rgb(221,221,221) 87%, rgb(229,229,229) 96%, rgb(235,235,235) 98%, rgb(189,189,189) 100%)!important;
background: -webkit-linear-gradient(top,  rgb(231,231,231) 0%,rgb(254,254,254) 2%,rgb(230,230,230) 27%,rgb(222,222,222) 27%,rgb(221,221,221) 29%,rgb(221,221,221) 87%,rgb(229,229,229) 96%,rgb(235,235,235) 98%,rgb(189,189,189) 100%)!important;
background: -o-linear-gradient(top,  rgb(231,231,231) 0%,rgb(254,254,254) 2%,rgb(230,230,230) 27%,rgb(222,222,222) 27%,rgb(221,221,221) 29%,rgb(221,221,221) 87%,rgb(229,229,229) 96%,rgb(235,235,235) 98%,rgb(189,189,189) 100%)!important;
background: linear-gradient(top,  rgb(231,231,231) 0%,rgb(254,254,254) 2%,rgb(230,230,230) 27%,rgb(222,222,222) 27%,rgb(221,221,221) 29%,rgb(221,221,221) 87%,rgb(229,229,229) 96%,rgb(235,235,235) 98%,rgb(189,189,189) 100%)!important;
float: left!important;
color:#000!important;
text-shadow: #fff 1px 1px 0px;
overflow:hidden!important;}



.frameMessage{
opacity: 0.95 !important;
background: #7d7e7d; /* Old browsers */
background: -moz-linear-gradient(top, #7d7e7d 0%, #0e0e0e 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#7d7e7d), color-stop(100%,#0e0e0e))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #7d7e7d 0%,#0e0e0e 100%)!important; /* Chrome10+,Safari5.1+ */
background: linear-gradient(to bottom, #7d7e7d 0%,#0e0e0e 100%)!important; /* W3C */
border:1px solid #000!important;
box-shadow: 0px 0px 6px 2px rgba(100,100,100,0.8)!important;
overflow:hidden!important;}

.frameEditor,.mailConversationStream,.messages{
background: none!important;border:1px #333!important;
}

.mailConversation{
-webkit-box-shadow: inset -13px 11px 24px  #383937;	/* webkit browser*/ 
box-shadow: inset -13px 11px 24px  #383937;
}

.windowHeader{
border-bottom:3px solid #008B8B;
position:relative;top:5px;}

.windowHeader .windowActions a.entry:hover{
border-bottom:3px solid #00ffff!important;
padding: 7px 2px 0 4px!important;
}

.messageTasks .editorActions{
border-top:0px solid #008B8B!important;
}

.messageTasks .editorActions a{
border-top:3px solid rgba(254,254,254,0)!important;
}

.messageTasks .editorActions a:hover{
border-top:3px solid #008B8B!important;
background: none!important;
}

.messageTasks .inputCompose{
    background: rgba(254,254,254,0.005)!important;
    border-color: rgba(254,254,254,0.0)rgba(254,254,254,0.0)  #008B8B rgba(254,254,254,0.0)!important;
    border-width :2px!important;
    height: 20px!important;
    border-radius: 77px!important;
    width: 360px;
    color:#fff!important;
}
.messageTasks .buttonSubmit {
background:none!important;
border:none!important;
color:#008B8B!important;}

.messageTasks .task{
    background: rgba(0,0,0,0.85)!important;
color:#fff!important;
text-align:center;
text-shadow: #222 1px 1px 0px;
-webkit-box-shadow: inset 2px 0px 3px  #000;	/* webkit browser*/ 
 box-shadow: inset 2px 0px 3px  #000;
}

.mailConversation .entry .pointerLeft,.mailConversation .entry .pointerRight{display:none;}

.mailConversationStream{overflow-y:hidden!important;}

.messageTasks .frameMessage .attachment,.messageTasks .smilies{
background: rgba(150,150,150,0.6)!important;
    border-color: #333!important;
    }
   
.messageTasks .frameMessage{
-webkit-box-shadow:  13px -11px 24px  #383937;	/* webkit browser*/ 
box-shadow: -13px 11px 24px  #383937;
   }
 
 .messageTasks .attachment .headline,
 .messageTasks .additionalOptions .option .text
 {color:#fff!important;}
   
.messageTasks .additionalOptions .option:hover{
   background:none!important;
border-bottom:3px solid #f1ad1a!important;   }

.messageTasks .additionalOptions .option{border-bottom:3px solid
rgba(150,150,150,0)!important;}
   
   
.icBold {
background:url("https://dl.dropboxusercontent.com/u/27347691/a_orange_chat/icBold.png")center no-repeat !important;
}
 .icItalic  {
background:url("https://dl.dropboxusercontent.com/u/27347691/a_orange_chat/icItalic.png")center no-repeat !important;
}  
 
 .icUnderline{
background:url("https://dl.dropboxusercontent.com/u/27347691/a_orange_chat/icUnderline.png")center no-repeat !important;
} 
    

.mailConversation .date{
background:none!important;
color:#008B8B!important;
} 
  
.mailConversation .entry .time    {
color:#008B8B!important;
} 


}
