css:
@-moz-document domain("classroom.phoenix.edu") {

/* Normalize Message Fonts */
#view_message_text span{
  font-size:16px !important;
  line-height: 19px !important;
  font-family: 'Calibri', arial, helvetica, clean, sans-serif !important;
}
#view_message_text {
  font-size:16px !important;
  line-height: 19px !important;
  font-family: 'Calibri', arial, helvetica, clean, sans-serif !important;
}
  

/* Uncomment to hide Google tools */
#petscontainer {
  display: none !important;
}

.pets-identity {
  padding-right: 0px  !important;
}



/* Main Forum Area */

.hd-bkg #hd #at-header {
  background-image: none !important; 
  font-size:11px !important;
}
  
.hd-bkg div#at-banner {
  height: 1px !important;
}
  
div#at-banner {
  	height: 0;
    overflow: visible !important;
}

.hd-bkg #hd div#at-nav2 a {
   color: white !important;
}

.at-datebox {
  margin-top: -50px !important;
  color: white;
  font-size:11px !important;
}

.openwindow-btn {
  display: none !important;
}

#messagelist {
  font-size:12px !important;
}  
  

.hd-bkg #hd .at-tabs ul#menu_nav_1 li a {
    line-height: 12px !important;
    padding: 0 8px !important;
}

.hd-bkg #hd .at-tabs ul#menu_nav_1 {
	margin: 2px 0 0 10px !important;
}
  
.hd-bkg #hd .at-tabs {
    height: 20px !important;
    margin-left: 0px !important;
}

div#at-topnav {
    padding: 0px 0px 0px 0 !important;
}

.yui-b {
  margin-top: 22px !important;
}

.yui-b > h1 {
  display: none;
}


  

ul.message_icontable {
  list-style-type:none !important;
  margin:0 !important; 
  padding:0 !important;
}

.message_icontable li {
  display:inline;
  padding-left: 5px;
}
  

ul.message_icontable #printMessage {
    display: none;
}  
ul.message_icontable #divWatchFlag {
    display: none;
}  
#removeMessageLink span:nth-child(2) {
    display: none;
}   
  


/* Uncomment to Remove footer and extra side bar */

div.at-panel:nth-child(3) {
    display: none;
}
div.ft-bkg {
    display: none;
}
  
  
}

@-moz-document url-prefix("https://classroom.phoenix.edu/afm212/secure/confirm.jspa"), url-prefix("https://classroom.phoenix.edu/afm212/secure/post!default.jspa") {
.hd-bkg {
    display: none;
}

.ft-bkg  {
    display: none;
} 
  
#doc3 {
   min-width: 768px !important;
}

#postForm {
    min-width: 768px !important;
}

}
