css:
@-moz-document url-prefix("http://www.tumblr.com/") {
body {
background-image: url("http://static.tumblr.com/44c79795d23f266100078bfd280d0349/c1ijy2l/79dmlvizk/tumblr_static_maka_soul_bg.png")!important;
background-position: bottom left !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important; 
background-color: /*[[mycolor]]*/ !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(27,0,0,0.1)
 }

#home_button {

    padding-right: 5px !important;
}

#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 38px !important;
    padding-top: 33px !important;
    background: url('http://static.tumblr.com/9a2255b27378839b0d20ea5e6e95b9ad/c1ijy2l/LMymlvi8i/tumblr_static_makasoul.png') !important;
}

#home_button .tab_notice {
    left: 36px !important;
    right: auto !important;
}
div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #fff;
opacity:0.8;
font-size: 0;
position: relative;
top: 10px;
vertical-align: top;
margin: 0 auto;
display: none;
}

}
