css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

body
{
background:white;
}

div.l-header .selection_nipple
{
border-bottom:12px solid rgb(204, 204, 204);
}

#logo a
{
background-image:url('http://i.imgur.com/0IAM7dF.png');
}

div.l-header .tab.iconic > a {
background-image:url('http://i.imgur.com/ufa3dKu.png');
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgb(204, 204, 204);
}

.post_avatar
{
background-color:rgb(170, 170, 170);
}

}
