css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://cogdev.net/blink") {
body, html {margin: 0 auto !important; padding: 0 !important; }

div.container h1:first-child a img:first-child { width: 200px !important; margin: 0 auto !important; }

.left_tickets li strong, .right_tickets li strong, .left_tickets li em, .right_tickets li em { display: none !important; }

table.blink { width: 400px !important; margin: 10px auto !important; }

td.blink_prize img { display: none !important; }

img.blink_avatar { display: none !important; }

.left_tickets li button, .right_tickets li button { margin: 0 !important; padding: 3px !important; font-size: 1em !important; }

.left_tickets li, .right_tickets li { display: none !important; }

.left_tickets li.free, .right_tickets li.free { display: list-item !important; margin: 2px 0 !important; }

div.container { width: 500px !important; margin: 0 auto !important; }

div#blink_systemmessage, div#blink_links, div#blink_gtcbuy { display: none !important; }

.left_tickets li, .right_tickets li { display: list-item !important; height: 0 !important; padding: 0 !important; border: 0 !important; }

.left_tickets li.free, .right_tickets li.free { height: 20px !important; margin: 6px !important; padding: 5px 10px !important; }

div#blinks { width: 400px !important; margin: 0 auto !important; padding: 0 !important; }

div.span-20 { float: inherit !important; clear: both !important; width: 400px !important; margin: 0 auto !important; padding: 0 !important; text-align: center !important; }

div.span-4, div#blink_bonks { margin: 0 auto !important; }

div#blink_login { width: 400px !important; margin-left: 50px !important; }

div#blink_logged_in { width: 250px !important; }

div#blink_cs { display: none !important; }

div#blink_menu { width: auto !important; margin-left: 50px !important; }

a#mnu_faq, a#mnu_achievements, a#mnu_deposit, a#mnu_stats, a#mnu_show_promos { display: none !important; }

div.span-3:first-child { margin-right: 90px !important; padding-right: 10px !important; }

div#winnings { display: none !important; }

div#past_blinks { display: none !important; }

h2.pretty { display: none !important; }

ul.completed_mini_blinks { display: none !important; }

table.complete { display: none !important; }

cite#footer { display: none !important; }

div#blinkbar { width: 490px !important; margin-left: 5px !important; }

ul#item_groups, ul#group_list { width: 130px !important; }

ul#item_groups li, ul#group_list li { text-overflow: ellipsis !important; font-size: 9px !important; overflow: none !important; }

ul#item_groups li img, ul#group_list li img { float: left; }

div.bar_deets { display: inline-block !important; width: 185px !important; }

div.bar_item, input#just_a_quickie_search { display: inline-block !important; width: 125px !important; }

div.deets_info { font-size: .7em !important; }

div.deets_info div.title { text-overflow: ellipsis !important; font-size: 1.4em !important; line-height: 1.4em !important; overflow: none !important; }

div.bar_deets img { float: left !important; width: 50px !important; }

div.bar_deets { padding: 5px !important; }

div.deets_info { margin-left: 55px !important; }

div.buy_buttons { width: auto !important; }

div.buy_buttons button#btn_quick_start { width: 80px !important; font-size: .8em !important; }

div.buy_buttons input.blink_btn { display: inline-block !important; margin: 3px 5px !important; padding: 6px 10px 6px 10px !important; }

div#blinkbar { max-height: 400px !important; }

.blink li, #featured_blink li { height: 0 !important; margin: -10px !important; padding: 0 !important; border: 0 !important; font-size: 0 !important; }

.blink li strong { display: none !important; }

#featured_blink li.free, li.free { height: 22px !important; margin: 0 !important; font-size: 1em !important; }

li.free { display: list-item !important; clear: both !important; height: auto !important; padding: 10px 20px !important; font-size: 1em !important; list-style-position: inside; }

li.free button { float: right !important; position: relative; top: -3px !important; } .left_tickets li, .right_tickets li {margin: -3px !important;} .blink li.free { margin: 6px 0px !important; border-top: 1px solid #444 !important; border-bottom: 1px solid #444 !important; background: TRANSPARENT !important; }

li.me { display: list-item !important; height: auto !important; padding: 10px 20px !important; }

#featured_blink div#promo_description { width: 400px !important; text-align: center !important; }

#featured_blink div.span-4 { float: none !important; margin: 0 auto !important; text-align: center !important; }

div#featured_scroll h2.pretty { display: block !important; width: 400px !important; text-align: center !important; font-size: 2em; }

div#featured_scroll div.span-10 { display: none !important; }

div#featured_scroll { display: block !important; float: none !important; clear: both !important; width: 400px !important; overflow: hidden !important; }

#featured_blink { width: 400px !important; margin: 0 auto !important; padding: 10px 0 !important; }

#featured_blink li { width: 376px !important; padding: 10px !important; }

}
