css:
/* AGENT_SHEET */
@-moz-document domain("ec2-54-242-172-93.compute-1.amazonaws.com"), domain("eternawiki.org") {
html { background: #011e46 fixed !important; }

 html > body, table:not(.mceLayout):not(.mceToolbar):not(.mceListBox):not(.mceSplitButton):not(.mceColorSplitMenu):not(#menu_wpTextbox1_wpTextbox1_formatselect_menu_tbl):not(#menu_wpTextbox1_wpTextbox1_fontselect_menu_tbl):not(#menu_wpTextbox1_wpTextbox1_fontsizeselect_menu_tbl), tbody, tr, td, h1, h2, h3, ul, ol, li, dl, dt, dd, p, blockquote, q, cite, fieldset, form, label, iframe, header, footer, nav, article, section, caption, center, abbr, sub, sup, font, span:not([class]):not([style]), code,
 #content tbody, #content tr, #content td,
 .noprint, .vectorTabs, .portal, .portal > .body,
 .reflist, .refbegin, .catlinks, .references,
 .editsection,  .option,  #siteSub,
 .toccolours, #template-documentation, .iezoomfix,  .infobox,
 .thumb,  .image.image > img[src^="//bits.wikimedia.org/"],
 #bodyContent .metadata img, #bodyContent .infobox img, #bodyContent .noprint img, #bodyContent .plainlinks img, .collapsible img,
 #searchButton, .suggestions-results, .suggestions-special,
 #column-content, #simpleSearch > input,
 .divTop10item,  body.mobile div,
 .ui-dialog div,  .mw-geshi > div > pre,
 div[id="content"] div:not([rel]):not([style])
{ background: none !important; }

div:not(.mceColorSplitMenu), tt, a:not([style]):not(.mceText), span[class]:not(.mceText):not(.mceIcon) { background-color: transparent !important; }
#mw-head, .vectorTabs:hover { background-color: #1d3158 !important; }

#content, #simpleSearch,  div[id="content"] table:not(.mceLayout):not(.mceToolbar):not(.mceListBox):not(.mceSplitButton):not(.mceColorSplitMenu), #content .infobox,  #content .options,  .referencetooltip > li,  .ui-dialog, .ui-widget-content,  #mw-mf-header, #mw-mf-drawer, #mw-mf-content-left { background: #011e46 !important; }

.mediaContainer .control-bar,
.portal:hover > .body > ul, .toc.toc, #toc.toc.toc:hover, #p-personal[id]:hover
{ background: #1d3158 !important; }
#mw-panel,  #footer
{ background: transparent !important; }

option, kbd, .keyboard-key,
 .ui-widget-overlay,
 .mw-search-formheader,
 .metadata.plainlinks,
 .tipsy, .tipsy-inner,
 #results, .mobile > #header,
 #content .flaggedrevs_short, .flaggedrevs_short_details,
 .suggestions,  #content .mw-geshi, pre { background: #011e46 !important; }

 div[style*="background"],
#content tr:nth-child(2n), tr:nth-child(2n) { background: rgba(20,20,20,0.2) !important; }
#content th, th,  .NavHead { background: rgba(120,120,120,0.1) !important; }

 #mw-content-text > p img, #mw-content-text > dl img,  img.tex { background: #eee !important; padding: 2px; border-radius: 3px; }
 .image > img[src^="//upload.wikimedia.org/"]:hover, #bodyContent .image > .thumbborder:hover,
 #mw-content-text > table > tbody > tr > td > .image > img,  .filehistory a img, #file img,
 img.thumbimage, .thumb .image > img { background: #eee !important; padding: 0; }
 .central-textlogo > img, #wm-home-header > img { background: #eee; padding: 5px; border-radius: 8px; }
 .articleFeedback-rating-label, .articleFeedback-rating-clear, .articleFeedback-switch { border-radius: 11px; }
 img[src$="/Bluebg_rounded.png"] { visibility: hidden; }

 .referencetooltip > li + li { opacity: 0 !important; }
.referencetooltip > li, #pagehistory > li.selected, #simpleSearch, a.button.active { border-color: inherit !important; }
 #pagehistory > li { border-color: transparent !important; }
 tr, td,  #content, #searchform,  abbr { border-color: #aaa !important; }
td[style*="border"]:empty { border-color: transparent !important; }
 .wikitable, th[style], td[style] { border-color: #777 !important; }
 .metadata { border-left-color: #8cf !important; }
 .ambox-notice { border-left-color: #27c !important; }
 .ambox-content { border-left-color: #c72 !important; }
 .ambox-style { border-left-color: #992 !important; }
 .mobile > #footer,  .navbox { box-shadow: none !important; }

#content .errorbox { background: #522 !important; }
#content .successbox { background: #151 !important; }
#content .warningbox, #content .usermessage { background: #551 !important; }
.suggestions-result-current, .suggestions-result:hover, #anoneditwarning, #anoneditwarningtest { background: #159 !important; }
.diff-addedline { border-color: #7f7 !important; } .diff-addedline .diffchange, .mw-plusminus-pos { color: #5a5 !important; }
.diff-deletedline { border-color: #f77 !important; } .diff-deletedline .diffchange, .mw-plusminus-neg { color: #fa9 !important; }

:not(span) { color: inherit !important; }
span { color: inherit; }
html, .tocnumber,  .central-featured em, .central-featured small { color: #fff !important; }
hr { height: 0 !important; border: 0 solid #999 !important; border-width: 1px 0 0 !important; }
a.mceText, a.mceMoreColors { color: #000 !important; text-shadow: none !important; }
a { color: #6ef !important; text-shadow: none !important; }
a:hover { color: #8ff !important; }
a.mceText:hover, a.mceMoreColors:hover { color: #000 !important; }
a:visited { color: #6de !important; }
a:visited:hover { color: #8ff !important; }
a.new, .error, .newpage { color: #f77 !important; } a.new:hover { color: #fa9 !important; }

::-moz-selection { background: #9cf !important; color: #000 !important; }
::selection { background: #9cf !important; color: #000 !important; }

input[type="submit"], input[type="button"], input[type="file"], input[type="reset"], button,
 .buttons span, a.button { color: #fff !important; border: 1px solid #777 !important; background: #172013 !important;
border-radius: 7px !important; box-shadow: none !important; text-shadow: none !important;
background-image: linear-gradient(rgba(255,255,255,0.2),rgba(125,125,125,0.2),rgba(0,0,0,0.3)) !important; }

input[type="submit"]:hover, input[type="button"]:hover, input[type="file"]:hover, input[type="reset"]:hover, button:hover, a.button:hover
{ border-color: #49c !important; background-color: #111 !important; color: #5c4 !important; box-shadow: 0 0 1em #8cf !important; }

button, input, select, textarea { -moz-appearance: none !important; }
input[type="radio"], input[type="checkbox"] { color: #8cf !important; background: none !important; border-color: #999 !important; }

input, select, textarea, input[type="text"] { background: #011e46 !important; color: #fff !important; padding: 2px !important; border: 1px solid #777 !important; box-shadow: none !important; }
input[type] { border-width: 1px !important; border-style: solid !important; }
textarea:hover, input:hover, select:hover, textarea:focus, input:focus, select:focus { border-color: #09f !important; }

select > button, #searchButton, #simpleSearch > input { background-color: transparent !important; border: none !important; box-shadow: none !important; }
html:root option:checked { background: #9cf !important; color: #000 !important; }


#preftoc > .selected > a, .portal > h5, .portal > h3 { border: 1px solid !important; border-radius: 5px; background: #1d3158 !important; }

.portal.portal:hover > .body > ul, #toc.toc.toc:hover, .vectorTabs[id]:hover, #p-personal[id]:hover, html > body > .suggestions[class] { opacity: 0.99 !important; }

html { background-image: linear-gradient(#1d3158, #011e46 300px) !important; }

ul { list-style-image: none !important; }

h1, h2 { border-bottom: 1px solid #FEC942 !important; }

div.vectorMenu ul { background-color: #011e46 !important; }
div.vectorTabs li { background-image: linear-gradient(#2b4984, #011e46 40px) !important; }
div.vectorTabs li.selected { background-image: linear-gradient(#3a62b0, #011e46 40px) !important; }
div#mw-panel { width: auto !important; }
#left-navigation { left: 15em !important; }
fieldset, #toc, .toc, .mw-warning { border-color: #439645 !important; }
.portlet h5 { text-transform: none !important; }
}
