/******************************************************************* 
 Customize grippy (it affects all the grippies)
*******************************************************************/

grippy {
-moz-appearance: none !important;
border-bottom: solid 0px !important;
border-top: solid 0px !important;
border-left: solid 0px !important;
border-right: solid 0px !important;
background-color: transparent !important;
}

grippy:hover {
background-color: rgba(215,235,255,0.5) !important;
}



/******************************************************************* 
 Customize All-in-One Sidebar
*******************************************************************/

#sidebar {
border: none !important;
}

#sidebars-titlebutton {
-moz-appearance: none !important;
-moz-border-radius-topleft: 0px !important;
-moz-border-radius-topright: 6px !important;
background-color: #dddddd !important;
border-right: solid 1px #999999 !important;
border-bottom: solid 1px #999999 !important;
border-left: solid 1px #999999 !important;
border-top: solid 1px #999999 !important;
width: 250px !important;
height: 28px !important;
min-height: 16px !important;
padding-top: 4px !important;
margin-top: 2px !important;
margin-bottom: 0px !important;
margin-left: -2px !important;
margin-right: 0px !important;
}

#sidebars-titlebutton:hover {
background-color: rgba(215,235,255,1) !important;
}

#sidebars-titlebutton > label {
color: #000000 !important;
font-weight: normal !important;
font-size: 80% !important;
margin-top: 1px !important;
margin-bottom: 1px !important;
margin-right: -1px !important;
margin-left: -1px !important;
}

sidebarheader {
min-height: 18px !important;
height: 28px !important;
border-bottom-width: 0px !important;
padding-top: 0px !important;
padding-bottom: 3px !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
border: none !important;
color: black !important;
}

#sidebarheader-spacer, 
.aiostbx-toolbox-plain {
border-bottom: solid 1px #999999 !important;
}

.aiostbx-toolbox-plain > toolbar {
border: none !important;
color: black !important;
height: 27px !important;
padding-top: 1px !important;
margin-top: 0px !important;
}

sidebarheader toolbarbutton:not([id="sidebar-close-button"]) {
-moz-appearance: none !important;
-moz-border-radius-topleft: 6px !important;
-moz-border-radius-topright: 6px !important;
height: 28px !important;
border-right: solid 1px #999999 !important;
border-bottom: solid 0px #999999 !important;
border-left: solid 1px #999999 !important;
border-top: solid 1px #999999 !important;
background: #dddddd 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF1NXa0dPYwMPJ0NHV6enq2dnaz9DV09PZ1dXa1djZxcfLzM/Ty8zQ1NXY2NjcycvPzM3T2tra83RnDwAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQDS8eATZ+jWXYqpu3SfZ9hFgAAzoALv0oeGDAAAAAElFTkSuQmCC") repeat-x top !important;
margin-left: -1px !important;
margin-top: 1px !important;
margin-bottom: 0px !important;
padding-left: 4px !important;
padding-right: 4px !important;
padding-bottom: 0px !important;
padding-top: 0px !important;
}

sidebarheader toolbarbutton:not([id="sidebar-close-button"]):hover,
toolbarbutton[id="viewButton"]:hover {
background: rgba(215,235,255,1) 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACdQTFRFzeb12+340uj23O34yuT01uv38Pf83u745fL64/H54PD51+v34fD5hLUNLQAAAClJREFUeNoMickNACAMgPBs1e4/r4QPATaHkkvQeLKYQNeGtfxJfgEGAAw5AJhVXkavAAAAAElFTkSuQmCC") repeat-x top !important;
border-color: #888888 !important;
}

sidebarheader toolbarbutton[checked="true"], 
sidebarheader toolbarbutton[checked="true"]:hover,
toolbarbutton[id="viewButton"] {
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF6Ons5ufr6ent6+vu9vb36uvs7Ozv4+Tn7e3u5Obp6uvu4ePm5+jr6+zt2tzh3uDj7u7u5OXpZ2yYZQAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQJTdigvB/j2Gqfo0Sfda1hFgAAwIALtNlQlcAAAAAElFTkSuQmCC") 
repeat-x top !important;
border-bottom: none !important;
border-left: solid 1px #999999 !important;
border-right: solid 1px #999999 !important;
border-top: solid 1px #999999 !important;
margin-bottom: -1px !important;
}

sidebarheader toolbarbutton:not([checked="true"]) .toolbarbutton-icon {
opacity: 0.8 !important;
}

sidebarheader toolbarbutton[checked="true"] .toolbarbutton-icon,
sidebarheader toolbarbutton:not([checked="true"]):hover .toolbarbutton-icon {
opacity: 1 !important;
}

#aiostbx-left-toolbox > toolbar {
-moz-appearance: none !important;
background-color: #eeeeee !important;
}

#bmHi-toolbar {
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAMAAAAMEmfoAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACpQTFRF6Ofo2tnZ7Ovs3+Dg5eXl19fW4uLiz8/P3Nzc0tHR6erp7e3t1NPU7u7uuRLVtgAAACVJREFUeNpswgcRACAQACCc7+xf1wIeuH62bKHqiiFpjimeAAMAGJUA3jOg9t0AAAAASUVORK5CYII=") repeat-x bottom !important;
border-top: none !important;
border-bottom: solid 1px #999999 !important;
margin-top: -1px !important;
min-height: 16px !important;
height: 27px !important;
}

toolbarbutton[id="viewButton"] {
-moz-appearance: none !important;
-moz-border-radius: 4px !important;
background-color: #eeeeee !important;
background-position: center !important;
padding-top: 2px !important;
padding-bottom: 1px !important;
margin: 1px 3px 1px 2px !important;
border: solid 1px #999999 !important;
}

textbox[id="search-box"] {
-moz-appearance: none !important;
-moz-border-radius: 4px !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
background-color: #ffffff !important;
border: solid 1px #999999 !important;
margin: 2px 1px 1px 2px !important;
height: 21px !important;
}

textbox[id="search-box"]:hover {
border-color: rgba(64,140,200,1) !important;
}

#bookmarks-view-children {
font-size: 10px !important;
font-weight: bold !important;
color: #444444 !important;
}

#historyTree {
font-size: 10px !important;
background: transparent 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAASCAIAAAAVNSPrAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAABVJREFUeNpi+P//PwOl+N27dwABBgCQszWYcyXFIgAAAABJRU5ErkJggg==") top !important;
}



/******************************************************************* 
 Remove All-in-One Sidebar Useless Menuitems
*******************************************************************/

menuitem[id="multipanel-mitem"],
menuitem[id="pageinfo-mitem"],
menuitem[id="source-mitem"],
menuitem[id="console-mitem"],
menuitem[id="dominspector-mitem"],
menuitem[label="Stylish"],
menuitem[id="aios-sidebar-mitem-prefs"],
#sidebars-titlebutton menuseparator:not([id=aios-sidebar-mitem-sep0]) {
display: none !important;
}
