css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.abc.net.au/radionational/") {

  div,p,td,strong { font: normal 11pt calibri,arial !important }
  body { background:none !important }
  strong { font-weight: bold !important; }
  
  /* programs page */
  
  div#qf-tabs ol.list {
    height:450px !important;
    border: 1px solid #ccc !important;
  }
  div.section.quickfind {
    margin:0 !important;
    padding:0 !important;
  }
  div.section.quickfind h3 {
    font:bold 13pt arial !important;
    color:#CB4721 !important;
    letter-spacing: 13px !important;
    margin: 0px 0 0 5px !important;
    line-height:30px !important;
  }
  
  /* Header / Nav Bar */
  
  div#header { 
    height:auto !important;
  }
  div#nav ul { 
    height:auto !important;
  }
  div#nav a {
    padding:5px 21px !important;
    height:auto !important;
  }

  /* Side Bar */

  div.aside.sidebar {
    border-left: 1px solid #ccc !important;
    margin-top: 10px !important;
    padding-top: 0px !important;
  }

  /* Program Intro */
  
  div.program-intro {
    padding:0 !important;
  }
  a.goto-more { 
    font: normal 11pt calibri,arial !important;
    color:blue !important;
    text-decoration: underline !important;
  }

  /* Main Episode List */

  DIV.article-index.section.ct-teaser-view.cs-tabbed {
    margin-bottom: 0px !important; 
  }

  div.ct-teaser-view div.figure { 
    width:auto !important; 
    padding:0 !important; 
    margin-right:0 !important; 
  }
  div.ct-teaser-detailed-hasimg {
    padding:0 0 0px 0 !important;
  }
  div.ct-teaser-detailed-hasimg .figure {
    margin:0 !important;
    text-align:left !important;
  }
  div.ct-teaser-detailed-hasimg .figure img {
    width:50% !important;
    margin-top:10px !important;
  }
  div.ct-teaser-view.cs-tabbed h3 {
    font-size:10pt !important;
    font:bold 13pt arial !important;
    margin: -20px 10px 5px 0 !important
  }
  div.ct-teaser-detailed-hasimg  div.header h3.title {
   font-size:13pt !important; 
   font-weight:bold !important;
   margin: 8px 0 5px 0 !important;
   padding: 0 !important;
  }
  div.ct-teaser-detailed-hasimg  div.header h3.title a {
   color: #1F497D !important;
  }
  div.ct-teaser-detailed-hasimg .summary, div.ct-teaser-detailed .header {
   width:500px !important; 
   float:left !important; 
   margin-left: -130px !important; 
  }
  div.ct-teaser-detailed-hasimg .summary p {
   margin-top: 5px !important; 
   width:auto !important; 
   line-height: 16px !important; 
   font-size: 10pt !important;
  }
  div.ct-teaser-detailed-hasimg  .summary p.goto {
   float:left !important;
   margin:0 !important;
   width:auto !important;
  }
  
  /* Audio Links */
  
  div.cs-teaser ul.cs-audio-links li {
   margin: 0 15px 0 0  !important;
   padding: 0 !important;
   width:auto !important;
  }
  div.cs-teaser div.header ul.cs-audio-links a {
    font-weight:normal !important;
  }
  div.article-index.section > ul.cs-audio-links li {
    top: -3px !important;
  }
  div.article-index.section > ul.cs-audio-links li a {
    font-weight:bold !important;
  }
  div.cs-teaser ul.cs-audio-links {
    overflow:visible !important;
    padding:0px !important;
    border: 0px !important;
    margin: 0px !important;
    height: auto !important;
  }
  div.cs-teaser ul.cs-audio-links li {
    overflow:visible !important;
    padding:0 20px 0 0px !important;
    border: 0px !important;
    margin: 0px !important;
    height: auto !important;
  }
  div.cs-teaser .ico-audio,.ico-download {
    background:none !important;
    line-height:100% !important;
    height:auto !important;
    padding:0 !important;
    margin: 0px !important;
  }
  div.cs-teaser a.ico {
    text-decoration: underline !important;
    font-weight: normal !important;
    color: #A24839 !important;
  }

  /* Mods for the simple list */
  div.ct-teaser-simple ul.cs-audio-links {
    width:100px !important;
  }
  div.ct-teaser-simple ul.cs-audio-links li {
    padding: 4px 0 2px 5px !important;
    font-size: 12px !important;
  }
  
  /* Single Program Page */

  DIV.section.rich-text p {
    width:600px !important;
  }
  DIV.cn-storyRelatedMedia {
    width:230px !important;
  }
  DIV.cn-storyRelatedMedia img {
    width: 100% !important;
    height: 100% !important;
    display:block !important;
  }
  DIV.section.rich-text {
    padding:0 !important;
  }

  
  /* Guide / Schedule */

  DIV.section.schedule td {
    height: 45px !important;
    border-bottom: 2px solid #E1E2CF !important;
  }
  DIV.section.schedule tr.even td {
    background: #fff !important;
  }
  
  
}
