/*------------------------------------------------------------------
 MBlue Simple-Gray Slim-Download (Ver. 0.3.5)
--------------------------------------------------------------------
Author: M.BLUE - Tested on: Firefox 3.0.1 - Last Update: 27.07.2008
--------------------------------------------------------------------
Description:
 Makes the Download-Window integrate better with the Simple-Gray
 Theme, and makes the download list slim.
--------------------------------------------------------------------
Supported Extensions:
 All-in-One Sidebar.
--------------------------------------------------------------------
Notes:
 You can use the "Simple-Gray Sidebar" style to open the download
 window inside the sidebar (need AIOS).
--------------------------------------------------------------------
Integrates with:
 MBlue Simple-Gray Theme
 MBlue Simple-Gray Theme Ext-Support
 MBlue Simple-Gray Sidebar
 MBlue Simple-Gray Sidebar Ext-Support
>MBlue Simple-Gray Slim-Download
 MBlue Simple-Gray Slim-Extension
 MBlue Simple-Gray Buttons
 MBlue Simple-Gray Top-Toolbar
 MBlue Simple-Gray Popup
 MBlue Simple-Gray Statusbar Icon
 MBlue Combine Nav-Buttons Vista
--------------------------------------------------------------------
--------------------------------------------------------------------
Updates:
 26.07.2008 Ver. 0.3.5 = new version
 18.07.2008 Ver. 0.3.1 = working on new version
 10.07.2008 Ver. 0.2.1 = small fixes
  8.07.2008 Ver. 0.2.0 = ready to release I guess
  6.07.2008 Ver. 0.1.8 = optimized for using with AIOS
  5.07.2008 Ver. 0.1.7 = small fixes
  2.07.2008 Ver. 0.1.6 = changed some colors
  1.07.2008 Ver. 0.1.5 = changed the clear-list button
 30.06.2008 Ver. 0.1.3 = changed the search box
 30.06.2008 Ver. 0.1.2 = optimized for the sidebar
 28.06.2008 Ver. 0.1.1 = changed backgrounds and font-size
 27.06.2008 Ver. 0.1.0 = first version
--------------------------------------------------------------------
------------------------------------------------------------------*/



/******************************************************************* 
 Customize the Download-Window
*******************************************************************/



#downloadManager {
-moz-appearance: none !important;
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAMAAAAMEmfoAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADlQTFRF7e7u5+fn1NPU6Ofo2tnZ6+vr7Ovs3+Dg5eXl19fW39/fiIiI5eTk0tHR7u7u4uLi3Nzc6erp7e3tTZ6PJwAAADJJREFUeNoMyVkOgCAUwMARlEVQ0fsf1pd+NE0Nl6TbLdWjyV7F5w6Ej+jNjHdavwADABZzAPyyVPv2AAAAAElFTkSuQmCC") center bottom !important;
border-top: solid 1px #999999 !important;
border-bottom: none !important;
}

richlistbox#downloadView {
-moz-appearance: none !important;
background: #eeeeee !important;
background-image: 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAMAAAAMEmfoAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACpQTFRF6Ofo2tnZ7Ovs3+Dg5eXl19fW4uLiz8/P3Nzc0tHR6erp7e3t1NPU7u7uuRLVtgAAACVJREFUeNpswgcRACAQACCc7+xf1wIeCNPRJEPRVSzZdn+eAAMAETsA3jGzuOYAAAAASUVORK5CYII=") !important;
background-position: center top !important;
background-repeat: repeat-x !important;
margin-top: -1px !important;
margin-bottom: 0px !important;
}



/******************************************************************* 
 Customize the Download Items
*******************************************************************/



richlistbox#downloadView label.name, 
richlistbox#downloadView .status { 
font-size: 80% !important;
font-weight: normal !important;
color: black !important;
max-width: 640px !important;
margin-left: 3px !important;
margin-right: 1px !important;
}

richlistbox#downloadView richlistitem {
-moz-appearance: none !important;
border: solid 1px #888888 !important;
-moz-border-radius: 6px !important;
margin-left: 2px !important;
margin-right: 2px !important;
margin-top: 2px !important;
margin-bottom: 2px !important;
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAYAAAA7zJfaAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAElJREFUeNqMyzEKgFAYAmBxevc/XkRTTbVFBJXa+ztByweiIokI4OMqzmJnL1ZKXhhr4mMPtDQWM21vdHLU+GZrLegPVPzDK8AAZ5EsDTfn5/gAAAAASUVORK5CYII=") 
repeat-x top !important;
color: black !important;
}

richlistbox#downloadView richlistitem:hover, 
richlistbox#downloadView richlistitem[current="true"]  {
background-color: #e2f2fb !important;
border: solid 1px #777777 !important;
outline: none !important;
}

richlistbox#downloadView richlistitem[state="4"] {
background-color: #ffdd77 !important;
border: solid 1px #ffaa22 !important;
opacity: 0.6 !important;
}

richlistbox#downloadView richlistitem[state="2"],
richlistbox#downloadView richlistitem[state="3"] {
background-color: #cccccc !important;
opacity: 0.6 !important;
}

richlistbox#downloadView richlistitem[state="2"]:hover,
richlistbox#downloadView richlistitem[state="3"]:hover,
richlistbox#downloadView richlistitem[state="4"]:hover,
richlistbox#downloadView richlistitem[current="true"] {
opacity: 1 !important;
}



/******************************************************************* 
 Customize the clear list button
*******************************************************************/



#clearListButton {
-moz-appearance: none !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
-moz-border-radius: 4px !important;
border: solid 1px #999999 !important;
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF6Ons5ufr6ent6+vu9vb36uvs7Ozv4+Tn7e3u5Obp6uvu4ePm5+jr6+zt2tzh3uDj7u7u5OXpZ2yYZQAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQJTdigvB/j2Gqfo0Sfda1hFgAAwIALtNlQlcAAAAAElFTkSuQmCC") 
repeat-x top !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
margin-left: 1px !important;
min-height: 19px !important;
height: 21px !important;
font-size: 10px !important;
color: #444444 !important;
}

#clearListButton:not([disabled="true"]):hover {
background: #e2f2fb 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACdQTFRFzeb12+340uj23O34yuT01uv38Pf83u745fL64/H54PD51+v34fD5hLUNLQAAAClJREFUeNoMickNACAMgPBs1e4/r4QPATaHkkvQeLKYQNeGtfxJfgEGAAw5AJhVXkavAAAAAElFTkSuQmCC") repeat-x top !important;
border-color: #777777 !important;
color: #333333 !important;
}

#clearListButton[disabled="true"] {
background: #dddddd 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF1NXa0dPYwMPJ0NHV6enq2dnaz9DV09PZ1dXa1djZxcfLzM/Ty8zQ1NXY2NjcycvPzM3T2tra83RnDwAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQDS8eATZ+jWXYqpu3SfZ9hFgAAzoALv0oeGDAAAAAElFTkSuQmCC") repeat-x top !important;
border-color: #999999 !important;
color: #888888 !important;
}



/******************************************************************* 
 Customize the Search Box
*******************************************************************/



textbox[id="searchbox"] {
-moz-appearance: none !important;
-moz-border-radius: 4px !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
background-color: #ffffff !important;
border: solid 1px #999999 !important;
margin-top: 2px !important;
margin-left: 3px !important;
margin-bottom: 1px !important;
height: 21px !important;
}

textbox[id="searchbox"]:hover {
border-color: rgba(64,140,200,1) !important;
}
