#extensionsBox {
margin: 0px 0px 0px -1px !important;
}


/******************************************************************* 
 Change font-size
*******************************************************************/


richlistbox#extensionsView .addonName { 
font-size: 80% !important;
font-weight: normal !important;
color: black !important;
max-width: 640px !important;
margin-left: 1px !important;
margin-right: 1px !important;
}



/******************************************************************* 
 Change background and margins
*******************************************************************/



richlistbox#extensionsView, 
#searchPanel {
-moz-appearance: none !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAMAAAAMEmfoAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACpQTFRF6Ofo2tnZ7Ovs3+Dg5eXl19fW4uLiz8/P3Nzc0tHR6erp7e3t1NPU7u7uuRLVtgAAACVJREFUeNpswgcRACAQACCc7+xf1wIeCNPRJEPRVSzZdn+eAAMAETsA3jGzuOYAAAAASUVORK5CYII=") repeat-x top !important;
margin-top: 0px !important;
margin-bottom: 0px !important;
margin-left: 0px !important;
margin-right: 0px !important;
border-top: solid 0px #999999 !important;
border-bottom: solid 0px #999999 !important;
border-left: solid 1px #999999 !important;
border-right: solid 1px #999999 !important;
padding: 0px 0px 0px 0px !important;
}



/******************************************************************* 
 Change items appearance
*******************************************************************/



richlistbox#extensionsView richlistitem {
-moz-appearance: none !important;
border: solid 1px #888888 !important;
-moz-border-radius: 6px !important;
margin-left: 2px !important;
margin-right: 2px !important;
margin-top: 2px !important;
margin-bottom: 2px !important;
height: 24px !important;
min-width: 32px !important;
padding-top: 4px !important;
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAYAAAA7zJfaAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAElJREFUeNqMyzEKgFAYAmBxevc/XkRTTbVFBJXa+ztByweiIokI4OMqzmJnL1ZKXhhr4mMPtDQWM21vdHLU+GZrLegPVPzDK8AAZ5EsDTfn5/gAAAAASUVORK5CYII=") 
repeat-x top !important;
color: black !important;
min-width: 24px !important;
height: auto !important;
}

richlistbox#extensionsView richlistitem:hover, 
richlistbox#extensionsView richlistitem[current="true"] {
background-color: #e2f2fb !important;
border: solid 1px #888888 !important;
opacity: 1 !important;
}

richlistbox#extensionsView richlistitem[current="true"] {
background-color: #e2f2fb !important;
height: auto !important;
}

richlistbox#extensionsView richlistitem:not([updateable="true"]),
richlistbox#extensionsView richlistitem:not([compatible="true"]) {
background-color: #cccccc !important;
opacity: 0.6 !important;
}

richlistbox#extensionsView richlistitem:not([updateable="true"]):hover,
richlistbox#extensionsView richlistitem:not([compatible="true"]):hover {
opacity: 1 !important;
}

richlistbox#extensionsView richlistitem[selected="true"]:not([updateable="true"]) {
background-color: rgba(255,128,0,0.2) !important;
height: auto !important;
}

richlistbox#extensionsView richlistitem:not([updateable="true"]) {
height: 42px !important;
}

richlistbox#extensionsView richlistitem label {
color: #000000 !important;
margin: 1px 0px 1px 1px !important;
font-weight: normal !important;
font-size: 80% !important;
}

richlistbox#extensionsView richlistitem .addonVersion {
margin-right: 5px !important;
}

richlistbox#extensionsView richlistitem .selectedStatusMsgs label,
richlistbox#extensionsView richlistitem .addon-description label {
width: 192px !important;
margin-left: 0px !important;
}

richlistbox#extensionsView richlistitem .addonTextBox {
min-width: 32px !important;
width: auto !important;
max-height: 1000px !important;
}

richlistbox#extensionsView richlistitem spacer {
display: none !important;
}



