-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
-moz-border-radius: 4px !important;
border: solid 1px #999999 !important;
background: #eeeeee 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF6Ons5ufr6ent6+vu9vb36uvs7Ozv4+Tn7e3u5Obp6uvu4ePm5+jr6+zt2tzh3uDj7u7u5OXpZ2yYZQAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQJTdigvB/j2Gqfo0Sfda1hFgAAwIALtNlQlcAAAAAElFTkSuQmCC") 
repeat-x top !important;
min-width: 16px !important;
width: auto !important;
min-height: 12px !important;
margin: 1px 1px 1px 1px !important;
}

richlistbox#extensionsView richlistitem button[class="uninstallHide optionsButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="uninstallHide themeButton useThemeButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="disableShow enableHide uninstallHide enableButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="enableShow disableHide uninstallHide disableButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="uninstallHide uninstallButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="uninstallShow cancelUninstallButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="installShow cancelInstallButton"][disabled="true"] label,
richlistbox#extensionsView richlistitem button[class="upgradeShow cancelUpgradeButton"][disabled="true"] label {
color: #888888 !important;
}

richlistbox#extensionsView richlistitem button[class="uninstallHide optionsButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="uninstallHide themeButton useThemeButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="disableShow enableHide uninstallHide enableButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="enableShow disableHide uninstallHide disableButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="uninstallHide uninstallButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="uninstallShow cancelUninstallButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="installShow cancelInstallButton"][disabled="true"],
richlistbox#extensionsView richlistitem button[class="upgradeShow cancelUpgradeButton"][disabled="true"],
button[id="installFileButton"][disabled="true"],
button[id="installUpdatesAllButton"][disabled="true"],
button[id="showUpdateInfoButton"][disabled="true"],
button[id="hideUpdateInfoButton"][disabled="true"],
button[id="checkUpdatesAllButton"][disabled="true"],
button[id="continueDialogButton"][disabled="true"],
button[id="skipDialogButton"][disabled="true"],
button[id="aios-toolsButton"][disabled="true"] {
background: #dddddd 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADZQTFRF1NXa0dPYwMPJ0NHV6enq2dnaz9DV09PZ1dXa1djZxcfLzM/Ty8zQ1NXY2NjcycvPzM3T2tra83RnDwAAAClJREFUeNoMwokRgCAAACAstfJ3/2XtQDS8eATZ+jWXYqpu3SfZ9hFgAAzoALv0oeGDAAAAAElFTkSuQmCC") repeat-x top !important;
}

richlistbox#extensionsView richlistitem button[class="uninstallHide optionsButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="uninstallHide themeButton useThemeButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="disableShow enableHide uninstallHide enableButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="enableShow disableHide uninstallHide disableButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="uninstallHide uninstallButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="uninstallShow cancelUninstallButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="installShow cancelInstallButton"]:not([disabled="true"]):hover,
richlistbox#extensionsView richlistitem button[class="upgradeShow cancelUpgradeButton"]:not([disabled="true"]):hover,
button[id="installFileButton"]:not([disabled="true"]):hover,
button[id="installUpdatesAllButton"]:not([disabled="true"]):hover,
button[id="showUpdateInfoButton"]:not([disabled="true"]):hover,
button[id="hideUpdateInfoButton"]:not([disabled="true"]):hover,
button[id="checkUpdatesAllButton"]:not([disabled="true"]):hover,
button[id="continueDialogButton"]:not([disabled="true"]):hover,
button[id="skipDialogButton"]:not([disabled="true"]):hover,
button[id="aios-toolsButton"]:not([disabled="true"]):hover,
