background: #e2f2fb 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAUCAMAAAB70KeTAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAACdQTFRFzeb12+340uj23O34yuT01uv38Pf83u745fL64/H54PD51+v34fD5hLUNLQAAAClJREFUeNoMickNACAMgPBs1e4/r4QPATaHkkvQeLKYQNeGtfxJfgEGAAw5AJhVXkavAAAAAElFTkSuQmCC") repeat-x top !important;
border-color: #777777 !important;
}

#installFileButton,
#installUpdatesAllButton,
#showUpdateInfoButton,
#hideUpdateInfoButton,
#checkUpdatesAllButton,
#continueDialogButton,
#skipDialogButton,
#aios-toolsButton {
font-size: 80% !important;
margin: 1px 1px 1px 1px !important;
max-width: 200px !important;
width: auto !important;
height: 30px !important;
}



/******************************************************************* 
 Change the bottom bar (becomes Top using AIOS)
*******************************************************************/



#commandBarBottom {
-moz-appearance: none !important;
background: #eeeeee 
url("data:image/png;base64,") repeat-x center !important;
margin: 0px 0px 0px 0px !important;
border-top: solid 1px #999999 !important;
/* AIOS */
margin: -2px 0px -4px 0px !important;
}

#resizerBox {
display: none !important;
}



/******************************************************************* 
 Change the Notification
*******************************************************************/



#addonsMsg {
margin: 3px -1px 0px 0px !important;
border-top: solid 1px #999999 !important;
}

#addonsMsg > notification {
-moz-appearance: -moz-win-media-toolbox !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
margin: 0px 0px 2px -1px !important;
border: none !important;
background: rgba(0,0,0,1) !important;
font-size: 80% !important;
color: white !important;
}

#progressBox {
margin: -5px 0px 0px 0px !important;
}

#progressStatus {
width: 100px !important;
}



/******************************************************************* 
 Change the Theme Browser appearance
*******************************************************************/



#themeSplitter {
-moz-appearance: none !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
border: none !important;
min-width: 2px !important;
width: 2px !important;
height: 2px !important;
/* AIOS */
border-top: solid 1px #999999 !important;
border-bottom: solid 1px #999999 !important;
}


#themePreviewArea{
border: none !important;
margin: -1px 0px -1px 0px !important;
/* AIOS */
margin: 0px 1px 0px 0px !important;
}



/******************************************************************* 
 Change the Addons Explorer appearance
*******************************************************************/



#extensionsView label {
-moz-appearance: none !important;
font-size: 10px !important;
}

vbox[type="header-recommended"] {
font-size: 80% !important;
font-weight: normal !important;
text-align: center !important;
}

label[class="text-link"][id="browseAddons"] {
display: none !important;
}

vbox[type="footer-recommended"] {
font-size: 80% !important;
margin: 0 !important;
}

vbox[type="recommended-failure"] {
display: none !important;
}

.addonThumbnailContainer {
-moz-border-radius: 8px !important;
border: solid 1px #999999 !important;
margin: 250px -140px 0px 0px !important;
}


.descriptionWrap {
border: none !important;
padding: 0px 0px 0px 0px !important;
}

.addon-name-version {
margin: 0px -3px -2px -1px !important;
}

.addonRating {
}

textbox[id="searchbox"] {
-moz-appearance: none !important;
-moz-border-radius: 4px !important;
-moz-border-left-colors: none !important;
-moz-border-right-colors: none !important;
-moz-border-top-colors: none !important;
-moz-border-bottom-colors: none !important;
background-color: #ffffff !important;
border: solid 1px #999999 !important;
margin-top: 2px !important;
margin-left: 3px !important;
margin-bottom: 1px !important;
height: 21px !important;
}

textbox[id="searchbox"]:hover {
border-color: rgba(64,140,200,1) !important;
}
