css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("teracod.com") {
#header {
	height:156px !important;
}
#header .row-1 {
	background:#101010 !important;
	height:32px !important;
}
	#header .row-1 .fright {
		padding:3px 2px 0 0 !important;
	}
#header .row-2 {

	height:124px !important;
}
	
	#header .nav {
		float:left !important;
		padding-top:85px !important;
	}
		#header .nav li {
			float:left !important;
			padding-right:3px !important;
		}
			#header .nav li a {
				float:left !important;
				border:none !important;
				padding:8px 15px 11px 15px !important;
				font-size:14px !important;
				color: #FFFFFF !important;
				text-decoration:none !important;
			}
			#header .nav li a:hover, #header .nav li a.current {
    color: #FFFFFF !important;
    display: block !important;
    /*font-weight: bold !important;*/
    text-decoration: none !important;
    text-shadow: 0 -1px 0 #222222 !important;
			}

	#header .row-2 .logo {
		padding:0 0 0 284px !important;
	}

.col-1, .col-2, .col-3, .col-4, .col-5 { 
	float:left !important;
}


.wrapper { 
	width:100% !important;
	overflow:hidden !important;
}




.cwrapper{
    min-height:600px !important;
}
.container { 
	width:962px !important;
	margin:0 auto !important;
	font-size:.8125em !important;
}

.headericon{
    padding-left: 5px !important;
}
.logoimg{
	position:relative !important;
top:-118px !important;
 margin-left: 70px !important;

    margin-top:20px !important;
}

#search {
	background: transparent !important;
    border-radius: 10px 10px 10px 10px !important;
    position: relative !important;
	
	}

#search-form {
	float:right !important;
	background:url('http://v4.teracod.net/static/layout/v4/images/search-bg.png')no-repeat 0 0 !important;
	background-position:center bottom !important;
	width:253px !important;
	height:38px !important;
	padding-top:82px !important;
}
	#search-form fieldset {
		border:none !important;
		padding:5px 0 0 4px !important;
	}
		#search-form input.text {
			width:200px !important;
			border:none !important;
			font-size:14px !important;
			color:#646463 !important;
			padding:0 0 0 9px !important;
		}
		#search-form input.submit {
			border:none !important;
			width:31px !important;
			height:30px !important;
			cursor:pointer !important;
		}
		
		

}
