css:
@-moz-document url-prefix(http://www.mtbs3d.com/phpBB),
	url-prefix(http://www.mtbs3d.com/phpBB),
	regexp('http://www\\.mtbs3d\\.com/([phbPHB]+)/.*') 
{
		
	/* Layout ------------ */
	body {
		color: #000000 !important;		background-color: #dbdbdb !important; background-image: none !important;	}
	#body-simple {
		background: #e6e6e6 !important;	}
	
	/* Misc stuff */
	.error, table td .error {
		color: #0f17f5 !important;	}
	
	.spacer {
		background-color: #b59879 !important; background-image: none !important;	}
	
	hr	{ border-color: #dc8f49 !important;}	
	/* Links
	 ------------ */
	/* CSS spec requires a:link, a:visited, a:hover and a:active rules to be specified in this order. */
	/* See http://www.phpbb.com/bugs/phpbb3/59685 */
	a {
		color: #dc8f49 !important;	}
	
	a:visited {
		color: #dc8f49 !important;	}
	
	a:hover {
		color: #0f17f5 !important;	}
	
	a:active {
		color: #dc8f49 !important;	}
	
	a.topictitle, .postbody a {
		color: #dc8f49 !important;	}
	a.topictitle:visited, .postbody a:visited {
		color: #b86f2c !important;	}
	a.topictitle:visited:hover, .postbody a:visited:hover {
		color: #dc8f49 !important;	}
	a.topictitle:hover, .postbody a:hover {
		color: #0f17f5 !important;	}
	
	/* Header and logo */
	#maintable {
		border-color: #ffffff !important;	}
	#logorow {
		background: none !important; 
	}
	
	.pm-notify-new, .pm-notify-unread {
		border-left: solid 1px #ce803a !important;		border-right: solid 1px #dc8f49 !important;		border-top: solid 1px #ce803a !important;		border-bottom: solid 1px #dc8f49 !important;		background-color: #cecece !important; background-image: none !important;	}
	.pm-notify-header {
		color: #333333 !important;	}
	.pm-notify-new, .pm-notify-unread:hover {
		border-left: solid 1px #030ac9 !important;		border-right: solid 1px #0f17f5 !important;		border-top: solid 1px #030ac9 !important;		border-bottom: solid 1px #0f17f5 !important;	}
	
	/* Navigation buttons */
	.navrow {
		background-color: #c47c39 !important; background-image: none !important; 		border-bottom: solid 1px #ffffff !important;		border-top: solid 1px #ffffff !important;		color: #0f17f5 !important;	}
	.navrow a, .navrow a:visited {
		color: #000000 !important;	}
	.navrow a:hover { 
		color: #0f17f5 !important; 	}
	
	a.link-new, a.link-new:visited { color: #0f17f5 !important; }	a.link-new:hover, a.link-new:visited:hover { color: #000000 !important; }	
	/* Content and tables */
	#contentrow {
		background-color: #e6e6e6 !important;	}
	
	.tablebg {
		border-color: #ffffff !important; 		background-color: #cecece !important; background-image: none !important;	}
	
	.row1, .row2, .row3, .legend, .forumrules, tr.row1 .row, tr.row2 .row, tr.row3 .row, .postbottom {
		border-left: solid 1px #bfbfbf !important;		border-right: solid 1px #ededed !important;		border-top: solid 1px #c3c3c3 !important;		border-bottom: solid 1px #ececec !important;		background-color: #cecece !important; background-image: none !important;	}
	th {
		color: #33200f !important; 		background-color: #b59879 !important; background-image: none !important;		border-left: solid 1px #ce833d !important;		border-right: solid 1px #e9b382 !important;		border-top: solid 1px #c67e3c !important;		border-bottom: solid 1px #e9b382 !important;	}
	th a, th a:visited {
		color: #33200f !important !important;	}
	th a:hover {
		color: #000000 !important;	}
	.catdiv {
		border-left: solid 1px #bfbfbf !important;		border-right: solid 1px #ededed !important;		border-top: solid 1px #c3c3c3 !important;		border-bottom: solid 1px #ececec !important;		background-color: #d3d3d3 !important; background-image: none !important;	}
	.rtl .catdiv {
		background-color: #d3d3d3 !important; background-image: none !important;	}
	
	.cat {
		border-left: solid 1px #bfbfbf !important;		border-right: solid 1px #ededed !important;		border-top: solid 1px #c3c3c3 !important;		border-bottom: solid 1px #ececec !important;		background-color: #d3d3d3 !important; background-image: none !important;	}
	.cathead {
		background-color: #d99d65 !important; background-image: none !important;		border-left: solid 1px #ce833d !important;		border-right: solid 1px #e9b382 !important;		border-top: solid 1px #c67e3c !important;		border-bottom: solid 1px #e9b382 !important;	}
	
	/* Table header */
	caption, .cap-div { 
		background: none !important; 
	}
	.cap-left {
		background: none !important; 
	}
	.cap-right {
		background: none !important; 
	}
	caption, caption h1, caption h2, caption h3, caption h4, caption span,
	.cap-div, .cap-div h1, .cap-div h2, .cap-div h3, .cap-div h4, .cap-div span { 
		color: #cd7a2f !important;	}
	caption h1, caption h2, caption h3, caption h4, caption span,
	.cap-div h1, .cap-div h2, .cap-div h3, .cap-div h4, .cap-div span { 
		display: inline !important;
	}
	caption a, caption a:visited, .cap-div a, .cap-div a:visited {
		color: #cd7a2f !important;	}
	caption a:hover, .cap-div a:hover {
		color: #2f2f2f !important;	}
	
	
	/* Footer */
	#wrapfooter {
		color: #333333 !important;	}
	
	.copyright {
		color: #777777 !important;	}
	.copyright a, .copyright a:visited {
		color: #555555 !important;	}
	.copyright a:hover { color: #000000 !important; }	
	/* Breadcrumbs */
	p.breadcrumbs {
		color: #555555 !important;	}
	
	/* Index/viewforum */
	.subforum.read { 
		-webkit-filter: invert(100%) !important; 
	}
	.subforum.unread { 
		-webkit-filter: invert(100%) !important; 
	}
	.forumrules {
		border-left-color: #555555 !important;		border-top-color: #555555 !important;		border-right-color: #333333 !important;		border-bottom-color: #333333 !important;	}
	.forumrules h3 {
		color: #0f17f5 !important;	}
	
	/* Post */
	.postsubject {
		color: #dc8f49 !important;	}
	td.postbottom { 
		background-color: #d2d2d2 !important; background-image: none !important; 		color: #2f2f2f !important;	}
	td.postbuttons { padding-top: 3px !important; }
	.postdetails { 
		color: #333333 !important;	}
	.postdetails b {
		color: #222222 !important;	}
	.posterrank		{ color: #4f4f4f !important; }	.posterprofile	{ color: #2f2f2f !important; }	
	.postauthor {
		color: #dc8f49 !important;	}
	
	.postreported, .postreported a:link, .postreported a:visited, .postreported a:hover, .postreported a:active {
		color: #0f17f5 !important;	}
	
	.postapprove, .postapprove a:link, .postapprove a:visited, .postapprove a:hover, .postapprove a:active {
		color: #ff00ff !important;	}
	
	
	.signature {
		color: #111111 !important;	}
	.signature .line { color: #555555 !important; }	
	.posthilit {
		background-color: #dc8f49 !important;		color: #000000 !important;	}
	
	.postsubject {
		color: #dc8f49 !important;	}
	
	/* UCP */
	.folder-current a, .folder-current a:visited {
		color: #000000 !important;	}
	.folder-current a:hover { color: #0f17f5 !important; }	
	td.pm_marked_colour { background: #0f17f5 !important; }	td.pm_marked_colour_left { border-left: solid 3px #0f17f5 !important; }	td.pm_marked_colour_right { border-right: solid 3px #0f17f5 !important; }	
	td.pm_replied_colour { background: #dc8f49 !important; }	td.pm_replied_colour_left { border-left: solid 3px #dc8f49 !important; }	td.pm_replied_colour_right { border-right: solid 3px #dc8f49 !important; }	
	td.pm_friend_colour { background: #ff88ff !important; }	td.pm_friend_colour_left { border-left: solid 3px #ff88ff !important; }	td.pm_friend_colour_right { border-right: solid 3px #ff88ff !important; }	
	td.pm_foe_colour { background: #22ffff !important; }	td.pm_foe_colour_left { border-left: solid 3px #22ffff !important; }	td.pm_foe_colour_right { border-right: solid 3px #22ffff !important; }	
	
	/* Form Elements
	 ------------ */
	textarea, select, .post, .btnbbcode, .btnmain, .btnlite, .btnfile, .helpline {
		color: #2f2f2f !important;		border-color: #dc8f49 !important;		background-color: #dddddd !important;	}
	
	.btnbbcode {
		border: 1px solid #bbbbbb !important;	}
	.btnbbcode:hover {
		border-color: #666666 !important;		color: #000000 !important;	}
	
	.helpline {
		background-color: transparent !important;
		color: #555555 !important;	}
	#helpline { color: #555555 !important; }	
	* html textarea, * html select, * html .post, * html .btnmain, * html .btnlite, * html .btnfile {
		border-color: #2f2f2f !important;	}
	textarea:hover, select:hover, .post:hover, .btnmain:hover, .btnlite:hover, .btnfile:hover {
		border-color: #2f2f2f !important;	}
	textarea:focus, select:focus, .post:focus, .btnmain:focus, .btnlite:focus, .btnfile:focus {
		border-color: #0f17f5 !important; 		color: #000000 !important;	}
	
	label:hover { 
		color: #0f17f5 !important; 	}
	
	/* BBCode
	 ------------ */
	.quotetitle, .attachtitle, .codetitle {
		border-color: #f6f6f6 !important;		color: #2f2f2f !important;		background-color: #d1d1d1 !important;	}
	
	.quotecontent, .attachcontent {
		border-color: #f6f6f6 !important;		background-color: #d1d1d1 !important;		color: #111111 !important;	}
	
	.codecontent {
		border-color: #f6f6f6 !important;		background-color: #d1d1d1 !important;		color: #111111 !important;	}
	
	.quotewrapper, .codewrapper, .attachwrapper {
		background-color: #d98e49 !important; background-image: none !important;		border-color: #f6f6f6 !important;	}
	.quotewrapper { background-image: none !important; }
	
	.syntaxbg {
		color: #000000 !important;	}
	
	.syntaxcomment {
		color: #555555 !important;	}
	
	.syntaxdefault {
		color: #000000 !important;	}
	
	.syntaxhtml {
		color: #000000 !important;	}
	
	.syntaxkeyword {
		color: #0f17f5 !important;	}
	
	.syntaxstring {
		color: #dc8f49 !important;	}
	
	
	/* More misc stuff */
	p.moderators {
		color: #333333 !important;	}
	
	td.history-current, tr.row1 td.history-current, tr.row2 td.history-current {
		background: #bbbbbb !important;	}
	
	.sep {
		color: #777777 !important;	}
	
	/* reset style for file input */
	.btnfile, .btnfile:hover, .btnfile:focus { 
	    background-color: #000000 !important;	    color: #ffffff !important;	}
	
	/*** EXTRA ***/
	
	.vt-p img, .postonline img {-webkit-filter: invert(100%) !important;}
}
