css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tagesschau.de") {

html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, font, img, ins, kbd, q, s, samp, small, strike, sub, sup, tt, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
font-family: Verdana,helvetica,arial,sans-serif!important;

}

div.linklist ul.list li a{
/* background-image: url(image/icon_intern_d5e0f7.gif)!important; */
background-repeat: no-repeat!important;
background-position: 0px 4px!important;
 padding-left: 18px!important; 
}


body.tagesschau .box {
background-image: none !important;
}

div.box, div.teaser, div.linklinst, div.linklinst a, 
div.box.h3, div.box.h3.resort, div.Con{
 background-color:rgb(222, 231, 248)!important;
} 


.button a  ,.button.share, .button.like, div.buttons, div.button.all {
background: linear-gradient(to bottom, rgb(0, 11, 165) 0%, rgb(0, 11, 165) 101%) repeat scroll 0% 0% transparent!important;
} 


input#edit-submit.form-submit, li.entry{
color: rgb(255,255,255)!important;
background: linear-gradient(to bottom, rgb(0, 11, 165) 0%, rgb(0, 11, 165) 101%) repeat scroll 0% 0% transparent!important;
}

li.entry{
background: linear-gradient(to bottom, rgb(0, 0, 130) 0%, rgb(0, 0, 130) 101%) repeat scroll 0% 0% transparent!important;
}


li.entry.active{
background: linear-gradient(to bottom, rgb(200, 200, 200) 0%, rgb(200, 200, 200) 101%) repeat scroll 0% 0% transparent!important;
}


p.infotext{
  font-size:12px!important;
}

.mod h3.headline {
   font-size: 0.9em !important;
}

.metaAdditionalEntry{
  font-size: 0.95em !important;
}


p.text.small  {
	font-size: 12px!important;
        font-weight: 400!important;
        line-height: 15.6px!important;
}

p.question.quizQuestion, div.polSingle, div.answer label{
	font-size: 13px!important;
        font-weight: 400!important;
        line-height: 17.3px!important;
}

a{
font-size: 14px!important;
}

h2.headline, h2.conHeadline {
  font-size: 16px!important;
  font-weight:800 !important;
  color: rgb(0, 0, 68)!important;  
}

h2.headline.small{
  font-size: 16px!important;
  font-weight:800!important;
  color: rgb(0, 0, 68)!important;  
}


h3.headline{
  font-size: 16px!important;
  font-weight:500!important;
  color: rgb(0, 0, 68)!important;
}

h3.headline.small{
  font-size: 15.6px!important;
  font-weight: 700!important;
  color: rgb(0, 0, 68)!important;
}

h4.subtitle.small{
  font-size: 13px!important;
  color: rgb(0, 0, 68)!important;
}

.masternavi a {
 font-size: 13.45px !important;
}

}
