css:
@-moz-document domain("facebook.com"), domain("feedly.com") {

*[dir=rtl],

  html[dir=rtl] > body,

  div[dir=rtl],

  span[dir=rtl],

  .entry-body,

  .direction_rtl,

  ._1rv,

  .rtl,

  .inputtext,

  .webMessengerMessageGroup,

  html[dir=rtl] * {

    font-family: 'MB Lateefi','MB Sindhi Web',Tahoma!important;

    font-size: 130%;

}

}



@-moz-document domain("ur.wikipedia.org") {

*[dir=rtl],

  html[dir=rtl] > body,

  div[dir=rtl],

  span[dir=rtl],

  html[dir=rtl] * {

    font-family: 'MB Lateefi','MB Sindhi Web',Tahoma!important;

    font-size: 105%;

  }

}



@-moz-document domain("urduvoa.com") {

*[dir=rtl],

  body * {

    font-family: 'MB Lateefi','MB Sindhi Web',Tahoma!important;

    font-size: 115%;

    line-height: 1.5;

    }

.boxwidget div.news_textcolumn.black a, .boxwidget_part ul li a {

    line-height: 1.5;

  }

.middle_content h1 {

    font-size: 80%;

 }

}



@-moz-document domain("twitter.com") {

*[dir=rtl],

  div[dir=rtl],

  span[dir=rtl],

.tweet-text-rtl {

    font-family: 'MB Lateefi','MB Sindhi Web',Tahoma!important;

    font-size: 120%;

    line-height: 1.5;

    }

}



@-moz-document domain("google.com"), domain("google.com.pk") {

*[dir=rtl],

  html[dir=rtl] > body,

  div[dir=rtl],

  span[dir=rtl],

  .direction_rtl,

  ._1rv,

  .rtl,

  .inputtext,

  html[dir=rtl] * {

    font-family: 'MB Lateefi','MB Sindhi Web',Jameel Noori Nastaleeq,Tahoma!important;

    font-size: 115%;

}

}
