css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.firefallthegame.com") {
    #ff-nav-wrapper, 
    #footer,
    .headerL,
    .headerC,
    .ff-h1, 
    .publicTabs .home, 
    .footwrap { display: none; }
    
    .pageWrapper {
        position: relative;
    }
    
    .headerR {
        background: none !important;
        float: none !important;
        height: auto !important;
        width: 200px !important;
        position: absolute;
        top: 13px;
        right: 135px;
        z-index: 5000;
    }
    
    .fflogout {
        margin: 0px !important;
    }
    
    body,
    .contentL,
    .contentM,
    .contentR{
        background: none !important;
    }
    
    .contentL {
        margin-bottom: 0px;
    }
    
    .contentM {
        padding-top: 0px !important;
    }
    
    .contentM > div {
        position: static !important;
    }
    
    #content .pageWidth {
        top: 0px !important;
    }
    
    #content .pageContent {
        margin-top: 0px !important;
    }
      
    #content.forum_view div.linkGroup a {
        position: absolute !important; 
        top: 80px;
        right: 0px;
        z-index: 100000;
    }
        
    .message .messageText,
    .message .signature,
    .devtracker_post_content .message
    {
        font-family: Calibri, Arial, Helvetica, sans-serif !important;
    }
    
    .message aside blockquote {
        font-size: 1.0em !important;
        background: rgba(0,0,0,.3) !important;
        border-top-left-radius: 0px !important;
    }

    .messageUserBlock div.avatarHolder {
        height: 102px !important;
        width: 102px !important;
        padding: 23px 20px 21px 21px !important;
    }
    
    .messageUserBlock h3.userText {
        top: -160px !important;
    }
    
    .messageUserBlock .helper {
        margin: 0px !important;
    }
    
    .message .newIndicator {
        margin: 22px -33px 5px 5px !important;
    }
    
    ol#forums {
        margin-top: 0px !important;
    }
    
    #forums .categoryNodeInfo {
        padding-bottom: 0px !important;
    }
    
    #forums .forum .nodeWrapper {
        margin: 1px 254px 0px 0px !important;
        padding-top: 0px !important;
    }
    
    #forums .forum .nodeLastPost {
        margin: 1px !important;
        width: 240px !important;
        min-height: 44px !important;
        top: 1px !important;
    }
    
    #forums .forum .borderArrowTL {
        top: 0px !important;
    }
    
    #forums .forum .borderArrowTR {
        top: -9px !important;
    }
    
    li.discussionListItem {
        position: relative !important;
    }
    
    li.discussionListItem span.borderArrowTL {
        position: absolute !important;
        top: 1px !important;
        left: 0px !important;
    }
    
    li.discussionListItem span.borderArrowTR {
        position: absolute !important;
        top: 1px !important;
        left: auto !important;
        right: -2px !important;
    }
    
    .mceContentBody  {
        background-color: #000000 !important;
        font-family: Calibri, Arial, Helvetica, sans-serif !important;
    }
    
    .alertsPopup ol li * {
        opacity: .4;
    }
    .alertsPopup ol li:hover *,
    .alertsPopup ol.Unread li * {
        opacity: 1;
    }
    
    #side-img-container #searchBar {
        margin-bottom: 11px !important;
    }
    
    #side-img-container #searchBar #QuickSearch {
        position: static !important;
        padding: 0px !important;
    }
    
    fieldset.breadcrumb {
        background: url("http://forums.firefallthegame.com/community/styles/firefall/xenforo/sources/subnavbg.png") top left repeat !important;
        font-size: 12px !important;
    }
    
    div.breadBoxTop fieldset.breadcrumb {
        margin: 0px 12px !important;
    }
    
    .sharePage {
        min-height: 120px;
    }
}
