css:
/* NOTE: ViewTube compatibility is only possible when in viewtube's file you

change line 382: from 'div' to 'viewtube' */



@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("youtube.com"),domain("www.youtube.com") {



@font-face {
	font-family: 'GeosansLight';
  	src: url('http://tekoneko.co.uk/fonts/geo_sans_light.woff') format('woff');
  	font-weight: normal;
  	font-style: normal;
	}




@font-face {

    font-family: Ubuntu;

    font-style: normal;

    font-weight: 400;

    src: local('Ubuntu'),

    url(http://themes.googleusercontent.com/static/fonts/ubuntu/v4/mZSs29ggGoaqrCNB3kDfZQ.woff) format('woff');

    }





body {

    background: #F7F7F7 !important;

    }



.feed-item-main {

    margin-top: auto !important;

    margin-left: 10px !important;

    }



#yt-masthead-container {

    background: #34444D !important;

    top: 0 !important;

    width: 100% !important;

    box-shadow: 0px 2px 6px #333 !important;

    }

    

.clearfix {

    border-radius: 3px !important;

    }



.feed-channel-header-title:hover {

    text-decoration: underline !important;

    }



.metadata {

    opacity: 0.0 !important;

    transition-duration: 0.5s !important;

    }



.feed-item-main:hover .metadata {

    opacity: 1.0 !important;

    transition-duration: 0.5s !important;

    }

    

#yt-masthead a.yt-uix-button, ul.yt-uix-button-menu, .yt-uix-button-menu {

    background: #F7F7F7 !important;

    }



#masthead-search-terms, .yt-uix-button.start.yt-uix-sessionlink.yt-uix-button-default, #yt-masthead button.yt-uix-button {

    background: #eee !important;

    border: 1px solid #bdbdbd !important;

    margin-left: auto !important;
 
    margin-right: auto !important;

    }

    

.watch-view-count {

    font-family: 'Ubuntu' !important;

    font-size: 25px !important;

    }    



#watch7-action-panel-footer {

    opacity: 0.0 !important;

    }



#watch-description-expand:hover, #watch-description-collapse:hover {

    border-top: 1px solid #C4C4C4 !important;

    }


.yt-uix-button-toggled, .action-panel-trigger:hover, .yt-uix-button-epic-nav-item:hover  {

    border-bottom-color: #2793E6 !important;

    }


.watch-title.yt-uix-expander-head {

    font-family: 'Ubuntu' !important;

    font-size: 25px !important;

    }    

    

.yt-uix-button-pane {

    border: 1px solid #bdbdbd !important;

    }

    

.yt-uix-button.start.yt-uix-sessionlink.yt-uix-button-default {

    height: 29px !important;

    width: 40px !important;

    }



#sb-button-notify {
    
    background: #E4444D !important;

    
    
    }



*:active, *:focus {

    outline: none !important;

    }

#upload-btn {
    
    border-radius: 2px !important;

    background: white !important;

    }

#appbar-settings-button {
    
    border-top-right-radius: 2px !important;
    
    border-bottom-right-radius: 2px !important; 

    }

#masthead-search-terms {

    border-radius: 2px !important;

    height: 28px !important;

    }



img, .yt-thumb-default, .video-thumb {

    border-radius: 3px !important;

    }



.channel-header-profile-image {

    border-top-left-radius: 0px !important;

    border-top-right-radius: 0px !important;

    }

    

.c4-header-content, #channel-subheader, .branded-page-v2-subnav-container {

    opacity: 0.4 !important;

    transition-duration: 0.5s !important;

    }

    

.c4-header-content:hover, #channel-subheader:hover, .branded-page-v2-subnav-container:hover {

    opacity: 1.0 !important;

    transition-duration: 0.5s !important;

    }

   

.epic-nav-item-heading {

    border: none !important;

    }

   

.yt-uix-button-default {

    background: #F7F7F7 !important;

    }



body #masthead-expanded-container {

    background: #F2F8F9 !important;

    border-top: 1px solid #333 !important;

    box-shadow: 0px 2px 6px #333 !important;

    }



body #masthead-expanded-container a {

    color: #F7F7F7 !important;

    }

#appbar-guide-button {
    
    height: 29px !important;
    
    background-color: #34444D !important;
    
    margin-right: -10px !important;
    
    }

.yt-masthead-logo-container {
    
    width: 63px !important;

    }

#gh-activityfeed {
    margin-left: 10px !important;

    margin-right: auto !important;
    }

#channel-subheader {
    border-bottom: 1px solid #F7F7F7 !important;
    }

#yt-masthead #logo-container:hover {

    text-shadow: 0 0 0.2em #00C4FF !important;

    text-decoration: none !important;

    }

.guide-channels-list.guide-item-container.yt-uix-scroller.filter-has-matches {

    height: auto !important;

    }

#masthead-search-term {

    background: #F7F7F7 !important;

    font-family: Ubuntu !important;

    }

#masthead-search {

    width: 300px !important;

    }

#masthead-search-term:hover {

    background: -moz-linear-gradient(#f8f8f8,#eaeaea) !important;

    opacity: 0.8 !important;

    }

#masthead-search-term:focus {

    background: -moz-linear-gradient(#dfdfdf,#d4d4d4) !important;

    opacity: 0.8 !important;

    }

.search-btn-component {
    
    background: none !important;

    border: none !important;

    opacity: 0.0 !important;
    
    }

yt-uix-button-content {

    

    }

#watch7-sidebar {

    margin-top: auto !important;

    padding-top: 10px !important;

    }

.feed-thumb-watched {

    border-top-left-radius: 2px !important;

    border-top-right-radius: 2px !important;

    }

#yt-masthead-user{

    color: #F7F7F7 !important;

    font-family: Ubuntu !important;

    margin-right: 60px !important;   
    
    }

    

#yt-masthead-user-displayname {

    font-size: 32px !important;

    color: #F7F7F7 !important;

    }

    

#page-container{

    margin-top: 12px !important;

    padding-bottom: 0px !important;

    }

    

.feed-promo, h3, .guide-item {

    font-family: Ubuntu !important;

    font-weight: normal !important;

    }


#guide-subscriptions-section:hover #guide-subs-footer-container{    

    opacity: 1.0 !important;

    transition-duration: 0.5s !important;

    }



.content-item-metadata {    

    opacity: 0.0 !important;

    transition-duration: 0.5s !important;

    }



.channels-content-item.yt-uix-shelfslider-item:hover .content-item-metadata{    

    opacity: 1.0 !important;

    transition-duration: 0.5s !important;

    }

    

.yt-uix-button-subscribed-branded:hover {

    text-shadow: none !important;

    color: #C4C4C4 !important;

    text-decoration: none !important;

    background: #797979 !important;

    opacity: 0.6 !important;

    }

    

#watch7-user-header {

    opacity: 0.5 !important;

    transition-duration: 0.5s !important;

    }

    

#watch7-user-header:hover {

    opacity: 1.0 !important;

    transition-duration: 0.5s !important;

    }

    

#player {

    background: #F7F7F7 !important;

    }



.guide-item.yt-uix-sessionlink.guide-item-selected, .guide-item.guide-user-item.create-channel-lightbox.narrow-item.guide-item-selected {

    background: #F7F7F7 !important;

    font-weight: bold !important;

    text-shadow: none !important;

    color: #2F2F2F !important;

    }



.yt-uix-tooltip {

    color: #2F2F2F !important;

    }



a.guide-item.yt-uix-sessionlink:hover, .guide-item.guide-user-item.create-channel-lightbox.narrow-item:hover {

    background: #F7F7F7 !important;

    opacity: 0.4 !important;

    color: #2F2F2F !important;

    width: 160px !important;

    height: 24px !important;

    }



.branded-page-v2-primary-col {

    border-top: 1px solid lightgray !important;

    }



body #footer-container {

    background: #F7F7F7 !important;

    box-shadow: 0px -2px 6px #333 !important;

    }



.yt-uix-button-promotional.yt-uix-button.yt-uix-button-dark {

    background: #F7F7F7 !important;

    color: #F2F8F9 !important;

    font-weight: normal !important;

    font-family: Ubuntu !important;

    }



#watch7-action-buttons.clearfix {

    opacity:  0.0 !important;

    transition-duration: 0.5s;

    border: none !important;

    border-radius: 0px !important;

    }



#watch7-action-buttons.clearfix:hover {

    opacity:  1.0 !important;

    transition-duration: 0.5s;

    border: none !important;

    border-radius: 0px !important;

    }



#action-panel-details, #watch7-user-header #watch7-headline {

    border: none !important;

    border-radius: 0px !important;

    }



#watch7-content {

    border: 1px solid #bdbdbd !important; 

    border-top: none !important;

    border-bottom-left-radius: 3px !important;

    border-bottom-right-radius: 3px !important;

    }



.video-extras-likes-dislikes {

    opacity: 0.0 !important;

    transition-duration: 0.5s    

    }



.video-extras-likes-dislikes:hover {

    opacity: 1.0 !important;

    transition-duration: 0.5s;

    }



.video-extras-sparkbars {

    border: none !important;

    }



.yt-uix-subscription-button {

    color: black !important;

    }



.yt-uix-button-subscribe-branded {

    border: 1px solid #bdbdbd !important;

    background: none !important;

    }



.yt-subscription-button-subscriber-count-branded-horizontal {

    opacity: 0.0 !important;

    transition-duration: 0.5s !important;

    border: none !important;

    background: none !important;

    }



.yt-uix-button-subscription-container:hover .yt-subscription-button-subscriber-count-branded-horizontal {

    opacity: 1.0 !important;

    transition-duration: 0.5s !important;

    }

.yt-uix-button-icon-bell {
    margin-top: -1px !important;
    margin-left: 3px !important;

    }


.yt-uix-button-empty, .yt-uix-button, .appbar-settings-button, button {

    background: none !important;

    }


#masthead-expanded-menu-list a.yt-uix-sessionlink, #masthead-expanded-menu-google-column1 ul .masthead-expanded-menu-item a {
    
    color: #555 !important;
    
    }
    
    
/* RIP HTML5 Styles */



    /* ViewTube Stuff */



viewtube div, viewtube select {

    border: none !important;

    color: #444 !important;

    }



viewtube {

    background: none !important;

    opacity: 0.4 !important;

    transition-duration: 0.2s !important;

    z-index: 1 !important;

    }



viewtube:hover {

    opacity: 1.0 !important;

    transition-duration: 0.2s !important;

    }

    

.doodle {

    opacity: 0.0 !important;

    }

    
    

/* Huge Munimul "Delete Everything" List 

NOTE: Most things listed here are personal 

preference and deleted because myself personally

didn't have a use for them, people might wanna

re-enable (delete) "#watch-discussion" to get

the comments section back  */





.channel-link, 

.addto-watch-later-button, 

#masthead-upload-button-group, 

.branded-page-related-channels.branded-page-gutter-padding, 

.guide-input-container.guide-quick-filter-container, 

.feed-author-bubble.yt-uix-sessionlink, 

.feed-author-bubble.yt-uix-sessionlink.rec, 

.content-region, 

#footer, 

#footer-container, 

#watch7-branded-banner, 

#yt-masthead-user-displayname,

#player-branded-banner,
    
#logo-container
{

    display: none !important;

    }


}
